<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Circles & Triangles</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
            margin: 0;
            padding: 20px;
        }
        #main-container {
            display: flex;
            flex-direction: column;
            width: 80%;
            max-width: 800px;
        }
        #canvas-container {
            position: relative;
            width: 100%;
            height: 400px;
            border: 1px solid #ccc;
        }
        #info-panel {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 10px;
            margin-top: 10px;
        }
        .info-header, .info-total-label {
            font-weight: bold;
        }
        #controls-container {
            margin-top: 20px;
        }
        label {
            margin-right: 10px;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <h1>Circles & Triangles</h1>
    <p>Circles have 360 degrees...</p>
    <div id="main-container">
        <div id="canvas-container"></div>
        <div id="info-panel">
            <span class="info-header">Arc</span>
            <span class="info-header">Vertex</span>
            <span id="arc-label-1">Arc AB</span>
            <span id="arc-value-1">108°</span>
            <span id="vertex-label-1">Vertex C</span>
            <span id="vertex-value-1">54.°</span>
            <span id="arc-label-2"></span>
            <span id="arc-value-2"></span>
            <span id="vertex-label-2"></span>
            <span id="vertex-value-2"></span>
            <span id="arc-label-3"></span>
            <span id="arc-value-3"></span>
            <span id="vertex-label-3"></span>
            <span id="vertex-value-3"></span>
            <hr>
            <span class="info-total-label">Total</span>
            <span id="arc-total">?</span>
            <span class="info-total-label"></span>
            <span id="vertex-total">?</span>
        </div>
    </div>
    <div id="controls-container">
        <div>
            <label>Vertex</label>
            <input type="radio" id="radio-a" name="vertex-select" value="A"><label for="radio-a">A</label>
            <input type="radio" id="radio-b" name="vertex-select" value="B"><label for="radio-b">B</label>
            <input type="radio" id="radio-c" name="vertex-select" value="C" checked><label for="radio-c">C</label>
            <input type="radio" id="radio-none" name="vertex-select" value="None"><label for="radio-none">None</label>
        </div>
        <div>
            <label for="slider-a">A</label>
            <input type="range" id="slider-a" min="0" max="360" step="0.5" value="306">
        </div>
        <div>
            <label for="slider-b">B</label>
            <input type="range" id="slider-b" min="0" max="360" step="0.5" value="54">
        </div>
        <div>
            <label for="slider-c">C</label>
            <input type="range" id="slider-c" min="0" max="360" step="0.5" value="180">
        </div>
    </div>

    <script>
        let angleA = 306;
        let angleB = 54;
        let angleC = 180;
        let selectedVertex = 'C';
        let draggingVertex = null;

        function setup() {
            createCanvas(800, 400).parent('canvas-container');
            noLoop();
            updateUI();
        }

        function draw() {
            background(255);
            drawCircle();
            drawTriangle();
            drawVertices();
            drawSectors();
            if (selectedVertex !== 'None') {
                drawHighlightedAngles();
            }
        }

        function drawCircle() {
            fill('#aaccde');
            stroke('#666666');
            strokeWeight(2);
            ellipse(width / 2, height / 2, 300, 300);
            fill('#666666');
            ellipse(width / 2, height / 2, 10, 10);
        }

        function drawTriangle() {
            beginShape();
            vertex(getVertexX(angleA), getVertexY(angleA));
            vertex(getVertexX(angleB), getVertexY(angleB));
            vertex(getVertexX(angleC), getVertexY(angleC));
            endShape(CLOSE);
            fill('rgba(173, 216, 230, 0.5)');
            stroke('#666666');
            strokeWeight(2);
        }

        function drawVertices() {
            textSize(20);
            fill('#00008B');
            text('A', getVertexX(angleA) + 10, getVertexY(angleA) - 10);
            text('B', getVertexX(angleB) + 10, getVertexY(angleB) - 10);
            text('C', getVertexX(angleC) + 10, getVertexY(angleC) - 10);
        }

        function drawSectors() {
            let sectors = [
                { start: angleA, end: angleB, color: '#e0f0e0' },
                { start: angleB, end: angleC, color: '#f5f5d5' },
                { start: angleC, end: angleA, color: '#e0e8f8' }
            ];
            sectors.forEach(sector => {
                fill(sector.color);
                arc(width / 2, height / 2, 300, 300, radians(sector.start), radians(sector.end), PIE);
            });
        }

        function drawHighlightedAngles() {
            let startAngle, endAngle, arcLength, vertexAngle;
            switch (selectedVertex) {
                case 'A':
                    startAngle = angleB;
                    endAngle = angleC;
                    arcLength = (angleC - angleB + 360) % 360;
                    vertexAngle = arcLength / 2;
                    break;
                case 'B':
                    startAngle = angleC;
                    endAngle = angleA;
                    arcLength = (angleA - angleC + 360) % 360;
                    vertexAngle = arcLength / 2;
                    break;
                case 'C':
                    startAngle = angleA;
                    endAngle = angleB;
                    arcLength = (angleB - angleA + 360) % 360;
                    vertexAngle = arcLength / 2;
                    break;
            }
            stroke('#008000');
            strokeWeight(3);
            arc(width / 2, height / 2, 300, 300, radians(startAngle), radians(endAngle));
            drawArrow(radians((startAngle + endAngle) / 2), radians(endAngle - startAngle));
            fill('#008000');
            textSize(16);
            text(`θ = ${arcLength.toFixed(1)}°`, width / 2 + 150 * cos(radians((startAngle + endAngle) / 2)), height / 2 + 150 * sin(radians((startAngle + endAngle) / 2)));

            stroke('#FF0000');
            strokeWeight(3);
            arc(getVertexX(selectedVertex), getVertexY(selectedVertex), 50, 50, 0, radians(vertexAngle * 2));
            drawArrow(radians(vertexAngle), radians(vertexAngle * 2));
            fill('#FF0000');
            textSize(16);
            text(`θ/2 = ${vertexAngle.toFixed(1)}°`, getVertexX(selectedVertex) + 25 * cos(radians(vertexAngle)), getVertexY(selectedVertex) + 25 * sin(radians(vertexAngle)));
        }

        function drawArrow(angle, length) {
            push();
            translate(width / 2, height / 2);
            rotate(angle);
            line(0, 0, length, 0);
            line(length, 0, length - 10, 10);
            line(length, 0, length - 10, -10);
            pop();
        }

        function getVertexX(angle) {
            return width / 2 + 150 * cos(radians(angle));
        }

        function getVertexY(angle) {
            return height / 2 + 150 * sin(radians(angle));
        }

        function updateUI() {
            document.getElementById('arc-value-1').innerText = `108°`;
            document.getElementById('vertex-value-1').innerText = `54.°`;
            document.getElementById('arc-label-2').innerText = '';
            document.getElementById('arc-value-2').innerText = '';
            document.getElementById('vertex-label-2').innerText = '';
            document.getElementById('vertex-value-2').innerText = '';
            document.getElementById('arc-label-3').innerText = '';
            document.getElementById('arc-value-3').innerText = '';
            document.getElementById('vertex-label-3').innerText = '';
            document.getElementById('vertex-value-3').innerText = '';
            document.getElementById('arc-total').innerText = '?';
            document.getElementById('vertex-total').innerText = '?';

            if (selectedVertex !== 'None') {
                let arcLength, vertexAngle;
                switch (selectedVertex) {
                    case 'A':
                        arcLength = (angleC - angleB + 360) % 360;
                        vertexAngle = arcLength / 2;
                        document.getElementById('arc-label-1').innerText = `Arc BC`;
                        document.getElementById('arc-value-1').innerText = `${arcLength.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = `Vertex A`;
                        document.getElementById('vertex-value-1').innerText = `${vertexAngle.toFixed(1)}°`;
                        break;
                    case 'B':
                        arcLength = (angleA - angleC + 360) % 360;
                        vertexAngle = arcLength / 2;
                        document.getElementById('arc-label-1').innerText = `Arc CA`;
                        document.getElementById('arc-value-1').innerText = `${arcLength.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = `Vertex B`;
                        document.getElementById('vertex-value-1').innerText = `${vertexAngle.toFixed(1)}°`;
                        break;
                    case 'C':
                        arcLength = (angleB - angleA + 360) % 360;
                        vertexAngle = arcLength / 2;
                        document.getElementById('arc-label-1').innerText = `Arc AB`;
                        document.getElementById('arc-value-1').innerText = `${arcLength.toFixed(1)}°`;
                        document.getElementById('vertex-label-1').innerText = `Vertex C`;
                        document.getElementById('vertex-value-1').innerText = `${vertexAngle.toFixed(1)}°`;
                        break;
                }
            } else {
                let arcAB = (angleB - angleA + 360) % 360;
                let arcBC = (angleC - angleB + 360) % 360;
                let arcCA = (angleA - angleC + 360) % 360;
                document.getElementById('arc-label-1').innerText = `Arc AB`;
                document.getElementById('arc-value-1').innerText = `${arcAB.toFixed(1)}°`;
                document.getElementById('vertex-label-1').innerText = `Vertex C`;
                document.getElementById('vertex-value-1').innerText = `${arcAB / 2.toFixed(1)}°`;
                document.getElementById('arc-label-2').innerText = `Arc BC`;
                document.getElementById('arc-value-2').innerText = `${arcBC.toFixed(1)}°`;
                document.getElementById('vertex-label-2').innerText = `Vertex A`;
                document.getElementById('vertex-value-2').innerText = `${arcBC / 2.toFixed(1)}°`;
                document.getElementById('arc-label-3').innerText = `Arc CA`;
                document.getElementById('arc-value-3').innerText = `${arcCA.toFixed(1)}°`;
                document.getElementById('vertex-label-3').innerText = `Vertex B`;
                document.getElementById('vertex-value-3').innerText = `${arcCA / 2.toFixed(1)}°`;
                document.getElementById('arc-total').innerText = `360°`;
                document.getElementById('vertex-total').innerText = `180°`;
            }
        }

        document.getElementById('slider-a').addEventListener('input', () => {
            angleA = parseFloat(document.getElementById('slider-a').value);
            updateUI();
            redraw();
        });

        document.getElementById('slider-b').addEventListener('input', () => {
            angleB = parseFloat(document.getElementById('slider-b').value);
            updateUI();
            redraw();
        });

        document.getElementById('slider-c').addEventListener('input', () => {
            angleC = parseFloat(document.getElementById('slider-c').value);
            updateUI();
            redraw();
        });

        document.querySelectorAll('input[name="vertex-select"]').forEach(input => {
            input.addEventListener('change', () => {
                selectedVertex = input.value;
                updateUI();
                redraw();
            });
        });

        function mousePressed() {
            if (dist(mouseX, mouseY, getVertexX(angleA), getVertexY(angleA)) < 25) {
                draggingVertex = 'A';
            } else if (dist(mouseX, mouseY, getVertexX(angleB), getVertexY(angleB)) < 25) {
                draggingVertex = 'B';
            } else if (dist(mouseX, mouseY, getVertexX(angleC), getVertexY(angleC)) < 25) {
                draggingVertex = 'C';
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                let angle = degrees(atan2(mouseY - height / 2, mouseX - width / 2));
                if (angle < 0) angle += 360;
                switch (draggingVertex) {
                    case 'A':
                        angleA = angle;
                        break;
                    case 'B':
                        angleB = angle;
                        break;
                    case 'C':
                        angleC = angle;
                        break;
                }
                document.getElementById(`slider-${draggingVertex.toLowerCase()}`).value = angle.toFixed(1);
                updateUI();
                redraw();
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }
    </script>
</body>
</html>
