<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-tau">&tau;</label>
                <button id="btn-dec-tau">-</button>
                <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1.0">
                <button id="btn-inc-tau">+</button>
                <span id="value-tau">1.00</span>
            </div>
            <div class="control-group">
                <label for="slider-phi">&phi;</label>
                <button id="btn-dec-phi">-</button>
                <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0">
                <button id="btn-inc-phi">+</button>
                <span id="value-phi">2.00</span>
            </div>
            <div class="control-group">
                <label for="slider-beta">&beta;</label>
                <button id="btn-dec-beta">-</button>
                <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
                <button id="btn-inc-beta">+</button>
                <span id="value-beta">0.20</span>
            </div>
            <div class="control-group">
                <label for="slider-gamma">&gamma;</label>
                <button id="btn-dec-gamma">-</button>
                <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
                <button id="btn-inc-gamma">+</button>
                <span id="value-gamma">20</span>
            </div>
            <div class="control-group">
                <label for="slider-xi">&xi;</label>
                <button id="btn-dec-xi">-</button>
                <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
                <button id="btn-inc-xi">+</button>
                <span id="value-xi">0.400</span>
            </div>
        </div>
        <div id="visualization-panel">
            <div id="plot-concentration"></div>
            <div id="plot-temperature"></div>
        </div>
    </div>

    <script>
        // Initialize variables
        let tau = 1.0;
        let phi = 2.0;
        let beta = 0.2;
        let gamma = 20;
        let xi = 0.4;

        // Update function for sliders
        function updateSliderValue(event) {
            const value = parseFloat(event.target.value);
            event.target.nextElementSibling.textContent = value.toFixed(3);
            if (event.target.id === 'slider-tau') {
                tau = value;
            } else if (event.target.id === 'slider-phi') {
                phi = value;
            } else if (event.target.id === 'slider-beta') {
                beta = value;
            } else if (event.target.id === 'slider-gamma') {
                gamma = value;
            } else if (event.target.id === 'slider-xi') {
                xi = value;
                updateMarker();
            }
            runSimulation();
        }

        // Increment/Decrement functions
        function adjustSlider(event) {
            const sliderId = event.target.nextElementSibling.id;
            const slider = document.getElementById(sliderId);
            const step = parseFloat(slider.step);
            const currentValue = parseFloat(slider.value);
            if (event.target.textContent === '+') {
                slider.value = Math.min(currentValue + step, parseFloat(slider.max));
            } else {
                slider.value = Math.max(currentValue - step, parseFloat(slider.min));
            }
            slider.dispatchEvent(new Event('input'));
        }

        // Initialize plots
        function initPlots() {
            const layout = {
                yaxis: { title: 'c', range: [0, 1.1], zeroline: false },
                xaxis: { title: 'ξ', range: [0, 1.0], zeroline: false },
                margin: { t: 0, b: 0, l: 0, r: 0 }
            };
            Plotly.newPlot('plot-concentration', [{ x: [], y: [] }], layout);

            const tempLayout = {
                yaxis: { title: 'θ', autorange: true, zeroline: false },
                xaxis: { title: 'ξ', range: [0, 1.0], zeroline: false },
                margin: { t: 0, b: 0, l: 0, r: 0 }
            };
            Plotly.newPlot('plot-temperature', [{ x: [], y: [] }], tempLayout);
        }

        // Run simulation
        function runSimulation() {
            // Dummy data for demonstration
            const xiValues = Array.from({ length: 100 }, (_, i) => i / 99);
            const cValues = xiValues.map(xi => Math.sin(Math.PI * xi)); // Example concentration
            const thetaValues = xiValues.map(xi => 1 + 0.5 * Math.cos(Math.PI * xi)); // Example temperature

            const layout = {
                yaxis: { title: 'c', range: [0, 1.1], zeroline: false },
                xaxis: { title: 'ξ', range: [0, 1.0], zeroline: false },
                margin: { t: 0, b: 0, l: 0, r: 0 }
            };
            Plotly.react('plot-concentration', [
                { x: xiValues, y: cValues, type: 'scatter', mode: 'lines' },
                { x: [xi, xi, 0], y: [0, cValues.find((_, i) => xiValues[i] === xi), cValues.find((_, i) => xiValues[i] === xi)], type: 'scatter', mode: 'lines', line: { color: '#333333' } }
            ], layout);

            const tempLayout = {
                yaxis: { title: 'θ', autorange: true, zeroline: false },
                xaxis: { title: 'ξ', range: [0, 1.0], zeroline: false },
                margin: { t: 0, b: 0, l: 0, r: 0 }
            };
            Plotly.react('plot-temperature', [
                { x: xiValues, y: thetaValues, type: 'scatter', mode: 'lines' },
                { x: [xi, xi, 0], y: [Math.min(...thetaValues), thetaValues.find((_, i) => xiValues[i] === xi), thetaValues.find((_, i) => xiValues[i] === xi)], type: 'scatter', mode: 'lines', line: { color: '#333333' } }
            ], tempLayout);

            updateAnnotations(cValues.find((_, i) => xiValues[i] === xi), thetaValues.find((_, i) => xiValues[i] === xi));
        }

        // Update annotations
        function updateAnnotations(c, theta) {
            Plotly.relayout('plot-concentration', {
                annotations: [{
                    x: 0.95,
                    y: 0.5,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${tau.toFixed(2)}\nξ = ${xi.toFixed(3)}\nc = ${c.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 }
                }]
            });

            Plotly.relayout('plot-temperature', {
                annotations: [{
                    x: 0.95,
                    y: 0.5,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${tau.toFixed(2)}\nξ = ${xi.toFixed(3)}\nθ = ${theta.toFixed(5)}`,
                    showarrow: false,
                    font: { size: 12 }
                }]
            });
        }

        // Update marker
        function updateMarker() {
            const xiValues = Array.from({ length: 100 }, (_, i) => i / 99);
            const cValues = xiValues.map(xi => Math.sin(Math.PI * xi)); // Example concentration
            const thetaValues = xiValues.map(xi => 1 + 0.5 * Math.cos(Math.PI * xi)); // Example temperature

            Plotly.restyle('plot-concentration', {
                x: [[xi, xi, 0]],
                y: [[0, cValues.find((_, i) => xiValues[i] === xi), cValues.find((_, i) => xiValues[i] === xi)]]
            });

            Plotly.restyle('plot-temperature', {
                x: [[xi, xi, 0]],
                y: [[Math.min(...thetaValues), thetaValues.find((_, i) => xiValues[i] === xi), thetaValues.find((_, i) => xiValues[i] === xi)]]
            });

            updateAnnotations(cValues.find((_, i) => xiValues[i] === xi), thetaValues.find((_, i) => xiValues[i] === xi));
        }

        // Event listeners
        document.getElementById('slider-tau').addEventListener('input', updateSliderValue);
        document.getElementById('slider-phi').addEventListener('input', updateSliderValue);
        document.getElementById('slider-beta').addEventListener('input', updateSliderValue);
        document.getElementById('slider-gamma').addEventListener('input', updateSliderValue);
        document.getElementById('slider-xi').addEventListener('input', updateSliderValue);

        document.getElementById('btn-dec-tau').addEventListener('click', adjustSlider);
        document.getElementById('btn-inc-tau').addEventListener('click', adjustSlider);
        document.getElementById('btn-dec-phi').addEventListener('click', adjustSlider);
        document.getElementById('btn-inc-phi').addEventListener('click', adjustSlider);
        document.getElementById('btn-dec-beta').addEventListener('click', adjustSlider);
        document.getElementById('btn-inc-beta').addEventListener('click', adjustSlider);
        document.getElementById('btn-dec-gamma').addEventListener('click', adjustSlider);
        document.getElementById('btn-inc-gamma').addEventListener('click', adjustSlider);
        document.getElementById('btn-dec-xi').addEventListener('click', adjustSlider);
        document.getElementById('btn-inc-xi').addEventListener('click', adjustSlider);

        // Initialize plots and run simulation on page load
        initPlots();
        runSimulation();
    </script>
</body>
</html>
