    <script>
        const ages = [30, 35, 40, 45, 50, 55];
        const volumes = [289, 445, 565, 704, 833, 924];

        function richardsCurve(t, b_max, b_0, r, beta) {
            const M = Math.pow(b_max / b_0, beta) - 1;
            return b_max / Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
        }

        function calculate45Law(b_max, b_0, r, beta) {
            const t_i = Math.log(((b_max / b_0) ** beta - 1) * beta) / r;
            const V_i = b_max * ((beta + 1) / (2 * beta)) ** (1 / beta);
            const V_i_prime = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));
            const u = t_i - (4 / 5) * V_i / V_i_prime;
            const p = V_i / Math.pow(t_i - u, 4 / 5);
            return { u, p };
        }

        function update() {
            const b_max = parseFloat(document.getElementById('slider-bmax').value);
            const b_0 = parseFloat(document.getElementById('slider-b0').value);
