            document.getElementById('value-beta').textContent = beta.toFixed(3);

            const t_values = Array.from({ length: 66 }, (_, i) => 15 + i);
            const richards_data = t_values.map(t => richardsCurve(t, b_max, b_0, r, beta));

            const { u, p } = calculate45Law(b_max, b_0, r, beta);
            const four5law_data = t_values.map(t => t > u ? p * Math.pow(t - u, 4 / 5) : null).filter(v => v !== null);

            const data = [
                {
                    x: ages,
                    y: volumes,
                    mode: 'markers',
                    marker: { color: 'blue' },
                    name: 'Data Points'
                },
                {
                    x: t_values,
                    y: richards_data,
                    mode: 'lines',
                    line: { color: 'orange' },
                    name: 'Richards Curve'
                },
                {
                    x: t_values.filter((_, i) => i >= u_index),
                    y: four5law_data,
                    mode: 'lines',
                    line: { color: 'blue' },
                    name: '4/5 Law'
                }
            ];

            const layout = {
