    <script>
        const canvas = document.getElementById('main-canvas');
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, canvas.clientWidth / canvas.clientHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ canvas });
        renderer.setSize(canvas.clientWidth, canvas.clientHeight);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const group = new THREE.Group();
        scene.add(group);

        camera.position.z = 100;

        let numUnits = 200;
        let angularAdvance = 137.5;
        let conicalFactor = 0;
        let rotateX = 30;
        let rotateZ = 0;
        let basePolygonSides = 7;

        function redraw() {
            numUnits = parseInt(document.getElementById('slider-units').value);
            angularAdvance = parseFloat(document.getElementById('slider-angle').value);
            conicalFactor = parseFloat(document.getElementById('slider-conical').value);
            rotateX = parseInt(document.getElementById('slider-rotate-x').value);
            rotateZ = parseInt(document.getElementById('slider-rotate-z').value);
            basePolygonSides = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);

            group.children.forEach(child => group.remove(child));

            const shape = new THREE.Shape();
            const radius = 10;
            for (let i = 0; i < basePolygonSides; i++) {
                const angle = (i / basePolygonSides) * 2 * Math.PI;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }
            shape.closePath();

            for (let i = 0; i < numUnits; i++) {
                const geometry = new THREE.ExtrudeGeometry(shape, { depth: 1 });
                const material = new THREE.MeshStandardMaterial({ color: 0x00ff00 });
                const mesh = new THREE.Mesh(geometry, material);

                const angle = i * (angularAdvance * (Math.PI / 180));
                const radius = 4 * Math.sqrt(i);
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conicalFactor * radius;
                const scale = 0.5 * Math.log(i + 2);

                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angle;

                group.add(mesh);
            }

            group.rotation.x = (rotateX * Math.PI) / 180;
            group.rotation.z = (rotateZ * Math.PI) / 180;

            renderer.render(scene, camera);
        }

        document.getElementById('slider-units').addEventListener('input', redraw);
        document.getElementById('btn-units-plus').addEventListener('click', () => {
            document.getElementById('slider-units').stepUp();
            redraw();
        });

        document.getElementById('slider-angle').addEventListener('input', redraw);
        document.getElementById('btn-angle-plus').addEventListener('click', () => {
            document.getElementById('slider-angle').stepUp();
            redraw();
        });

        document.getElementById('slider-conical').addEventListener('input', redraw);
        document.getElementById('btn-conical-plus').addEventListener('click', () => {
            document.getElementById('slider-conical').stepUp();
            redraw();
        });

        document.getElementById('slider-rotate-x').addEventListener('input', redraw);
        document.getElementById('btn-rotate-x-plus').addEventListener('click', () => {
            document.getElementById('slider-rotate-x').stepUp();
            redraw();
        });

        document.getElementById('slider-rotate-z').addEventListener('input', redraw);
        document.getElementById('btn-rotate-z-plus').addEventListener('click', () => {
            document.getElementById('slider-rotate-z').stepUp();
            redraw();
        });

        document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
            radio.addEventListener('change', redraw);
        });

        window.addEventListener('resize', () => {
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
            redraw();
        });

        redraw();
    </script>
