                <label for="input-h">h</label>
                <input type="number" id="input-h" value="1" min="-10" max="10" step="1">
                <label for="input-k">k</label>
                <input type="number" id="input-k" value="1" min="-10" max="10" step="1">
                <label for="input-l">l</label>
                <input type="number" id="input-l" value="1" min="-10" max="10" step="1">
            </div>
