                <label for="slider-opacity">plane opacity</label>
                <input type="range" id="slider-opacity" value="0.8" min="0" max="1" step="0.01">
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <!-- three.js and OrbitControls via CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/three.min.js"></script>
    <script src="https://threejs.org/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Initialize Three.js components
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 2;

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth * 0.8, window.innerHeight * 0.8);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const orbitControls = new THREE.OrbitControls(camera, renderer.domElement);

        // Add lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight1.position.set(1, 1, 1).normalize();
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1).normalize();
        scene.add(directionalLight2);

        // Add coordinate axes
        const axesHelper = new THREE.AxesHelper(1);
        scene.add(axesHelper);

        // Add unit cell
        const boxGeometry = new THREE.BoxGeometry(1, 1, 1);
        const edgesGeometry = new THREE.EdgesGeometry(boxGeometry);
        const lineMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
        const edges = new THREE.LineSegments(edgesGeometry, lineMaterial);
        scene.add(edges);

        // Groups for atoms and planes
        const atomsGroup = new THREE.Group();
        const planesGroup = new THREE.Group();
        scene.add(atomsGroup);
        scene.add(planesGroup);

        // Initial state
        let latticeType = 'SC';
        let h = 1, k = 1, l = 1;
        let opacity = 0.8;

        // Update scene function
        function updateScene() {
            // Clear previous objects
            atomsGroup.children.forEach(child => atomsGroup.remove(child));
            planesGroup.children.forEach(child => planesGroup.remove(child));

            // Draw atoms
            switch (latticeType) {
                case 'SC':
                    const scPositions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                        [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                        [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5]
                    ];
                    scPositions.forEach(pos => {
                        const sphereGeometry = new THREE.SphereGeometry(0.05, 32, 32);
                        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00, specular: 0x111111, shininess: 100 });
                        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                        sphere.position.set(...pos);
                        atomsGroup.add(sphere);
                    });
                    break;
                case 'BCC':
                    const bccPositions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                        [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                        [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5],
                        [0, 0, 0]
                    ];
                    bccPositions.forEach(pos => {
                        const sphereGeometry = new THREE.SphereGeometry(0.05, 32, 32);
                        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00, specular: 0x111111, shininess: 100 });
                        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                        sphere.position.set(...pos);
                        atomsGroup.add(sphere);
                    });
                    break;
                case 'FCC':
                    const fccPositions = [
                        [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                        [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                        [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                        [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5],
                        [0.5, 0, 0], [-0.5, 0, 0],
                        [0, 0.5, 0], [0, -0.5, 0],
                        [0, 0, 0.5], [0, 0, -0.5]
                    ];
                    fccPositions.forEach(pos => {
                        const sphereGeometry = new THREE.SphereGeometry(0.05, 32, 32);
                        const sphereMaterial = new THREE.MeshPhongMaterial({ color: 0x00ff00, specular: 0x111111, shininess: 100 });
                        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                        sphere.position.set(...pos);
                        atomsGroup.add(sphere);
                    });
                    break;
            }

            // Draw planes
            if (h === 0 && k === 0 && l === 0) return;

            const gcd = (a, b) => b ? gcd(b, a % b) : a;
            const gcdHKL = gcd(gcd(h, k), l);
            const hPrime = h / gcdHKL;
            const kPrime = k / gcdHKL;
            const lPrime = l / gcdHKL;

            const vertices = [
                [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5]
            ];

            const minValue = Math.min(...vertices.map(v => hPrime * v[0] + kPrime * v[1] + lPrime * v[2]));
            const maxValue = Math.max(...vertices.map(v => hPrime * v[0] + kPrime * v[1] + lPrime * v[2]));

            for (let n = Math.floor(minValue); n <= Math.ceil(maxValue); n++) {
                const shape = new THREE.Shape();
                let firstPoint = null;
                let lastPoint = null;

                vertices.forEach((v, i) => {
                    const nextVertex = vertices[(i + 1) % vertices.length];
                    const edgeDirection = [nextVertex[0] - v[0], nextVertex[1] - v[1], nextVertex[2] - v[2]];
                    const edgeLength = Math.sqrt(edgeDirection[0]**2 + edgeDirection[1]**2 + edgeDirection[2]**2);
                    const edgeNormal = [edgeDirection[1] * edgeDirection[2] - edgeDirection[2] * edgeDirection[1],
                                        edgeDirection[2] * edgeDirection[0] - edgeDirection[0] * edgeDirection[2],
                                        edgeDirection[0] * edgeDirection[1] - edgeDirection[1] * edgeDirection[0]];

                    const t = (n - (hPrime * v[0] + kPrime * v[1] + lPrime * v[2])) / (hPrime * edgeDirection[0] + kPrime * edgeDirection[1] + lPrime * edgeDirection[2]);
                    if (t >= 0 && t <= 1) {
                        const intersectionPoint = [v[0] + t * edgeDirection[0], v[1] + t * edgeDirection[1], v[2] + t * edgeDirection[2]];
                        if (!firstPoint) firstPoint = intersectionPoint;
                        if (lastPoint) {
                            shape.lineTo(intersectionPoint[0], intersectionPoint[1]);
                        } else {
                            shape.moveTo(intersectionPoint[0], intersectionPoint[1]);
                        }
                        lastPoint = intersectionPoint;
                    }
                });

                if (firstPoint && lastPoint) {
                    shape.lineTo(firstPoint[0], firstPoint[1]);
                }

                const shapeGeometry = new THREE.ShapeGeometry(shape);
                const planeMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff, transparent: true, opacity: opacity, side: THREE.DoubleSide });
                const planeMesh = new THREE.Mesh(shapeGeometry, planeMaterial);
                planesGroup.add(planeMesh);
            }
        }

        // Event listeners
        document.getElementById('btn-sc').addEventListener('click', () => {
            document.getElementById('btn-sc').classList.add('active');
            document.getElementById('btn-bcc').classList.remove('active');
            document.getElementById('btn-fcc').classList.remove('active');
            latticeType = 'SC';
            updateScene();
        });

        document.getElementById('btn-bcc').addEventListener('click', () => {
            document.getElementById('btn-sc').classList.remove('active');
            document.getElementById('btn-bcc').classList.add('active');
            document.getElementById('btn-fcc').classList.remove('active');
            latticeType = 'BCC';
            updateScene();
        });

        document.getElementById('btn-fcc').addEventListener('click', () => {
            document.getElementById('btn-sc').classList.remove('active');
            document.getElementById('btn-bcc').classList.remove('active');
            document.getElementById('btn-fcc').classList.add('active');
            latticeType = 'FCC';
            updateScene();
        });

        document.getElementById('input-h').addEventListener('input', () => {
            h = parseInt(document.getElementById('input-h').value);
            updateScene();
        });

        document.getElementById('input-k').addEventListener('input', () => {
            k = parseInt(document.getElementById('input-k').value);
            updateScene();
        });

        document.getElementById('input-l').addEventListener('input', () => {
            l = parseInt(document.getElementById('input-l').value);
            updateScene();
        });

        document.getElementById('slider-opacity').addEventListener('input', () => {
            opacity = parseFloat(document.getElementById('slider-opacity').value);
            planesGroup.children.forEach(plane => plane.material.opacity = opacity);
        });

        // Initial scene setup
        updateScene();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            orbitControls.update();
            renderer.render(scene, camera);
        }

        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth * 0.8, window.innerHeight * 0.8);
        });
    </script>
</body>
</html>
