</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-points">number of points</label>
                <input type="range" id="slider-points" min="2" max="200" step="1" value="20">
                <span id="label-points">+ 20</span>
            </div>
            <div>
                <label for="plot-type">plot type</label>
                <button id="btn-3d" class="active">3D</button>
                <button id="btn-2d">2D</button>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="canvas-3d"></canvas>
            <div id="div-2d-plot">
                <canvas id="canvas-2d"></canvas>
                <div id="div-2d-text"></div>
            </div>
        </div>
    </div>

    <!-- External Libraries -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://threejs.org/examples/js/controls/OrbitControls.js"></script>

    <script>
        const THOMSON_POINTS = {
            "2": [[1, 0, 0], [-1, 0, 0]],
            "3": [[1, 0, 0], [-0.5, Math.sqrt(3)/2, 0], [-0.5, -Math.sqrt(3)/2, 0]],
            // Add more pre-calculated points as needed
        };

        let numPoints = 20;
        let points = [];
        let scene, camera, renderer, controls, pointGroup;

        function init3D() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight);
            renderer.setClearColor(0xf0f0f0);
            document.getElementById('canvas-3d').appendChild(renderer.domElement);

            const ambientLight = new THREE.AmbientLight(0xcccccc);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
            directionalLight.position.set(1, 1, 1).normalize();
            scene.add(directionalLight);

            const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
            const sphereMaterial = new THREE.MeshLambertMaterial({ color: 0xffffe0, transparent: true, opacity: 0.6 });
            const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(sphere);

            pointGroup = new THREE.Group();
            scene.add(pointGroup);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            camera.position.z = 3;

            window.addEventListener('resize', onWindowResize, false);
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function update3DPoints() {
            pointGroup.children.forEach(child => child.parent.remove(child));
            points.forEach(point => {
                const solidSphere = new THREE.Mesh(new THREE.SphereGeometry(0.025, 16, 16), new THREE.MeshBasicMaterial({ color: 0x0000ff }));
                const ring = new THREE.Mesh(new THREE.RingGeometry(0.02, 0.025, 32), new THREE.MeshBasicMaterial({ color: 0x0000ff, side: THREE.DoubleSide }));
                solidSphere.position.set(point[0], point[1], point[2]);
                ring.position.set(point[0], point[1], point[2]);
                pointGroup.add(solidSphere);
                pointGroup.add(ring);
            });
        }

        function animate() {
            requestAnimationFrame(animate);
            pointGroup.children.forEach(child => {
                if (child instanceof THREE.Mesh) {
                    const isFront = child.position.z > 0;
                    child.visible = isFront;
                    if (!isFront && child.geometry.type === 'RingGeometry') {
                        child.lookAt(camera.position);
                    }
                }
            });
            renderer.render(scene, camera);
        }

        function init2D() {
            const canvas = document.getElementById('canvas-2d');
            const ctx = canvas.getContext('2d');
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;

            function drawPoints() {
                ctx.clearRect(0, 0, canvas.width, canvas.height);
                points.forEach(point => {
                    const theta = Math.atan2(point[1], point[0]);
                    const phi = Math.acos(point[2]);
                    const x = (theta + Math.PI) / (2 * Math.PI) * canvas.width;
                    const y = phi / Math.PI * canvas.height;
                    ctx.beginPath();
                    ctx.arc(x, y, 3, 0, 2 * Math.PI);
                    ctx.fillStyle = '#0000ff';
                    ctx.fill();
                });
            }

            function calculateDistances() {
                let minDistance = Infinity;
                for (let i = 0; i < points.length; i++) {
                    for (let j = i + 1; j < points.length; j++) {
                        const dx = points[i][0] - points[j][0];
                        const dy = points[i][1] - points[j][1];
                        const dz = points[i][2] - points[j][2];
                        const distance = Math.sqrt(dx * dx + dy * dy + dz * dz);
                        if (distance < minDistance) {
                            minDistance = distance;
                        }
                    }
                }
                return minDistance;
            }

            function update2DText(minDistance) {
                document.getElementById('div-2d-text').innerHTML = `
                    minimum distance between points = ${minDistance.toFixed(5)}<br>
                    maximum distance between points = 2.
                `;
            }

            function render2D() {
                drawPoints();
                const minDistance = calculateDistances();
                update2DText(minDistance);
            }

            render2D();
        }

        function updatePoints() {
            if (THOMSON_POINTS[numPoints]) {
                points = THOMSON_POINTS[numPoints].map(coord => coord.slice());
            } else {
                points = [];
                for (let i = 0; i < numPoints; i++) {
                    const theta = Math.random() * 2 * Math.PI;
                    const phi = Math.acos(2 * Math.random() - 1);
                    const x = Math.sin(phi) * Math.cos(theta);
                    const y = Math.sin(phi) * Math.sin(theta);
                    const z = Math.cos(phi);
                    points.push([x, y, z]);
                }
            }
            update3DPoints();
            init2D();
        }

        document.getElementById('slider-points').addEventListener('input', function() {
            numPoints = parseInt(this.value);
            document.getElementById('label-points').textContent = `+ ${numPoints}`;
            updatePoints();
        });

        document.getElementById('btn-3d').addEventListener('click', function() {
            this.classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'block';
            document.getElementById('div-2d-plot').style.display = 'none';
        });

        document.getElementById('btn-2d').addEventListener('click', function() {
            this.classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'none';
            document.getElementById('div-2d-plot').style.display = 'block';
        });

        init3D();
        updatePoints();
        animate();
    </script>
</body>
</html>
