            <div class="slider-group">
                <label for="slider-angle">angle relative to ecliptic</label>
                <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
                <span id="value-angle">23.45</span>
            </div>
            <div class="slider-group">
                <label for="slider-longitude">longitude of periapsis</label>
                <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
                <span id="value-longitude">102.95</span>
            </div>
            <div class="slider-group">
                <label for="slider-eccentricity">orbit eccentricity</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
                <span id="value-eccentricity">0.016722</span>
            </div>
        </div>
        <div class="visualization-area">
            <canvas id="orbit-canvas" width="600" height="400"></canvas>
            <canvas id="plot-canvas" width="600" height="400"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        let orbitCanvas, plotCanvas;
        let angleSlider, longitudeSlider, eccentricitySlider;
        let angleValue, longitudeValue, eccentricityValue;

        function setup() {
            orbitCanvas = createCanvas(600, 400);
            orbitCanvas.parent('orbit-canvas');
            plotCanvas = createCanvas(600, 400);
            plotCanvas.parent('plot-canvas');

            angleSlider = select('#slider-angle');
            longitudeSlider = select('#slider-longitude');
            eccentricitySlider = select('#slider-eccentricity');

            angleValue = select('#value-angle');
            longitudeValue = select('#value-longitude');
            eccentricityValue = select('#value-eccentricity');

            angleSlider.input(updateValues);
            longitudeSlider.input(updateValues);
            eccentricitySlider.input(updateValues);

            updateValues();
        }

        function updateValues() {
            angleValue.html(angleSlider.value().toFixed(2));
            longitudeValue.html(longitudeSlider.value().toFixed(2));
            eccentricityValue.html(eccentricitySlider.value().toFixed(6));

            drawOrbit();
            drawPlot();
        }

        function drawOrbit() {
            orbitCanvas.background(240);
            orbitCanvas.stroke(160, 82, 45);
            orbitCanvas.noFill();

            const angle = radians(angleSlider.value());
            const longitude = radians(longitudeSlider.value());
            const eccentricity = parseFloat(eccentricitySlider.value());

            const a = 0.4 * orbitCanvas.width;
            const b = a * sqrt(1 - pow(eccentricity, 2));

            orbitCanvas.push();
            orbitCanvas.translate(orbitCanvas.width / 2, orbitCanvas.height / 2);
            orbitCanvas.rotate(longitude);
            orbitCanvas.ellipse(0, 0, 2 * a, 2 * b);
            orbitCanvas.pop();
        }

        function drawPlot() {
            plotCanvas.background(255);
            plotCanvas.stroke(0);
            plotCanvas.strokeWeight(1);

            const angle = radians(angleSlider.value());
            const longitude = radians(longitudeSlider.value());
            const eccentricity = parseFloat(eccentricitySlider.value());

            const points = [];
            let prevEOT = 0;

            for (let M = 0; M <= TWO_PI; M += 0.01) {
                let E = M;
                for (let i = 0; i < 10; i++) {
                    E = E - (E - eccentricity * sin(E) - M) / (1 - eccentricity * cos(E));
                }
                const nu = 2 * atan2(sqrt(1 + eccentricity) * sin(E / 2), sqrt(1 - eccentricity) * cos(E / 2));
                const lambda = nu + longitude;
                const alpha = atan2(cos(angle) * sin(lambda), cos(lambda));
                let EOT_rad = M - alpha;

                if (abs(EOT_rad - prevEOT) > PI) {
                    EOT_rad += EOT_rad > prevEOT ? -TWO_PI : TWO_PI;
                }
                prevEOT = EOT_rad;

                const EOT_minutes = EOT_rad * (720 / PI);
                points.push({ x: map(M, 0, TWO_PI, 0, plotCanvas.width), y: map(EOT_minutes, -10, 10, plotCanvas.height, 0) });
            }

            const minX = min(points.map(p => p.x));
            const maxX = max(points.map(p => p.x));
            const minY = min(points.map(p => p.y));
            const maxY = max(points.map(p => p.y));

            plotCanvas.line(0, minY, plotCanvas.width, minY);
            plotCanvas.line(0, maxY, plotCanvas.width, maxY);
            for (let x = minX; x <= maxX; x += plotCanvas.width / 6) {
                plotCanvas.line(x, minY, x, maxY);
            }

            plotCanvas.stroke(255, 140, 0);
            plotCanvas.strokeWeight(2);
            beginShape();
            for (let point of points) {
                vertex(point.x, point.y);
            }
            endShape();
        }
    </script>
</body>
