        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let c = 4, h_C = 1.9, delta = 0.5, plotRange = 1, step = 5, verification = true;
        let canvas;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('p5Canvas');
            noLoop();
            redrawScene();
        }

        function draw() {
            // Drawing logic handled by redrawScene
        }

        function redrawScene() {
            clear();
            const baseScale = 50;
            const scaleFactor = baseScale * plotRange;
            translate(width / 2, height * 0.6);
            scale(1, -1);
            scale(scaleFactor);

            const F = { x: 0, y: 0 };
            const C = { x: 0, y: h_C };
            const D = { x: c / 2, y: 0 };
            const K = { x: c, y: 0 };

            // Calculate point M
            const a = c * c - 4 * h_C * h_C;
            const b = 4 * h_C * h_C;
            const cM = (b - a) / (4 * h_C);
            const y_M = (-b + sqrt(b * b - 4 * a * cM)) / (2 * a);
            const x_M = (c * h_C - 2 * h_C * y_M + h_C * h_C - c * c / 4) / c;
            const M = { x: x_M, y: y_M };

            const R = dist(M.x, M.y, C.x, C.y);
            const x_B = 2 * x_M - c / 2;
            const B = { x: x_B, y: 0 };
            const A = { x: x_B - c, y: 0 };

            if (verification) {
                const E = { x: c - x_B, y: 0 };
            }

            // Drawing
            strokeWeight(1);
            stroke(0);
            fill(0);

            if (step >= 1) {
                line(-width / (2 * scaleFactor), 0, width / (2 * scaleFactor), 0); // Line λ
                point(F.x, F.y); text("F", F.x + 5, F.y + 5);
                point(C.x, C.y); text("C", C.x + 5, C.y + 5);
                point(D.x, D.y); text("D", D.x + 5, D.y + 5);
                line(F.x, F.y, C.x, C.y);
            }

            if (step >= 2) {
                point(K.x, K.y); text("K", K.x + 5, K.y + 5);
                line(C.x, C.y, D.x, D.y);
                line(D.x, D.y, K.x, K.y);
                point(M.x, M.y); text("M", M.x + 5, M.y + 5);
                line(M.x, M.y, C.x, C.y);
                line(M.x, M.y, D.x, D.y);
                line(M.x, M.y, K.x, K.y);
                arc(M.x, M.y, 2 * R, 2 * R, 0, TWO_PI);
            }

            if (step >= 3) {
                point(B.x, B.y); text("B", B.x + 5, B.y + 5);
            }

            if (step >= 4) {
                point(A.x, A.y); text("A", A.x + 5, A.y + 5);
            }

            if (step >= 5) {
                strokeWeight(3);
                line(A.x, A.y, B.x, B.y);
                line(B.x, B.y, C.x, C.y);
                line(C.x, C.y, A.x, A.y);
                strokeWeight(1);
            }

            if (verification) {
                point(E.x, E.y); text("E", E.x + 5, E.y + 5);
                line(C.x, C.y, E.x, E.y);
                line(K.x, K.y, B.x, B.y);
            }
        }

        document.getElementById('slider-c').addEventListener('input', function() {
            c = parseFloat(this.value);
            document.getElementById('c-value').textContent = c.toFixed(1);
            redrawScene();
        });

        document.getElementById('slider-h_C').addEventListener('input', function() {
            h_C = parseFloat(this.value);
            document.getElementById('h_C-value').textContent = h_C.toFixed(1);
            redrawScene();
        });

        document.getElementById('slider-delta').addEventListener('input', function() {
            delta = parseFloat(this.value);
            document.getElementById('delta-value').textContent = delta.toFixed(2);
            redrawScene();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function() {
            plotRange = parseFloat(this.value);
            document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);
            redrawScene();
        });

        document.querySelectorAll('input[name="steps"]').forEach(input => {
            input.addEventListener('change', function() {
                step = parseInt(this.value);
                redrawScene();
            });
        });

        document.getElementById('check-verification').addEventListener('change', function() {
            verification = this.checked;
            redrawScene();
        });
    </script>
</body>
</html>
