            width: 200px;
        }
        #control-panel span {
            width: 100px;
            text-align: right;
        }
        #plot-container {
            margin-top: 20px;
            border: 1px solid #ccc;
            background-color: white;
        }
    </style>
</head>
<body>
    <!-- Main container -->
    <div id="demo-container">

        <!-- Control Panel Section -->
        <div id="control-panel">

            <!-- Heat Transfer Coefficient Slider -->
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
                <span id="ua-value">0</span>
            </div>

            <!-- Reverse Reaction Pre-exponential Factor Slider -->
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
                <span id="kr0-value">0</span>
            </div>

            <!-- Feed Temperature Slider -->
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
                <span id="tf-value">265</span>
            </div>

            <!-- Residence Time Slider -->
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value">400</span>
            </div>

        </div>

        <!-- Visualization Section -->
        <div id="plot-container"></div>
    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const C_A0 = 10; // mmol/dm³
        const R = 1.987; // cal/mol K
        const E_f = 25000; // cal/mol
        const ΔH_rxn = -200000; // cal/mol
        const E_r = E_f - ΔH_rxn; // cal/mol
        const k_f0 = 1e15; // 1/s
        const rhoCp = 20000; // cal/(dm³ K)

        function updateValueDisplays() {
            const uaValue = document.getElementById('slider-ua').value;
            const kr0Value = document.getElementById('slider-kr0').value;
            const tfValue = document.getElementById('slider-tf').value;
            const tauValue = document.getElementById('slider-tau').value;

            document.getElementById('ua-value').textContent = uaValue;
            document.getElementById('kr0-value').textContent = kr0Value.toExponential(1);
            document.getElementById('tf-value').textContent = tfValue;
            document.getElementById('tau-value').textContent = tauValue;
        }

        function calculateCurves() {
            const ua = parseFloat(document.getElementById('slider-ua').value);
            const kr0Slider = parseFloat(document.getElementById('slider-kr0').value);
            const tf = parseFloat(document.getElementById('slider-tf').value);
            const tau = parseFloat(document.getElementById('slider-tau').value);
            const kr0 = kr0Slider * 1e12;

            const temperatures = Array.from({ length: 151 }, (_, i) => 250 + i);
            const massBalanceData = [];
            const energyBalanceData = [];

            temperatures.forEach(T => {
                const kf = k_f0 * Math.exp(-E_f / (R * T));
                const kr = kr0 * Math.exp(-E_r / (R * T));
                const CB_mass = (tau * kf * C_A0) / (1 + tau * (kf + kr));
                massBalanceData.push([T, CB_mass]);

                const CB_energy = (rhoCp * (T - tf) + ua * tau * (T - tf)) / (-ΔH_rxn);
                energyBalanceData.push([T, CB_energy]);
            });

            return { massBalanceData, energyBalanceData };
        }

        function updatePlot() {
            const { massBalanceData, energyBalanceData } = calculateCurves();

            const trace1 = {
                x: massBalanceData.map(d => d[0]),
                y: massBalanceData.map(d => d[1]),
                mode: 'lines',
                name: 'Mass Balance',
                line: { color: '#008000' }
            };

            const trace2 = {
                x: energyBalanceData.map(d => d[0]),
                y: energyBalanceData.map(d => d[1]),
                mode: 'lines',
                name: 'Energy Balance',
                line: { color: '#0000FF' }
            };

            const data = [trace1, trace2];

            const layout = {
                title: 'Product Concentration vs Temperature',
                xaxis: { title: 'temperature (K)', range: [250, 400] },
                yaxis: { title: 'product concentration (mmol/dm³)', range: [0, 12] },
                annotations: [
                    { x: 360, y: 6, text: 'mass balance', showarrow: true, arrowhead: 1, ax: 0, ay: -30, font: { color: '#008000' } },
                    { x: 280, y: 2, text: 'energy balance', showarrow: true, arrowhead: 1, ax: 0, ay: -30, font: { color: '#0000FF' } }
                ],
                font: { family: 'sans-serif' },
                grid: { xgridcolor: '#ccc', ygridcolor: '#ccc' }
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', () => {
                updateValueDisplays();
                updatePlot();
            });
        });

        // Initial plot setup
        updateValueDisplays();
        updatePlot();
    </script>
</body>
</html>
