    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="container">
        <div id="control-panel">
            <h4>Stability of Multilayered Emulsions</h4>
            <div class="slider-group">
                <label for="slider-phi">\( \phi \)</label>
                <input type="range" id="slider-phi" min="0.0001" max="0.05" step="0.0001" value="0.005">
                <span id="phi-value-display">0.005</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-gamma-sat">\( \Gamma_{sat} \) (mg/m²)</label>
                <input type="range" id="slider-gamma-sat" min="0.1" max="10" step="0.1" value="1.5">
                <span id="gamma-sat-value-display">1.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-r">\( r \) (nm)</label>
                <input type="range" id="slider-r" min="50" max="1000" step="10" value="300">
                <span id="r-value-display">300</span>
            </div>
            <div class="slider-group">
                <label for="slider-rpe">\( r_{pe} \) (nm)</label>
                <input type="range" id="slider-rpe" min="5" max="50" step="1" value="20">
                <span id="rpe-value-display">20</span>
            </div>
            <div class="slider-group">
                <label for="slider-mw">\( M_W \) (kDa)</label>
                <input type="range" id="slider-mw" min="10" max="1000" step="1" value="166">
                <span id="mw-value-display">166</span>
            </div>
            <hr>
            <div class="slider-group">
                <label for="slider-phi-axis">\( \phi \) axis max.</label>
                <input type="range" id="slider-phi-axis" min="0.01" max="0.5" step="0.01" value="0.05">
                <span id="phi-axis-value-display">0.05</span>
            </div>
            <div class="slider-group">
                <label for="slider-c-axis">C axis max.</label>
                <input type="range" id="slider-c-axis" min="1.0" max="5.0" step="0.1" value="2.0">
                <span id="c-axis-value-display">2.0</span>
            </div>
        </div>
        <div id="vis-area">
            <div id="info-display">
                <p id="formula-display"><span style="color:blue;">y_1(\phi)</span>, <span style="color:red;">y_2(\phi)</span>, and <span style="color:green;">y_3(\phi)</span></p>
                <p id="phimax-display"></p>
                <p id="range-display"></p>
            </div>
            <div id="plot-container"></div>
        </div>
    </div>
    <script>
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;

        function updateDisplayValues() {
            document.getElementById('phi-value-display').textContent = document.getElementById('slider-phi').value;
            document.getElementById('gamma-sat-value-display').textContent = document.getElementById('slider-gamma-sat').value;
            document.getElementById('r-value-display').textContent = document.getElementById('slider-r').value;
            document.getElementById('rpe-value-display').textContent = document.getElementById('slider-rpe').value;
            document.getElementById('mw-value-display').textContent = document.getElementById('slider-mw').value;
            document.getElementById('phi-axis-value-display').textContent = document.getElementById('slider-phi-axis').value;
            document.getElementById('c-axis-value-display').textContent = document.getElementById('slider-c-axis').value;
        }

        function calculatePhiMax(gamma_sat, r) {
            const A = (3 * gamma_sat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * document.getElementById('slider-mw').value / (document.getElementById('slider-rpe').value ** 3);
            return Math.sqrt(B / A);
        }

        function calculateY1(phi, gamma_sat, r) {
            return (3 * gamma_sat / r) * C_UNIT_CONVERSION * phi;
        }

        function calculateY2(phi, gamma_sat, r) {
            return calculateY1(phi, gamma_sat, r) / (1 - phi);
        }

        function calculateY3(phi, gamma_sat, r) {
            return calculateY1(phi, gamma_sat, r) + (K_DEPLETION * document.getElementById('slider-mw').value / (document.getElementById('slider-rpe').value ** 3)) / phi;
        }

        function updatePlot() {
            const phi = parseFloat(document.getElementById('slider-phi').value);
            const gamma_sat = parseFloat(document.getElementById('slider-gamma-sat').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const phi_max = calculatePhiMax(gamma_sat, r);

            const x = Array.from({ length: 100 }, (_, i) => 0.0001 + i * (phi_max - 0.0001) / 99);
            const y1 = x.map(phi => calculateY1(phi, gamma_sat, r));
            const y2 = x.map(phi => calculateY2(phi, gamma_sat, r));
            const y3 = x.map(phi => calculateY3(phi, gamma_sat, r));

            const data = [
                { x, y: y1, type: 'scatter', mode: 'lines', line: { color: 'blue' } },
                { x, y: y2, type: 'scatter', mode: 'lines', line: { color: 'red' } },
                { x, y: y3, type: 'scatter', mode: 'lines', line: { color: 'green' } },
                { x: x.slice(0, x.indexOf(phi_max) + 1), y: y2.slice(0, x.indexOf(phi_max) + 1), type: 'scatter', mode: 'lines', fill: 'tonexty', fillcolor: 'rgba(173, 216, 230, 0.5)' },
                { x: [phi, phi, phi], y: [y1[phi_index], y2[phi_index], y3[phi_index]], type: 'scatter', mode: 'markers', marker: { color: ['blue', 'red', 'green'] } }
            ];

            const layout = {
                xaxis: { title: '\( \phi \) (volume fraction)', range: [0, parseFloat(document.getElementById('slider-phi-axis').value)] },
                yaxis: { title: 'C (kg/m³)', range: [0, parseFloat(document.getElementById('slider-c-axis').value)] },
                shapes: [{
                    type: 'line',
                    x0: phi,
                    x1: phi,
                    y0: 0,
                    y1: parseFloat(document.getElementById('slider-c-axis').value),
                    line: { color: 'grey', dash: 'dash' }
                }],
                showlegend: false
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        function updateInfoDisplay() {
            const phi = parseFloat(document.getElementById('slider-phi').value);
            const gamma_sat = parseFloat(document.getElementById('slider-gamma-sat').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const phi_max = calculatePhiMax(gamma_sat, r);

            document.getElementById('phimax-display').textContent = `\( \\phi_{max} = ${phi_max.toFixed(4)} \)`;

            const lower_bound = calculateY2(phi, gamma_sat, r);
            const upper_bound = calculateY3(phi, gamma_sat, r);
            document.getElementById('range-display').textContent = `Stability range: [${lower_bound.toFixed(2)}, ${upper_bound.toFixed(2)}]`;
        }

        function updateAll() {
            updateDisplayValues();
            updatePlot();
            updateInfoDisplay();
        }

        document.querySelectorAll('input[type="range"]').forEach(input => {
            input.addEventListener('input', updateAll);
        });

        updateAll();
    </script>
</body>
</html>
