<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Collision Simulation</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #f0f0f0;
        }
        #main-container {
            display: flex;
            width: 100%;
            max-width: 1200px;
            height: 800px;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #fff;
            border-right: 1px solid #ccc;
            box-shadow: 2px 0 5px rgba(0,0,0,0.1);
        }
        #control-panel h4 {
            margin-top: 20px;
        }
        #control-panel label {
            display: block;
            margin-bottom: 5px;
        }
        #control-panel input[type="range"] {
            width: 100%;
        }
        #joystick-container {
            width: 100px;
            height: 100px;
            border: 1px solid #ccc;
            position: relative;
            margin: 20px auto;
        }
        #joystick-knob {
            width: 20px;
            height: 20px;
            background-color: #000;
            border-radius: 50%;
            position: absolute;
            top: 40px;
            left: 40px;
        }
        #canvas-container {
            flex-grow: 1;
            position: relative;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-time">time relative to collision (s)</label>
            <input type="range" id="slider-time" min="-30" max="30" step="0.1" value="-10">
            <span id="label-time">-10.0</span>

            <h4>object 1</h4>
            <label for="slider-m1">mass (kg)</label>
            <input type="range" id="slider-m1" min="1" max="10" step="0.01" value="5">
            <span id="label-m1">5.00</span>
            <label for="slider-v1">speed (m/s)</label>
            <input type="range" id="slider-v1" min="1" max="20" step="0.1" value="10">
            <span id="label-v1">10.0</span>

            <h4>object 2</h4>
            <label for="slider-m2">mass (kg)</label>
            <input type="range" id="slider-m2" min="1" max="10" step="0.01" value="3">
            <span id="label-m2">3.00</span>
            <label for="slider-v2">speed (m/s)</label>
            <input type="range" id="slider-v2" min="1" max="20" step="0.1" value="5">
            <span id="label-v2">5.0</span>

            <label>observer velocity (m/s)</label>
            <div id="joystick-container">
                <div id="joystick-knob"></div>
            </div>
            <span id="label-v0x">1.10</span>
            <span id="label-v0y">1.10</span>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let v0x = 1.1, v0y = 1.1;
        let m1 = 5, m2 = 3, v1 = 10, v2 = 5;

        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();
            updateSimulation();
        }

        function draw() {
            background(255);
            drawTrajectories();
            drawParticles();
            drawObserverVelocity();
            drawMetrics();
        }

        function updateSimulation() {
            const s1_cm = v1;
            const s2_cm = (m1 / m2) * s1_cm;
            const u1_cm = createVector(s1_cm, 0);
            const u2_cm = createVector(-s2_cm, 0);

            const v1_cm = p5.Vector.fromAngle(u1_cm.heading() - radians(45));
            const v2_cm = p5.Vector.fromAngle(u2_cm.heading() - radians(45));

            const v0 = createVector(v0x, v0y);

            const u1_obs = p5.Vector.add(u1_cm, v0);
            const u2_obs = p5.Vector.add(u2_cm, v0);

            const energy = 0.5 * m1 * u1_obs.magSq() + 0.5 * m2 * u2_obs.magSq();

            document.getElementById('label-v0x').textContent = v0x.toFixed(2);
            document.getElementById('label-v0y').textContent = v0y.toFixed(2);
            document.getElementById('label-m1').textContent = m1.toFixed(2);
            document.getElementById('label-m2').textContent = m2.toFixed(2);
            document.getElementById('label-v1').textContent = v1.toFixed(1);
            document.getElementById('label-v2').textContent = v2.toFixed(1);
            document.getElementById('label-energy').textContent = energy.toFixed(2);
        }

        function drawTrajectories() {
            strokeWeight(2);
            stroke(255, 0, 0);
            drawingContext.setLineDash([5, 5]);
            line(-width, 0, width, 0);

            stroke(0, 0, 255);
            drawingContext.setLineDash([5, 5]);
            line(-width, 0, width, 0);
        }

        function drawParticles() {
            const t = parseFloat(document.getElementById('slider-time').value);
            const v1_cm = p5.Vector.fromAngle(radians(-45));
            const v2_cm = p5.Vector.fromAngle(radians(-45));

            const pos1 = v1_cm.copy().mult(t);
            const pos2 = v2_cm.copy().mult(t);

            const v0 = createVector(v0x, v0y);
            const pos1_obs = p5.Vector.add(pos1, v0.mult(t));
            const pos2_obs = p5.Vector.add(pos2, v0.mult(t));

            const r1 = sqrt(m1) * 5;
            const r2 = sqrt(m2) * 5;

            fill(255, 0, 0);
            ellipse(pos1_obs.x + width / 2, -pos1_obs.y + height / 2, r1 * 2);

            fill(0, 0, 255);
            ellipse(pos2_obs.x + width / 2, -pos2_obs.y + height / 2, r2 * 2);
        }

        function drawObserverVelocity() {
            const v0 = createVector(v0x, v0y);
            stroke(0);
            strokeWeight(2);
            line(width / 2, height / 2, width / 2 - v0.x * 20, height / 2 - v0.y * 20);

            const arrowSize = 7;
            push();
            translate(width / 2 - v0.x * 20, height / 2 - v0.y * 20);
            rotate(v0.heading());
            line(0, 0, arrowSize, 0);
            line(0, 0, -arrowSize, arrowSize / 2);
            line(0, 0, -arrowSize, -arrowSize / 2);
            pop();
        }

        function drawMetrics() {
            const v0 = createVector(v0x, v0y);
            const v0Magnitude = v0.mag();

            const textX = 20;
            const textY = 20;
            textSize(16);
            fill(0);

            text(`v₀: ${v0Magnitude.toFixed(2)} m/s`, textX, textY);
            text(`v₁: ${v1.toFixed(1)} m/s`, textX, textY + 20);
            text(`v₂: ${v2.toFixed(1)} m/s`, textX, textY + 40);
            text(`Energy: ${energy.toFixed(2)} J`, textX, textY + 60);
        }

        function handleSliderChange() {
            m1 = parseFloat(document.getElementById('slider-m1').value);
            m2 = parseFloat(document.getElementById('slider-m2').value);
            v1 = parseFloat(document.getElementById('slider-v1').value);
            v2 = parseFloat(document.getElementById('slider-v2').value);
            updateSimulation();
            redraw();
        }

        function handleTimeSliderChange() {
            redraw();
        }

        function handleJoystickMove(event) {
            const rect = event.target.getBoundingClientRect();
            const x = event.clientX - rect.left - 50;
            const y = event.clientY - rect.top - 50;

            v0x = constrain(x / 50, -5, 5);
            v0y = constrain(y / 50, -5, 5);

            document.getElementById('joystick-knob').style.left = `${x + 50}px`;
            document.getElementById('joystick-knob').style.top = `${y + 50}px`;

            updateSimulation();
            redraw();
        }

        document.getElementById('slider-m1').addEventListener('input', handleSliderChange);
        document.getElementById('slider-m2').addEventListener('input', handleSliderChange);
        document.getElementById('slider-v1').addEventListener('input', handleSliderChange);
        document.getElementById('slider-v2').addEventListener('input', handleSliderChange);
        document.getElementById('slider-time').addEventListener('input', handleTimeSliderChange);
        document.getElementById('joystick-container').addEventListener('mousemove', handleJoystickMove);
        document.getElementById('joystick-container').addEventListener('mousedown', () => {
            document.getElementById('joystick-container').addEventListener('mousemove', handleJoystickMove);
        });
        document.getElementById('joystick-container').addEventListener('mouseup', () => {
            document.getElementById('joystick-container').removeEventListener('mousemove', handleJoystickMove);
        });

        setup();
    </script>
</body>
</html>
