<body>
    <div class="main-container">
        <div class="control-panel">
            <label for="shift-slider">shift</label>
            <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            <input type="text" id="text-input" list="phrase-list" value="abcd">
            <datalist id="phrase-list">
                <option value="abcd"></option>
                <option value="test phrase"></option>
                <option value="to be or not to be"></option>
                <option value="the quick brown fox jumped over the lazy dog"></option>
            </datalist>
        </div>
        <div class="display-area">
            <div class="alphabet-mapping">
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>
            <div class="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>
            <div class="text-output-section">
                <p id="plaintext-output"><span class="plaintext-label">plaintext :</span> abcd</p>
                <p id="ciphertext-output"><span class="ciphertext-label">ciphertext:</span> MNOP</p>
            </div>
        </div>
    </div>

    <script>
        const shiftSlider = document.getElementById('shift-slider');
        const textInput = document.getElementById('text-input');
        const plaintextAlphabet = document.getElementById('plaintext-alphabet');
        const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');
        const encryptionKeyDisplay = document.getElementById('encryption-key-display');
        const decryptionKeyDisplay = document.getElementById('decryption-key-display');
        const plaintextOutput = document.getElementById('plaintext-output');
        const ciphertextOutput = document.getElementById('ciphertext-output');

        function updateCipher() {
            const shiftValue = parseInt(shiftSlider.value);
            const text = textInput.value.toLowerCase();

            // Update Alphabet Mapping
            const alphabet = 'abcdefghijklmnopqrstuvwxyz';
            const shiftedAlphabet = alphabet.slice(shiftValue) + alphabet.slice(0, shiftValue);
            plaintextAlphabet.innerHTML = alphabet.split('').map(char => `<span>${char}</span>`).join('');
            ciphertextAlphabet.innerHTML = shiftedAlphabet.toUpperCase().split('').map(char => `<span>${char}</span>`).join('');

            // Update Key Display
            encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
            decryptionKeyDisplay.textContent = `decryption key: ${(26 - shiftValue) % 26}`;

            // Update Text Output
            const ciphertext = encrypt(text, shiftValue);
            plaintextOutput.textContent = `plaintext : ${text}`;
            ciphertextOutput.textContent = `ciphertext: ${ciphertext}`;
        }

        function encrypt(text, shift) {
            return text.split('').map(char => {
                if (char >= 'a' && char <= 'z') {
                    const index = char.charCodeAt(0) - 'a'.charCodeAt(0);
                    const shiftedIndex = (index + shift) % 26;
                    return String.fromCharCode('A'.charCodeAt(0) + shiftedIndex);
                } else {
                    return char;
                }
            }).join('');
        }

        // Initial update on page load
        updateCipher();

        // Event listeners for updates
        shiftSlider.addEventListener('input', updateCipher);
        textInput.addEventListener('input', updateCipher);
    </script>
</body>
</html>
