<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Ionic Compound Visualization</title>
    <style>
        body {
            margin: 0;
            font-family: Arial, sans-serif;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel {
            width: 100%;
            padding: 10px;
            background-color: #f4f4f4;
            border-bottom: 1px solid #ccc;
            display: flex;
            justify-content: space-around;
        }
        #control-panel div {
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel button {
            margin: 5px;
            padding: 5px 10px;
            cursor: pointer;
            border: none;
            background-color: #ddd;
            transition: background-color 0.3s;
        }
        #control-panel button.active {
            background-color: #aaa;
        }
        #main-display-container {
            width: 100%;
            height: 80vh;
            border: 1px solid #ccc;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #canvas-container {
            width: 100%;
            height: 100%;
        }
        #ratio-text {
            margin-top: 10px;
            text-align: center;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div>
            <label>coordination</label>
            <button id="btn-threefold" class="active">threefold</button>
            <button id="btn-fourfold">fourfold</button>
            <button id="btn-sixfold">sixfold</button>
            <button id="btn-eightfold">eightfold</button>
        </div>
        <div>
            <label>relative anion radius</label>
            <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
            <span id="value-anion-radius">0.100</span>
        </div>
        <div>
            <label>relative cation radius</label>
            <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
            <span id="value-cation-radius">0.100</span>
        </div>
        <div>
            <label>opacity of anions</label>
            <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
        </div>
    </div>
    <div id="main-display-container">
        <div id="canvas-container"></div>
        <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/examples/js/controls/OrbitControls.js"></script>
    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight * 0.8);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.enableZoom = true;

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);

        const pointLight = new THREE.PointLight(0xffffff, 0.8);
        pointLight.position.set(5, 5, 5);
        scene.add(pointLight);

        let cationRadius = 0.1;
        let anionRadius = 0.1;
        let anionOpacity = 0.8;
        let coordinationType = 'threefold';

        function createMatcapTexture() {
            const canvas = document.createElement('canvas');
            canvas.width = 64;
            canvas.height = 64;
            const context = canvas.getContext('2d');
            const gradient = context.createRadialGradient(32, 32, 0, 32, 32, 32);
            gradient.addColorStop(0, 'blue');
            gradient.addColorStop(1, 'orange');
            context.fillStyle = gradient;
            context.fillRect(0, 0, 64, 64);
            return new THREE.CanvasTexture(canvas);
        }

        const cationMaterial = new THREE.MeshPhongMaterial({ matcap: createMatcapTexture() });
        const cationGeometry = new THREE.SphereGeometry(cationRadius, 32, 32);
        const cation = new THREE.Mesh(cationGeometry, cationMaterial);
        scene.add(cation);

        const anionMaterial = new THREE.MeshPhongMaterial({ color: 0xe0d8e0, transparent: true, opacity: anionOpacity });
        const anionGeometry = new THREE.SphereGeometry(anionRadius, 32, 32);
        const anions = [];

        function updateAnions() {
            anions.forEach(anion => scene.remove(anion));
            anions.length = 0;

            const positions = {
                'threefold': [
                    new THREE.Vector3(1.5, 0, 0),
                    new THREE.Vector3(-0.75, 1.299, 0),
                    new THREE.Vector3(-0.75, -1.299, 0)
                ],
                'fourfold': [
                    new THREE.Vector3(1.5, 1.5, 1.5),
                    new THREE.Vector3(1.5, -1.5, -1.5),
                    new THREE.Vector3(-1.5, 1.5, -1.5),
                    new THREE.Vector3(-1.5, -1.5, 1.5)
                ],
                'sixfold': [
                    new THREE.Vector3(1.5, 0, 0),
                    new THREE.Vector3(-1.5, 0, 0),
                    new THREE.Vector3(0, 1.5, 0),
                    new THREE.Vector3(0, -1.5, 0),
                    new THREE.Vector3(0, 0, 1.5),
                    new THREE.Vector3(0, 0, -1.5)
                ],
                'eightfold': [
                    new THREE.Vector3(1.5, 1.5, 1.5),
                    new THREE.Vector3(1.5, 1.5, -1.5),
                    new THREE.Vector3(1.5, -1.5, 1.5),
                    new THREE.Vector3(1.5, -1.5, -1.5),
                    new THREE.Vector3(-1.5, 1.5, 1.5),
                    new THREE.Vector3(-1.5, 1.5, -1.5),
                    new THREE.Vector3(-1.5, -1.5, 1.5),
                    new THREE.Vector3(-1.5, -1.5, -1.5)
                ]
            };

            positions[coordinationType].forEach(position => {
                const anion = new THREE.Mesh(anionGeometry, anionMaterial);
                anion.position.copy(position);
                anions.push(anion);
                scene.add(anion);
            });

            if (coordinationType !== 'threefold') {
                const boxGeometry = new THREE.BoxGeometry(3, 3, 3);
                const boxEdges = new THREE.EdgesGeometry(boxGeometry);
                const boxMaterial = new THREE.LineBasicMaterial({ color: 0x888888 });
                const boundingBox = new THREE.LineSegments(boxEdges, boxMaterial);
                scene.add(boundingBox);
            }
        }

        updateAnions();

        function updateRatioText() {
            const ratio = cationRadius / anionRadius;
            document.getElementById('ratio-text').textContent = `The ratio of the cation radius to the anion radius is ${ratio.toFixed(2)}.`;
        }

        document.getElementById('btn-threefold').addEventListener('click', () => setCoordinationType('threefold'));
        document.getElementById('btn-fourfold').addEventListener('click', () => setCoordinationType('fourfold'));
        document.getElementById('btn-sixfold').addEventListener('click', () => setCoordinationType('sixfold'));
        document.getElementById('btn-eightfold').addEventListener('click', () => setCoordinationType('eightfold'));

        function setCoordinationType(type) {
            coordinationType = type;
            document.querySelectorAll('#control-panel button').forEach(btn => btn.classList.remove('active'));
            document.getElementById(`btn-${type}`).classList.add('active');
            updateAnions();
            controls.reset();
            camera.position.z = 5;
            controls.update();
        }

        document.getElementById('slider-anion-radius').addEventListener('input', (event) => {
            anionRadius = parseFloat(event.target.value);
            document.getElementById('value-anion-radius').textContent = anionRadius.toFixed(3);
            anions.forEach(anion => anion.scale.setScalar(anionRadius));
            updateRatioText();
        });

        document.getElementById('slider-cation-radius').addEventListener('input', (event) => {
            cationRadius = parseFloat(event.target.value);
            document.getElementById('value-cation-radius').textContent = cationRadius.toFixed(3);
            cation.scale.setScalar(cationRadius);
            updateRatioText();
        });

        document.getElementById('slider-opacity').addEventListener('input', (event) => {
            anionOpacity = parseFloat(event.target.value);
            anionMaterial.opacity = anionOpacity;
        });

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight * 0.8;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight * 0.8);
        });
    </script>
</body>
</html>
