<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Inclined Plane Simulation</title>
    <style>
        body {
            display: flex;
            margin: 0;
            height: 100vh;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 150px;
            background-color: #f0f0f0;
            padding: 20px;
            box-sizing: border-box;
        }
        #slider-angle {
            writing-mode: bt-lr;
            transform: rotate(270deg);
            width: 100%;
            margin-bottom: 20px;
        }
        #animation-controls button {
            width: 100%;
            margin-bottom: 10px;
            padding: 10px;
            font-size: 16px;
            cursor: pointer;
        }
        #visualization-area {
            flex-grow: 1;
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
            box-sizing: border-box;
        }
        #info-display {
            margin-bottom: 20px;
            font-size: 18px;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="control-panel">
        <label for="slider-angle">slope θ</label>
        <input type="range" id="slider-angle" min="10" max="80" step="1" value="30">
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display">slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²</p>
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        let theta = 30;
        let isPlaying = false;
        let startTime = 0;
        const g = 9.81;
        const blockWidth = 20;
        const blockHeight = 10;
        const rampLength = 300;
        let blockPosition = 0;
        let velocity = 0;

        function setup() {
            const canvas = createCanvas(600, 400);
            canvas.parent('p5-canvas');
            angleMode(DEGREES);
            frameRate(60);
            updateInfoDisplay();
        }

        function draw() {
            background(255);
            translate(50, height - 50);

            // Draw inclined plane
            strokeWeight(2);
            stroke('yellow');
            fill('lightblue');
            triangle(0, 0, rampLength, 0, 0, -rampLength * tan(theta));

            // Draw block
            fill('darkgrey');
            stroke('black');
            rect(blockPosition, -blockHeight / 2, blockWidth, blockHeight);

            // Draw angle marker
            noFill();
            stroke('black');
            arc(0, 0, 50, 50, 0, theta);
            fill('black');
            textSize(12);
            text('θ', 25, 25);

            // Draw coordinate axes
            strokeWeight(1);
            stroke('black');
            line(0, 0, rampLength, 0); // x-axis
            line(0, 0, 0, -rampLength * tan(theta)); // y-axis

            // Draw force vectors
            drawArrow(blockPosition + blockWidth / 2, -blockHeight / 2, blockPosition + blockWidth / 2, -blockHeight / 2 - 50, 'red', 'F_grav=mg');
            drawArrow(blockPosition + blockWidth / 2, -blockHeight / 2, blockPosition + blockWidth / 2 + 50 * cos(theta), -blockHeight / 2 - 50 * sin(theta), 'deepskyblue', 'normal force');
            drawArrow(blockPosition + blockWidth / 2, -blockHeight / 2, blockPosition + blockWidth / 2 + 50 * sin(theta), -blockHeight / 2 + 50 * cos(theta), 'purple', 'F_net = m g sin(θ)');

            // Draw vector decomposition
            strokeDashArray([5, 5]);
            line(blockPosition + blockWidth / 2, -blockHeight / 2, blockPosition + blockWidth / 2 + 50 * sin(theta), -blockHeight / 2);
            line(blockPosition + blockWidth / 2 + 50 * sin(theta), -blockHeight / 2, blockPosition + blockWidth / 2 + 50 * sin(theta), -blockHeight / 2 - 50 * cos(theta));
            strokeDashArray([]);
            noFill();
            arc(blockPosition + blockWidth / 2, -blockHeight / 2, 20, 20, 270, 270 + theta);
            fill('black');
            textSize(12);
            text('θ', blockPosition + blockWidth / 2 + 10, -blockHeight / 2 + 10);

            // Update block position
            if (isPlaying) {
                if (startTime === 0) {
                    startTime = millis();
                }
                const t = (millis() - startTime) / 1000;
                const acceleration = g * sin(theta);
                blockPosition = 0.5 * acceleration * t * t;
                if (blockPosition >= rampLength) {
                    blockPosition = rampLength;
                    isPlaying = false;
                    startTime = 0;
                }
            }
        }

        function drawArrow(x1, y1, x2, y2, color, label) {
            stroke(color);
            line(x1, y1, x2, y2);
            push();
            translate(x2, y2);
            rotate(atan2(y2 - y1, x2 - x1) - PI / 2);
            triangle(0, 0, -10, 5, -10, -5);
            pop();
            fill(color);
            textSize(12);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2 - 10);
        }

        function updateInfoDisplay() {
            const acceleration = g * sin(theta);
            document.getElementById('info-display').innerHTML = `slope = \( \\theta \\) = ${theta}°; acceleration = \( g \\sin(\\theta) \\) = ${acceleration.toFixed(3)} m/s²`;
            MathJax.typeset();
        }

        document.getElementById('slider-angle').addEventListener('input', function() {
            theta = parseInt(this.value);
            blockPosition = 0;
            isPlaying = false;
            startTime = 0;
            updateInfoDisplay();
        });

        document.getElementById('btn-play').addEventListener('click', function() {
            isPlaying = true;
            startTime = millis();
        });

        document.getElementById('btn-pause').addEventListener('click', function() {
            isPlaying = false;
            startTime = 0;
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isPlaying = false;
            blockPosition = 0;
            startTime = 0;
            updateInfoDisplay();
        });
    </script>
</body>
</html>
