            <button id="btn-step-backward">-</button>
            <button id="btn-step-forward">+</button>
            <button id="btn-reset-step">«</button>
            <span id="step-value">1</span>
        </div>
        <div id="sort-control">
            <label>sort</label>
            <button id="btn-shellsort" class="sort-btn active">shellsort</button>
            <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
            <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
            <button id="btn-cocktail" class="sort-btn">cocktail</button>
            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>
    <div id="visualization-area">
        <canvas id="bar-chart-canvas"></canvas>
    </div>

    <script>
        let state = {
            currentAlgorithm: 'shellsort',
            numElements: 10,
            sortHistory: [],
            currentStep: 0,
            isPlaying: false,
            animationInterval: null
        };

        function setup() {
            createCanvas(800, 400);
            noLoop();
            randomizeArray();
        }

        function draw() {
            background(255);
            const margin = 50;
            const chartWidth = width - 2 * margin;
            const chartHeight = height - 2 * margin;
            const barWidth = chartWidth / state.numElements;

            // Draw Y-axis
            stroke(0);
            line(margin, margin, margin, height - margin);
            for (let i = 0; i <= 20; i += 5) {
                line(margin - 5, map(i, 0, 20, height - margin, margin), margin + 5, map(i, 0, 20, height - margin, margin));
                textSize(12);
                textAlign(RIGHT, CENTER);
                text(i, margin - 10, map(i, 0, 20, height - margin, margin));
            }

            // Draw bars
            colorMode(HSB);
            for (let i = 0; i < state.sortHistory[state.currentStep].length; i++) {
                const value = state.sortHistory[state.currentStep][i];
                const barHeight = map(value, 1, state.numElements, 0, chartHeight);
                fill(map(value, 1, state.numElements, 240, 0), 255, 255);
                stroke(0);
                rect(margin + i * barWidth, height - margin, barWidth, -barHeight);
            }
        }

        function randomizeArray() {
            state.numElements = parseInt(document.getElementById('slider-elements').value);
            let array = Array.from({ length: state.numElements }, (_, i) => i + 1);
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
            state.sortHistory = [array.slice()];
            state.currentStep = 0;
            document.getElementById('slider-step').max = 1;
            document.getElementById('slider-step').value = 1;
            document.getElementById('step-value').textContent = 1;
            if (state.isPlaying) {
                clearInterval(state.animationInterval);
                state.isPlaying = false;
                document.getElementById('btn-play-pause').textContent = '▶';
            }
            sortArray(state.currentAlgorithm);
        }

        function sortArray(algorithm) {
            const array = state.sortHistory[0].slice();
            switch (algorithm) {
                case 'shellsort':
                    shellSort(array);
                    break;
                case 'bubblesort':
                    bubbleSort(array);
                    break;
                case 'insertionsort':
                    insertionSort(array);
                    break;
                case 'cocktail':
                    cocktailShakerSort(array);
                    break;
                case 'bogosort':
                    bogoSort(array);
                    break;
            }
        }

        function shellSort(array) {
            let n = array.length;
            for (let gap = Math.floor(n / 2); gap > 0; gap = Math.floor(gap / 2)) {
                for (let i = gap; i < n; i++) {
                    let temp = array[i];
                    let j;
                    for (j = i; j >= gap && array[j - gap] > temp; j -= gap) {
                        array[j] = array[j - gap];
                        state.sortHistory.push(array.slice());
                    }
                    array[j] = temp;
                    state.sortHistory.push(array.slice());
                }
            }
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
        }

        function bubbleSort(array) {
            let n = array.length;
            let swapped;
            do {
                swapped = false;
                for (let i = 0; i < n - 1; i++) {
                    if (array[i] > array[i + 1]) {
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];
                        swapped = true;
                        state.sortHistory.push(array.slice());
                    }
                }
            } while (swapped);
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
        }

        function insertionSort(array) {
            let n = array.length;
            for (let i = 1; i < n; i++) {
                let key = array[i];
                let j = i - 1;
                while (j >= 0 && array[j] > key) {
                    array[j + 1] = array[j];
                    j = j - 1;
                    state.sortHistory.push(array.slice());
                }
                array[j + 1] = key;
                state.sortHistory.push(array.slice());
            }
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
        }

        function cocktailShakerSort(array) {
            let start = 0;
            let end = array.length - 1;
            let swapped = true;
            while (swapped) {
                swapped = false;
                for (let i = start; i < end; i++) {
                    if (array[i] > array[i + 1]) {
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];
                        swapped = true;
                        state.sortHistory.push(array.slice());
                    }
                }
                if (!swapped) break;
                swapped = false;
                end--;
                for (let i = end - 1; i >= start; i--) {
                    if (array[i] > array[i + 1]) {
                        [array[i], array[i + 1]] = [array[i + 1], array[i]];
                        swapped = true;
                        state.sortHistory.push(array.slice());
                    }
                }
                start++;
            }
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
        }

        function bogoSort(array) {
            let n = array.length;
            let sorted = false;
            while (!sorted) {
                for (let i = array.length - 1; i > 0; i--) {
                    const j = Math.floor(Math.random() * (i + 1));
                    [array[i], array[j]] = [array[j], array[i]];
                    state.sortHistory.push(array.slice());
                }
                sorted = isSorted(array);
            }
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
        }

        function isSorted(array) {
            for (let i = 0; i < array.length - 1; i++) {
                if (array[i] > array[i + 1]) return false;
            }
            return true;
        }

        document.getElementById('slider-elements').addEventListener('input', function() {
            document.getElementById('elements-value').textContent = this.value;
            if (state.currentAlgorithm === 'bogosort') {
                this.value = 7;
                document.getElementById('elements-value').textContent = 7;
            }
        });

        document.getElementById('slider-step').addEventListener('input', function() {
            state.currentStep = parseInt(this.value);
            document.getElementById('step-value').textContent = this.value;
            redraw();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            if (state.isPlaying) {
                clearInterval(state.animationInterval);
                state.isPlaying = false;
                this.textContent = '▶';
            } else {
                state.isPlaying = true;
                this.textContent = '❚❚';
                state.animationInterval = setInterval(() => {
                    if (state.currentStep < state.sortHistory.length - 1) {
                        state.currentStep++;
                        document.getElementById('slider-step').value = state.currentStep;
                        document.getElementById('step-value').textContent = state.currentStep;
                        redraw();
                    } else {
                        clearInterval(state.animationInterval);
                        state.isPlaying = false;
                        this.textContent = '▶';
                    }
                }, 200);
            }
        });

        document.getElementById('btn-step-backward').addEventListener('click', function() {
            if (state.currentStep > 0) {
                state.currentStep--;
                document.getElementById('slider-step').value = state.currentStep;
                document.getElementById('step-value').textContent = state.currentStep;
                redraw();
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            if (state.currentStep < state.sortHistory.length - 1) {
                state.currentStep++;
                document.getElementById('slider-step').value = state.currentStep;
                document.getElementById('step-value').textContent = state.currentStep;
                redraw();
            }
        });

        document.getElementById('btn-reset-step').addEventListener('click', function() {
            state.currentStep = 0;
            document.getElementById('slider-step').value = state.currentStep;
            document.getElementById('step-value').textContent = state.currentStep;
            redraw();
        });

        document.querySelectorAll('.sort-btn').forEach(button => {
            button.addEventListener('click', function() {
                document.querySelectorAll('.sort-btn').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                state.currentAlgorithm = this.id.replace('btn-', '');
                if (state.currentAlgorithm === 'bogosort') {
                    document.getElementById('slider-elements').disabled = true;
                    document.getElementById('slider-elements').value = 7;
                    document.getElementById('elements-value').textContent = 7;
                } else {
                    document.getElementById('slider-elements').disabled = false;
                }
                randomizeArray();
            });
        });

        document.getElementById('btn-randomize').addEventListener('click', randomizeArray);

        window.onload = setup;
    </script>
</body>
</html>
