            values.v1.textContent = parseFloat(sliders.v1.value).toFixed(2);
            values.x2.textContent = sliders.x2.value;
            values.v2.textContent = parseFloat(sliders.v2.value).toFixed(2);
        }

        function calculateIntersection() {
            const x1 = parseFloat(sliders.x1.value);
            const v1 = parseFloat(sliders.v1.value);
            const x2 = parseFloat(sliders.x2.value);
            const v2 = parseFloat(sliders.v2.value);

            let T, X;
            if (v1 === v2) {
                resultText.textContent = "The runners' paths never intersect";
                return { T: null, X: null };
            } else {
                T = (x1 - x2) / (v2 - v1);
                X = x1 + v1 * T;
                if (T < 0 || T > 1000) {
                    resultText.textContent = "The runners' paths never intersect";
                    return { T: null, X: null };
                } else {
                    resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
                    return { T, X };
                }
            }
        }

        function updatePlot() {
            const x1 = parseFloat(sliders.x1.value);
            const v1 = parseFloat(sliders.v1.value);
            const x2 = parseFloat(sliders.x2.value);
            const v2 = parseFloat(sliders.v2.value);

            const tData = [0, 1000];
            const pos1Data = [x1, x1 + v1 * 1000];
            const pos2Data = [x2, x2 + v2 * 1000];

            const intersection = calculateIntersection();
            const intersectionTrace = intersection.T !== null ? {
                x: [intersection.T],
                y: [intersection.X],
                mode: 'markers',
                marker: { color: 'green', size: 10, line: { color: 'darkgreen', width: 2 } },
                name: 'Intersection'
            } : [];

            const traces = [
                { x: tData, y: pos1Data, mode: 'lines', line: { color: 'rgb(70, 130, 180)' }, name: 'Runner 1' },
                { x: tData, y: pos2Data, mode: 'lines', line: { color: 'rgb(255, 165, 0)' }, name: 'Runner 2' },
                ...intersectionTrace
            ];

            const layout = {
                xaxis: { title: 't', range: [0, 1000], gridcolor: 'lightgray', zerolinecolor: 'gray' },
                yaxis: { title: 'distance', autorange: true, gridcolor: 'lightgray', zerolinecolor: 'gray' },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 }
            };

            Plotly.newPlot(plotContainer, traces, layout);
        }

        function resetSliders() {
            sliders.x1.value = 500;
            sliders.v1.value = -10;
            sliders.x2.value = 100;
            sliders.v2.value = 5;
            updateValues();
            updatePlot();
        }

        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                updateValues();
                updatePlot();
            });
        });

        document.getElementById('btn-reset').addEventListener('click', resetSliders);

        // Initial setup
        resetSliders();
    </script>
</body>
</html>
