        function draw() {
            background(202, 233, 255);
            noStroke();
            fill(155, 118, 83);
            rect(0, height / 2, width, height / 2);

            drawSun();
            drawClouds();
            drawRain();
            drawRocks();
            drawAnimal();
            drawPlants();
            drawWater();

            let sliderValue = int(sliderPhase.value());
            drawProcessArrows(sliderValue);

            checkTooltips();
        }

        function updateSliderValue() {
            let value = sliderPhase.value();
            sliderValueDisplay.html(value);
            redraw();
        }

        function resetSlider() {
            sliderPhase.value(0);
            updateSliderValue();
        }

        function drawSun() {
            fill(255, 221, 0);
            ellipse(width - 100, 100, 100, 100);
            stroke(255, 221, 0);
            noFill();
            for (let i = 0; i < 12; i++) {
                push();
                translate(width - 100, 100);
                rotate(radians(i * 30));
                line(50, 0, 70, 0);
                pop();
            }
        }

        function drawClouds() {
            fill(224, 224, 224);
            noStroke();
            ellipse(100, 50, 100, 60);
            ellipse(150, 50, 100, 60);
            ellipse(200, 50, 100, 60);
            fill(80, 80, 80);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Clouds", 150, 50);
        }

        function drawRain() {
            stroke(0, 0, 255);
            strokeWeight(2);
            line(120, 110, 130, 150);
            line(140, 110, 150, 150);
            line(160, 110, 170, 150);
        }

        function drawRocks() {
            fill(90, 90, 90);
            noStroke();
            ellipse(150, 450, 50, 30);
            ellipse(130, 470, 40, 20);
            ellipse(170, 470, 40, 20);
            fill(80, 80, 80);
            textSize(16);
            textAlign(CENTER, TOP);
            text("Rocks", 150, 480);
        }

        function drawAnimal() {
            fill(255);
            stroke(0);
            strokeWeight(2);
            ellipse(300, 400, 50, 70);
            ellipse(320, 380, 30, 50);
            rect(290, 430, 20, 40);
            rect(320, 430, 20, 40);
            fill(0, 0, 255);
            ellipse(310, 390, 10, 10);
            fill(90, 90, 90);
            ellipse(295, 395, 5, 5);
            ellipse(325, 395, 5, 5);
        }

        function drawPlants() {
            fill(144, 238, 144);
            noStroke();
            triangle(400, 450, 390, 400, 380, 450);
            triangle(420, 450, 410, 400, 400, 450);
            triangle(440, 450, 430, 400, 420, 450);
            fill(0, 100, 0);
            textSize(16);
            textAlign(LEFT, TOP);
            text("Plants", 450, 420);
        }

        function drawWater() {
            fill(0, 0, 255, 150);
            noStroke();
            ellipse(100, 450, 100, 100);
            ellipse(150, 450, 100, 100);
            fill(0, 0, 139);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Water", 125, 450);
        }

        function drawProcessArrows(sliderValue) {
            stroke(0);
            strokeWeight(2);
            if (sliderValue >= 1) drawArrow(150, 150, 150, 430, "Erosion");
            if (sliderValue >= 2) drawArrow(150, 430, 150, 480, "Runoff");
            if (sliderValue >= 3) drawArrow(150, 480, 100, 480, "Soil");
            if (sliderValue >= 4) drawArrow(150, 450, 100, 480, "Leaching");
            if (sliderValue >= 5) drawArrow(100, 480, 300, 400, "Plant uptake");
            if (sliderValue >= 6) drawArrow(400, 400, 300, 400, "Animals eat plants");
            if (sliderValue >= 7) drawArrow(300, 400, 300, 480, "Decomposition");
            if (sliderValue >= 8) drawCurvedArrow(100, 480, 150, 430, "Geologic uplift");
        }

        function drawArrow(x1, y1, x2, y2, label) {
            line(x1, y1, x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            let arrowSize = 7;
            push();
            translate(x2, y2);
            rotate(angle + PI);
            triangle(-arrowSize, arrowSize / 2, 0, 0, -arrowSize, -arrowSize / 2);
            pop();
            fill(0);
            textSize(14);
            textAlign(CENTER, CENTER);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2 - 10);
        }

        function drawCurvedArrow(x1, y1, x2, y2, label) {
            let controlX = (x1 + x2) / 2;
            let controlY = y1 - 100;
            bezier(x1, y1, controlX, controlY, controlX, controlY, x2, y2);
            let angle = atan2(y2 - controlY, x2 - controlX);
            let arrowSize = 7;
            push();
            translate(x2, y2);
            rotate(angle + PI);
            triangle(-arrowSize, arrowSize / 2, 0, 0, -arrowSize, -arrowSize / 2);
            pop();
            fill(0);
            textSize(14);
            textAlign(CENTER, CENTER);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2 - 10);
        }

        function checkTooltips() {
            for (let label of labels) {
                let textWidth = textWidth(label.text);
                if (mouseX > label.x - textWidth / 2 && mouseX < label.x + textWidth / 2 &&
                    mouseY > label.y - 10 && mouseY < label.y + 10) {
                    tooltipText = tooltips[label.text];
                    break;
                } else {
                    tooltipText = "";
                }
            }
            if (tooltipText !== "") {
                fill(255, 255, 204, 200);
                stroke(0);
                strokeWeight(1);
                rect(mouseX + 10, mouseY + 10, textWidth(tooltipText) + 20, 30, 5);
                fill(0);
                textSize(14);
                textAlign(LEFT, TOP);
                text(tooltipText, mouseX + 20, mouseY + 20);
            }
        }
    </script>
