                stroke(step.isFinal ? "#4d4dff" : "#ff4d4d");
                strokeWeight(6);
                line(example.vertices[edge.u].x, example.vertices[edge.u].y, example.vertices[edge.v].x, example.vertices[edge.v].y);
            }

            // Draw blossom
            if (step.blossom) {
                fill("rgba(0, 0, 255, 0.3)");
                stroke(0);
                strokeWeight(1);
                beginShape();
                for (let vertexIndex of step.blossom.vertices) {
                    vertex(example.vertices[vertexIndex].x, example.vertices[vertexIndex].y);
                }
                endShape(CLOSE);

                fill("#ffff00");
                stroke(0);
                strokeWeight(1);
                circle(example.vertices[step.blossom.topVertex].x, example.vertices[step.blossom.topVertex].y, 16);
            }

            // Draw vertices
            for (let vertex of example.vertices) {
                fill(0);
                noStroke();
                circle(vertex.x, vertex.y, 8);
            }
        }

        function updateDescription() {
            const example = demoData[currentExample];
            const step = example.steps[currentStep];
            document.getElementById("description-text").innerText = step.description;
        }

        document.querySelectorAll('#example-controls .btn').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#example-controls .btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                currentExample = btn.id.split('-')[2];
                currentStep = 0;
                document.querySelectorAll('#step-controls .btn').forEach(b => b.classList.remove('active'));
                document.getElementById('btn-step-1').classList.add('active');
                updateDescription();
            });
        });

        document.querySelectorAll('#step-controls .btn').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('#step-controls .btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
                currentStep = parseInt(btn.id.split('-')[2]) - 1;
                updateDescription();
            });
        });
    </script>
</body>
</html>
