    <script>
        // Import necessary modules
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';

        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 15;

        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('main-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const controls = new OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.25;
        controls.enableZoom = true;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1).normalize();
        scene.add(directionalLight2);

        // Materials
        const mainMaterial = new THREE.MeshStandardMaterial({ color: 0xD2B48C, transparent: true });
        const boundaryFrontMaterial = new THREE.MeshStandardMaterial({ color: 0xFFA500 });
        const boundaryBackMaterial = new THREE.MeshStandardMaterial({ color: 0x4169E1 });

        // Global variables for geometry
        let mainCylinderMesh;
        let drillCylinderMesh;
        let boundaryFrontTubeMesh;
        let boundaryBackTubeMesh;
        let csgResultMesh;

        // Utility function to update labels
        function updateLabel(id, value) {
            document.getElementById(id).textContent = parseFloat(value).toFixed(2);
        }

        // Function to calculate boundary curve
        function calculateBoundaryCurve(mainRadius, drillRadius, inclination, rotation, offset) {
            const frontPoints = [];
            const backPoints = [];

            for (let t = 0; t <= 2 * Math.PI; t += 0.01) {
                const x = drillRadius * Math.cos(t);
                const z = drillRadius * Math.sin(t);
                const y = offset;

                const drillMatrix = new THREE.Matrix4();
                drillMatrix.makeRotationX(inclination);
                drillMatrix.multiply(new THREE.Matrix4().makeRotationY(rotation));
                drillMatrix.setPosition(x, y, z);

                const worldPoint = new THREE.Vector3(x, y, z).applyMatrix4(drillMatrix);

                const direction = new THREE.Vector3(0, 1, 0).applyMatrix4(drillMatrix);
                const origin = worldPoint.clone().sub(direction.clone().multiplyScalar(10));

                const a = direction.x * direction.x + direction.z * direction.z;
                const b = 2 * (origin.x * direction.x + origin.z * direction.z);
                const c = origin.x * origin.x + origin.z * origin.z - mainRadius * mainRadius;

                const discriminant = b * b - 4 * a * c;
                if (discriminant >= 0) {
                    const sqrtDiscriminant = Math.sqrt(discriminant);
                    const t1 = (-b + sqrtDiscriminant) / (2 * a);
                    const t2 = (-b - sqrtDiscriminant) / (2 * a);

                    const intersection1 = origin.clone().add(direction.clone().multiplyScalar(t1));
                    const intersection2 = origin.clone().add(direction.clone().multiplyScalar(t2));

                    if (worldPoint.z > 0) {
                        frontPoints.push(intersection1);
                        frontPoints.push(intersection2);
                    } else {
                        backPoints.push(intersection1);
                        backPoints.push(intersection2);
                    }
                }
            }

            return { frontPoints, backPoints };
        }

        // Function to update the scene
        function updateScene() {
            const cylinderRadius = parseFloat(document.getElementById('slider-cylinder-radius').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const drillRadius = parseFloat(document.getElementById('slider-drill-radius').value);
            const inclination = parseFloat(document.getElementById('slider-inclination').value);
            const rotation = parseFloat(document.getElementById('slider-rotation').value);
            const offset = parseFloat(document.getElementById('slider-offset').value);
            const boundaryOnly = document.getElementById('checkbox-boundary-only').checked;

            updateLabel('label-cylinder-radius', cylinderRadius);
            updateLabel('label-opacity', opacity);
            updateLabel('label-drill-radius', drillRadius);
            updateLabel('label-inclination', inclination);
            updateLabel('label-rotation', rotation);
            updateLabel('label-offset', offset);

            // Remove existing meshes
            if (mainCylinderMesh) scene.remove(mainCylinderMesh);
            if (drillCylinderMesh) scene.remove(drillCylinderMesh);
            if (boundaryFrontTubeMesh) scene.remove(boundaryFrontTubeMesh);
            if (boundaryBackTubeMesh) scene.remove(boundaryBackTubeMesh);
            if (csgResultMesh) scene.remove(csgResultMesh);

            // Create main cylinder
            const mainGeometry = new THREE.CylinderGeometry(cylinderRadius, cylinderRadius, 8, 32);
            mainCylinderMesh = new THREE.Mesh(mainGeometry, mainMaterial);
            mainCylinderMesh.material.opacity = boundaryOnly ? 0.15 : opacity;
            mainCylinderMesh.material.color.set(boundaryOnly ? 0xf0f0f0 : 0xD2B48C);
            mainCylinderMesh.visible = !boundaryOnly;
            scene.add(mainCylinderMesh);

            // Create drill cylinder
            const drillGeometry = new THREE.CylinderGeometry(drillRadius, drillRadius, 10, 32);
            drillCylinderMesh = new THREE.Mesh(drillGeometry, mainMaterial);
            drillCylinderMesh.rotation.x = Math.PI / 2;
            drillCylinderMesh.rotation.x += inclination;
            drillCylinderMesh.rotation.y += rotation;
            drillCylinderMesh.position.y = offset;
            scene.add(drillCylinderMesh);

            // Perform CSG subtraction
            const csg = new CSG();
            const mainCsg = CSG.fromMesh(mainCylinderMesh);
            const drillCsg = CSG.fromMesh(drillCylinderMesh);
            const resultCsg = mainCsg.subtract(drillCsg);

            csgResultMesh = resultCsg.toMesh(mainMaterial);
            csgResultMesh.material.opacity = opacity;
            csgResultMesh.visible = !boundaryOnly;
            scene.add(csgResultMesh);

            // Calculate boundary curve
            const { frontPoints, backPoints } = calculateBoundaryCurve(cylinderRadius, drillRadius, inclination, rotation, offset);

            if (frontPoints.length > 1) {
                const frontGeometry = new THREE.TubeGeometry(new THREE.BufferGeometry().setFromPoints(frontPoints), 64, 0.05, 8, false);
                boundaryFrontTubeMesh = new THREE.Mesh(frontGeometry, boundaryFrontMaterial);
                scene.add(boundaryFrontTubeMesh);
            }

            if (backPoints.length > 1) {
                const backGeometry = new THREE.TubeGeometry(new THREE.BufferGeometry().setFromPoints(backPoints), 64, 0.05, 8, false);
                boundaryBackTubeMesh = new THREE.Mesh(backGeometry, boundaryBackMaterial);
                scene.add(boundaryBackTubeMesh);
            }
        }

        // Event listeners for sliders and checkbox
        document.querySelectorAll('#control-panel input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updateScene);
        });

        document.getElementById('checkbox-boundary-only').addEventListener('change', updateScene);

        // Initial scene update
        updateScene();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();

        // Handle window resize
        window.addEventListener('resize', () => {
            const width = window.innerWidth;
            const height = window.innerHeight;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        });
    </script>
</body>
</html>
