            drawSaturationBar();
            drawBrightnessBar();
            drawResultBar();

            positionArrow('#arrow-hue', hueValue);
            positionArrow('#arrow-saturation', saturationValue);
            positionArrow('#arrow-brightness', brightnessValue);
        }

        function drawSaturationBar() {
            const canvas = createCanvas(canvasWidth, 20).parent('canvas-saturation-parent');
            for (let x = 0; x < width; x++) {
                stroke(hueValue, x / width, brightnessValue);
                line(x, 0, x, height);
            }
        }

        function drawBrightnessBar() {
            const canvas = createCanvas(canvasWidth, 20).parent('canvas-brightness-parent');
            for (let x = 0; x < width; x++) {
                stroke(hueValue, saturationValue, x / width);
                line(x, 0, x, height);
            }
        }

        function drawResultBar() {
            const canvas = createCanvas(canvasWidth, 20).parent('canvas-result-parent');
            fill(hueValue, saturationValue, brightnessValue);
            noStroke();
            rect(0, 0, width, height, 10);
        }

        function positionArrow(selector, value) {
            const arrow = select(selector);
            arrow.style('left', `${(value * canvasWidth) - (arrowWidth / 2)}px`);
        }

        function resetValues() {
            hueSlider.value(0);
            saturationSlider.value(0.5);
            brightnessSlider.value(1);
            updateVisuals();
        }
    </script>
