    <script>
        // Function to solve ODEs using 4th-order Runge-Kutta method
        function rk4(f, y0, t0, tf, h) {
            const n = Math.ceil((tf - t0) / h);
            const t = new Array(n + 1);
            const y = new Array(n + 1).fill().map(() => new Array(y0.length));
            t[0] = t0;
            y[0] = y0;

            for (let i = 0; i < n; i++) {
                const k1 = f(t[i], y[i]);
                const k2 = f(t[i] + h / 2, y[i].map((yi, j) => yi + h / 2 * k1[j]));
                const k3 = f(t[i] + h / 2, y[i].map((yi, j) => yi + h / 2 * k2[j]));
                const k4 = f(t[i] + h, y[i].map((yi, j) => yi + h * k3[j]));

                t[i + 1] = t[i] + h;
                y[i + 1] = y[i].map((yi, j) => yi + h / 6 * (k1[j] + 2 * k2[j] + 2 * k3[j] + k4[j]));
            }

            return { t, y };
        }

        // Define the system of ODEs
        function odes(t, y, alpha, beta, gamma) {
            const dTdt = gamma * y[1] * Math.exp(y[0] / (1 + y[0] / alpha)) - beta * y[0];
            const dCdt = 1 - y[1] * Math.exp(y[0] / (1 + y[0] / alpha));
            return [dTdt, dCdt];
        }

        // Function to update the plot
        function updatePlot(alpha, beta, gamma) {
            const h = 0.05;
            const { t, y } = rk4((t, y) => odes(t, y, alpha, beta, gamma), [0, 0], 0, 20, h);
            const deltaT = y.map(row => row[0]);
            const cX = y.map(row => 1000 * row[1]);

            const traceDeltaT = {
                x: t,
                y: deltaT,
                mode: 'lines',
                line: { color: 'orange', width: 2 },
                name: 'ΔT'
            };

            const traceCX = {
                x: t,
                y: cX,
                mode: 'lines',
                line: { color: 'steelblue', width: 2 },
                name: '1000 C_X'
            };

            const layout = {
                xaxis: { title: 'Time (t)', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    }
                ]
