<body>
    <div id="controls-container">
        <div class="control-group">
            <label for="slider-time">time</label>
            <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
            <span id="value-time">7.00</span>
        </div>
        <div class="control-group">
            <label for="slider-wavelength">wavelength</label>
            <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
            <span id="value-wavelength">4.00</span>
        </div>
        <div class="control-group">
            <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
            <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
            <span id="value-longitudinal-amplitude">2.00</span>
        </div>
        <div class="control-group">
            <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
            <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
            <span id="value-latitudinal-amplitude">1.00</span>
        </div>
    </div>
    <div id="canvas-container"></div>

    <script>
        let time = 7.0;
        let wavelength = 4.0;
        let longitudinalAmplitude = 2.0;
        let latitudinalAmplitude = 1.0;
        let particles = [];
        const gridCols = 10;
        const gridRows = 6;
        const canvasWidth = 700;
        const canvasHeight = 450;

        function setup() {
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            calculateParticleGrid();

            document.getElementById('slider-time').addEventListener('input', function(e) {
                time = parseFloat(e.target.value);
                document.getElementById('value-time').textContent = time.toFixed(2);
            });
            document.getElementById('slider-wavelength').addEventListener('input', function(e) {
                wavelength = parseFloat(e.target.value);
                document.getElementById('value-wavelength').textContent = wavelength.toFixed(2);
            });
            document.getElementById('slider-longitudinal-amplitude').addEventListener('input', function(e) {
                longitudinalAmplitude = parseFloat(e.target.value);
                document.getElementById('value-longitudinal-amplitude').textContent = longitudinalAmplitude.toFixed(2);
            });
            document.getElementById('slider-latitudinal-amplitude').addEventListener('input', function(e) {
                latitudinalAmplitude = parseFloat(e.target.value);
                document.getElementById('value-latitudinal-amplitude').textContent = latitudinalAmplitude.toFixed(2);
            });
        }

        function calculateParticleGrid() {
            particles = [];
            const colSpacing = canvasWidth / (gridCols + 1);
            const rowSpacing = canvasHeight / (gridRows + 1);
            for (let rowIndex = 0; rowIndex < gridRows; rowIndex++) {
                for (let colIndex = 0; colIndex < gridCols; colIndex++) {
                    const x0 = (colIndex + 1) * colSpacing;
                    const y0 = (rowIndex + 1) * rowSpacing;
                    particles.push({ x0, y0, x: x0, y: y0 });
                }
            }
        }

        function draw() {
            if (!document.getElementById('slider-time').classList.contains('active')) {
                time += 0.03;
                document.getElementById('slider-time').value = time;
                document.getElementById('value-time').textContent = time.toFixed(2);
            }
            background('#E0F0FF');
            const k = TWO_PI / wavelength;
            for (let i = 0; i < particles.length; i++) {
                const { x0, y0 } = particles[i];
                const rowIndex = Math.floor(i / gridCols);
                const amp_decay = exp(- (rowIndex / (gridRows - 1)) * 1.5);
                const ampX = longitudinalAmplitude * amp_decay * 20;
                const ampY = latitudinalAmplitude * amp_decay * 20;
                const phase = k * x0 - time;
                noFill();
                stroke('#8F998F');
                strokeWeight(2);
                ellipse(x0, y0, 2 * ampX, 2 * ampY);
                const dx = ampX * cos(phase);
                const dy = ampY * sin(phase);
                particles[i].x = x0 + dx;
                particles[i].y = y0 + dy;
            }
            stroke('#0000FF');
            strokeWeight(2);
            drawingContext.setLineDash([5, 5]);
            for (let rowIndex = 0; rowIndex < gridRows; rowIndex++) {
                for (let colIndex = 0; colIndex < gridCols - 1; colIndex++) {
                    const p1 = particles[rowIndex * gridCols + colIndex];
                    const p2 = particles[rowIndex * gridCols + colIndex + 1];
                    line(p1.x, p1.y, p2.x, p2.y);
                }
            }
            drawingContext.setLineDash([]);
            noStroke();
            fill('#FF0000');
            for (let particle of particles) {
                ellipse(particle.x, particle.y, 8, 8);
            }
        }
    </script>
</body>
</html>
