        </div>
    </div>
    <div class="column visualization-area">
        <div id="plot-top"></div>
        <div id="plot-bottom"></div>
    </div>

    <script>
        const btnArrhenius = document.getElementById('btn-arrhenius');
        const btnExponential = document.getElementById('btn-exponential');
        const sliderN = document.getElementById('slider-n');
        const sliderTMin = document.getElementById('slider-tmin');
        const sliderTMax = document.getElementById('slider-tmax');
        const sliderTRef = document.getElementById('slider-tref');
        const sliderC = document.getElementById('slider-c');
        const sliderB = document.getElementById('slider-b');
        const sliderA = document.getElementById('slider-a');

        const valueN = document.getElementById('value-n');
        const valueTMin = document.getElementById('value-tmin');
        const valueTMax = document.getElementById('value-tmax');
        const valueTRef = document.getElementById('value-tref');
        const valueC = document.getElementById('value-c');
        const valueB = document.getElementById('value-b');
        const valueA = document.getElementById('value-a');

        let model = 'Arrhenius';

        btnArrhenius.addEventListener('click', () => {
            if (model !== 'Arrhenius') {
                model = 'Arrhenius';
                btnArrhenius.classList.add('active-button');
                btnExponential.classList.remove('active-button');
                updatePlots();
            }
        });

        btnExponential.addEventListener('click', () => {
            if (model !== 'exponential') {
                model = 'exponential';
                btnExponential.classList.add('active-button');
                btnArrhenius.classList.remove('active-button');
                updatePlots();
            }
        });

        sliderN.addEventListener('input', () => {
            valueN.textContent = sliderN.value;
            updatePlots();
        });

        sliderTMin.addEventListener('input', () => {
            valueTMin.textContent = sliderTMin.value;
            sliderTMax.min = parseInt(sliderTMin.value) + 1;
            sliderTMax.value = Math.max(sliderTMax.value, parseInt(sliderTMin.value) + 1);
            valueTMax.textContent = sliderTMax.value;
            updatePlots();
        });

        sliderTMax.addEventListener('input', () => {
            valueTMax.textContent = sliderTMax.value;
            updatePlots();
        });

        sliderTRef.addEventListener('input', () => {
            valueTRef.textContent = sliderTRef.value;
            updatePlots();
        });

        sliderC.addEventListener('input', () => {
            valueC.textContent = parseFloat(sliderC.value).toFixed(3);
            updatePlots();
        });

        sliderB.addEventListener('input', () => {
            valueB.textContent = parseFloat(sliderB.value).toFixed(2);
            updatePlots();
        });

        sliderA.addEventListener('input', () => {
            valueA.textContent = parseFloat(sliderA.value).toFixed(0);
            updatePlots();
        });

        function linearRegression(data) {
            const n = data.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumXX = 0;

            for (let i = 0; i < n; i++) {
                sumX += data[i][0];
                sumY += data[i][1];
                sumXY += data[i][0] * data[i][1];
                sumXX += data[i][0] * data[i][0];
            }

            const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;

            let ssRes = 0, ssTot = 0;
            const meanY = sumY / n;

            for (let i = 0; i < n; i++) {
                ssRes += Math.pow(data[i][1] - (slope * data[i][0] + intercept), 2);
                ssTot += Math.pow(data[i][1] - meanY, 2);
            }

            const rSquared = 1 - ssRes / ssTot;

            return { slope, intercept, rSquared };
        }

        function updatePlots() {
            const n = parseInt(sliderN.value);
            const tMin = parseInt(sliderTMin.value);
            const tMax = parseInt(sliderTMax.value);
            const tRef = parseFloat(sliderTRef.value);
            const c = parseFloat(sliderC.value);
            const b = parseFloat(sliderB.value);
            const a = parseFloat(sliderA.value);

            const temperatures = Array.from({ length: n }, (_, i) => tMin + (tMax - tMin) * i / (n - 1));
            const yValues = [];
            const yFittedValues = [];

            if (model === 'Arrhenius') {
                for (const T of temperatures) {
                    const kT = Math.exp(-a / (T + b));
                    const kTRef = Math.exp(-a / (tRef + b));
                    yValues.push(kT / kTRef);
                }

                const transformedData = temperatures.map((T, i) => [T - tRef, Math.log(yValues[i])]);
                const { slope: fittedC, intercept, rSquared } = linearRegression(transformedData);

                for (const T of temperatures) {
                    yFittedValues.push(Math.exp(fittedC * (T - tRef)));
                }

                const traceData = {
                    x: temperatures,
                    y: yValues,
                    mode: 'markers',
                    marker: { color: '#d62728' },
                    name: 'Data'
                };

                const traceFit = {
                    x: temperatures,
                    y: yFittedValues,
                    mode: 'lines',
                    line: { color: '#d62728' },
                    name: `Exponential Fit (c = ${fittedC.toFixed(3)}, R² = ${rSquared.toFixed(3)})`
                };

                Plotly.newPlot('plot-top', [traceData, traceFit], {
                    title: `exponential model fit to Arrhenius data\nc = ${fittedC.toFixed(3)}, R² = ${rSquared.toFixed(3)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T_ref)' }
                });

                const traceLinearData = {
                    x: transformedData.map(d => d[0]),
                    y: transformedData.map(d => d[1]),
                    mode: 'markers',
                    marker: { color: '#2ca02c' },
                    name: 'Transformed Data'
                };

                const traceLinearFit = {
                    x: [transformedData[0][0], transformedData[n - 1][0]],
                    y: [intercept, intercept + fittedC * (transformedData[n - 1][0] - transformedData[0][0])],
                    mode: 'lines',
                    line: { color: '#2ca02c' },
                    name: `Linear Fit (c = ${fittedC.toFixed(3)})`
                };

                Plotly.newPlot('plot-bottom', [traceLinearData, traceLinearFit], {
                    title: `linear fit of the exponential model\nR² = ${rSquared.toFixed(3)}`,
                    xaxis: { title: 'T - T_ref' },
                    yaxis: { title: 'log(k(T)/k(T_ref))' },
                    shapes: [
                        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, line: { color: 'black', dash: 'dash' } },
                        { type: 'line', x0: 0, y0: 0, x1: 1, y1: 0, line: { color: 'black', dash: 'dash' } }
                    ]
                });
            } else {
                for (const T of temperatures) {
                    const kT = Math.exp(c * (T - tRef));
                    const kTRef = Math.exp(c * (tRef - tRef));
                    yValues.push(kT / kTRef);
                }

                const transformedData = temperatures.map((T, i) => [1 / (T + b), Math.log(yValues[i])]);
                const { slope: fittedA, intercept, rSquared } = linearRegression(transformedData);

                for (const T of temperatures) {
                    yFittedValues.push(Math.exp(-fittedA / (T + b)) / Math.exp(-fittedA / (tRef + b)));
                }

                const traceData = {
                    x: temperatures,
                    y: yValues,
                    mode: 'markers',
                    marker: { color: '#d62728' },
                    name: 'Data'
                };

                const traceFit = {
                    x: temperatures,
                    y: yFittedValues,
                    mode: 'lines',
                    line: { color: '#d62728' },
                    name: `Arrhenius Fit (a = ${-fittedA.toFixed(0)}, R² = ${rSquared.toFixed(3)})`
                };

                Plotly.newPlot('plot-top', [traceData, traceFit], {
                    title: `Arrhenius model fit to exponential data\na = ${-fittedA.toFixed(0)}, R² = ${rSquared.toFixed(3)}`,
                    xaxis: { title: 'T (°C)' },
                    yaxis: { title: 'k(T)/k(T_ref)' }
                });

                const traceLinearData = {
                    x: transformedData.map(d => d[0]),
                    y: transformedData.map(d => d[1]),
                    mode: 'markers',
                    marker: { color: '#1f77b4' },
                    name: 'Transformed Data'
                };

                const traceLinearFit = {
                    x: [transformedData[0][0], transformedData[n - 1][0]],
                    y: [intercept, intercept - fittedA * (transformedData[n - 1][0] - transformedData[0][0])],
                    mode: 'lines',
                    line: { color: '#1f77b4' },
                    name: `Linear Fit (a = ${-fittedA.toFixed(0)})`
                };

                Plotly.newPlot('plot-bottom', [traceLinearData, traceLinearFit], {
                    title: `linear fit of the Arrhenius model\nR² = ${rSquared.toFixed(3)}`,
                    xaxis: { title: '1/(T + b)' },
                    yaxis: { title: 'log(k(T)/k(T_ref))' },
                    shapes: [
                        { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, line: { color: 'black', dash: 'dash' } },
                        { type: 'line', x0: 0, y0: 0, x1: 1, y1: 0, line: { color: 'black', dash: 'dash' } }
                    ]
                });
            }
        }

        updatePlots();
    </script>
</body>
</html>
