        function caesarEncrypt(text, shift) {
            return text.split('').map(char => {
                const code = char.charCodeAt(0) - 97;
                return String.fromCharCode(((code + shift) % 26) + 97);
            }).join('');
        }

        function vigenereEncrypt(text, keyword) {
            keyword = keyword.toLowerCase().replace(/[^a-z]/g, '');
            if (!keyword) keyword = 'a';
            let result = '';
            for (let i = 0; i < text.length; i++) {
                const shift = keyword[i % keyword.length].charCodeAt(0) - 97;
                const code = text[i].charCodeAt(0) - 97;
                result += String.fromCharCode(((code + shift) % 26) + 97);
            }
            return result;
        }

        function autokeyEncrypt(text, keyword) {
            keyword = keyword.toLowerCase().replace(/[^a-z]/g, '');
            if (!keyword) keyword = 'a';
            let result = '';
            let key = keyword + text;
            for (let i = 0; i < text.length; i++) {
                const shift = key[i].charCodeAt(0) - 97;
                const code = text[i].charCodeAt(0) - 97;
                result += String.fromCharCode(((code + shift) % 26) + 97);
            }
            return result;
        }

        function updateVisualization() {
            const message = document.getElementById('select-message').value;
            const cipher = document.getElementById('select-cipher').value;
            const keyword = document.getElementById('input-keyword').value;
            const offset = parseInt(document.getElementById('slider-offset').value, 10);

            const plaintext = preprocessText(messages[message]);
            const originalTextSnippet = messages[message].slice(0, 80);
            document.getElementById('original-text').textContent = originalTextSnippet;

            const originalFreq = calculateFrequency(plaintext);
            renderChart('original-chart', originalFreq);

            let ciphertext = '';
            switch (cipher) {
                case 'Caesar':
                    ciphertext = caesarEncrypt(plaintext, offset);
                    break;
                case 'DeVigenere':
                    ciphertext = vigenereEncrypt(plaintext, keyword);
                    break;
                case 'Autokey':
                    ciphertext = autokeyEncrypt(plaintext, keyword);
                    break;
            }
            document.getElementById('encrypted-text').textContent = ciphertext;

            const encryptedFreq = calculateFrequency(ciphertext);
            renderChart('encrypted-chart', encryptedFreq);

            const offsetSlider = document.getElementById('slider-offset');
            const offsetValue = document.getElementById('offset-value');
            const keywordInput = document.getElementById('input-keyword');

            if (cipher === 'Caesar') {
                offsetSlider.disabled = false;
                offsetValue.style.display = 'inline';
                keywordInput.disabled = true;
            } else {
                offsetSlider.disabled = true;
                offsetValue.style.display = 'none';
                keywordInput.disabled = false;
            }
        }

        document.getElementById('select-message').addEventListener('change', updateVisualization);
        document.getElementById('select-cipher').addEventListener('change', updateVisualization);
        document.getElementById('input-keyword').addEventListener('input', updateVisualization);
        document.getElementById('slider-offset').addEventListener('input', (e) => {
            document.getElementById('offset-value').textContent = e.target.value;
            updateVisualization();
        });

        updateVisualization();
    </script>
</body>
</html>
