    <script>
        // Function to calculate the modified Bessel function of the second kind of order 0
        function besselK0(x) {
            if (x <= 2) {
                const y = x * x / 4;
                return (-Math.log(x / 2) * besselI0(x)) + ((1 + y / 3) + (y * y / 15) + (y * y * y / 84) + (y * y * y * y / 480));
            } else {
                const z = 2 / x;
                return Math.exp(-x) / Math.sqrt(x) * (1 + (z / 2) + (z * z / 6) + (z * z * z / 10) + (z * z * z * z / 14));
            }
        }

        // Function to calculate the modified Bessel function of the second kind of order 1
        function besselK1(x) {
            if (x <= 2) {
                const y = x * x / 4;
                return (Math.log(x / 2) * besselI1(x)) + (1 / x) * (1 + (y / 1) + (y * y / 8) + (y * y * y / 24) + (y * y * y * y / 60));
            } else {
                const z = 2 / x;
                return Math.exp(-x) / Math.sqrt(x) * (1 + (z / 1) + (z * z / 3) + (z * z * z / 10) + (z * z * z * z / 35));
            }
        }

        // Function to calculate the Laplace-domain solution P_bar(s)
        function pBar(s, lambda, omega) {
            const fs = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
            const sqrtFs = Math.sqrt(s * fs);
            return besselK0(sqrtFs) / (sqrtFs * besselK1(sqrtFs));
        }

        // Function to calculate the inverse Laplace transform using the Stehfest algorithm
        function stehfest(laplaceFunc, t, n_terms) {
            const V = [
                [1],
                [0, 1],
                [1, 0, 1 / 2],
                [0, 1, 0, 1 / 3],
                [3 / 2, 0, 3 / 2, 0, 1 / 8],
                [0, 3 / 2, 0, 3 / 4, 0, 1 / 15],
                [15 / 8, 0, 15 / 8, 0, 15 / 16, 0, 1 / 48],
                [0, 15 / 8, 0, 5 / 4, 0, 15 / 32, 0, 1 / 105],
                [35 / 16, 0, 35 / 16, 0, 35 / 32, 0, 35 / 96, 0, 1 / 384],
                [0, 35 / 16, 0, 35 / 16, 0, 21 / 32, 0, 35 / 384, 0, 1 / 945]
            ];
            let result = 0;
            for (let k = 1; k <= n_terms; k++) {
                result += V[n_terms - 1][k - 1] * laplaceFunc(k / (n_terms * t));
            }
            return Math.log(2) / t * result;
        }

        // Function to calculate the pressure drop at a given time tau
        function calculatePressureDrop(tau, lambda, omega) {
            return stehfest(s => pBar(s, lambda, omega) / s, tau, 10);
        }

        // Function to update the plot based on current controls
        function updatePlot() {
            const lambda = Math.pow(10, document.getElementById('slider-lambda').value);
            const omega = Math.pow(10, document.getElementById('slider-omega').value);
            document.getElementById('value-lambda').textContent = lambda.toExponential(5);
            document.getElementById('value-omega').textContent = omega.toFixed(2);

            const tauValues = Array.from({ length: 200 }, (_, i) => Math.pow(10, i * (10 / 199)));
            const yValues = tauValues.map(tau => calculatePressureDrop(tau, lambda, omega));

            const plotData = [
                {
                    x: tauValues,
                    y: yValues,
                    mode: 'lines',
                    line: { color: 'red', width: 2 }
                }
            ];

            const activeButton = document.querySelector('#control-panel button.active');
            const layout = {
                xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                yaxis: { range: [0, 12], gridcolor: '#ddd' },
                margin: { l: 80, r: 50, b: 60, t: 60 },
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            };

            if (activeButton.id === 'btn-pressure-drop') {
                layout.title = "pressure drawdown at the wellbore";
                layout.yaxis.title = "dimensionless pressure drop, ΔP";
            } else if (activeButton.id === 'btn-difference') {
                layout.title = "deviation from the asymptote";
                layout.yaxis.title = "dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)";
                layout.yaxis.range = [0, 3.5];
                plotData[0].y = plotData[0].y.map((y, i) => y - 0.5 * (Math.log(tauValues[i]) + 0.8091));
            }

            Plotly.newPlot('plot-container', plotData, layout);
        }

        // Initial plot setup
        document.addEventListener('DOMContentLoaded', () => {
            updatePlot();
            document.getElementById('slider-lambda').addEventListener('input', updatePlot);
            document.getElementById('slider-omega').addEventListener('input', updatePlot);
            document.querySelectorAll('#control-panel button').forEach(button => {
                button.addEventListener('click', () => {
                    document.querySelector('#control-panel button.active').classList.remove('active');
                    button.classList.add('active');
                    updatePlot();
                });
            });
        });
    </script>
