    <script>
        let dataPoints = [
            { "age": 30, "volume": 289 },
            { "age": 35, "volume": 445 },
            { "age": 40, "volume": 565 },
            { "age": 45, "volume": 704 },
            { "age": 50, "volume": 833 },
            { "age": 55, "volume": 924 }
        ];

        let u = 22.7319;
        let p = 58.2729;
        let selectedPointIndex = -1;

        function setup() {
            const canvasWidth = 500;
            const canvasHeight = 500;
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            recalculate();
        }

        function draw() {
            background('#FFFFE0');
            drawGrid();
            drawAxes();
            drawLabels();
            drawDataPoints();
            drawFittedCurve();
        }

        function drawGrid() {
            stroke(200);
            strokeWeight(1);
            for (let i = 20; i <= 80; i += 10) {
                let x = map(i, 20, 80, 0, width);
                dashedLine(x, 0, x, height);
