        function dashedLine(x1, y1, x2, y2) {
            let dashLength = 5;
            let deltaX = x2 - x1;
            let deltaY = y2 - y1;
            let numDashes = floor(sqrt(deltaX * deltaX + deltaY * deltaY) / dashLength);
            for (let i = 0; i < numDashes; i++) {
                let t = i / numDashes;
                let startX = lerp(x1, x2, t);
                let startY = lerp(y1, y2, t);
                let endX = lerp(x1, x2, min(1, t + 0.05));
                let endY = lerp(y1, y2, min(1, t + 0.05));
                line(startX, startY, endX, endY);
            }
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(2);
            line(0, height, width, height); // X-axis
            line(0, 0, 0, height); // Y-axis

            for (let i = 20; i <= 80; i += 10) {
                let x = map(i, 20, 80, 0, width);
                line(x, height - 5, x, height + 5);
                textSize(12);
                textAlign(CENTER, TOP);
                text(i, x, height + 5);
            }

            for (let i = 0; i <= 1500; i += 200) {
                let y = map(i, 0, 1500, height, 0);
                line(-5, y, 5, y);
                textSize(12);
                textAlign(RIGHT, CENTER);
                text(i, -5, y);
            }
        }

        function drawLabels() {
            textSize(16);
            textAlign(CENTER, BOTTOM);
            text("Forest Growth Curve", width / 2, 0);

            textAlign(CENTER, TOP);
            text("Stand age, yr", width / 2, height + 20);

            push();
            translate(0, height / 2);
            rotate(-HALF_PI);
            textAlign(CENTER, RIGHT);
            text("Wood volume, m³ ha⁻¹", 0, -20);
            pop();

            push();
            translate(width, 0);
            rotate(-HALF_PI);
            textAlign(CENTER, LEFT);
            text("4/5 law", 0, 20);
            pop();
        }

        function drawDataPoints() {
            stroke(0);
            strokeWeight(1);
            fill('#0000CD');
            for (let i = 0; i < dataPoints.length; i++) {
                let { age, volume } = dataPoints[i];
                let x = map(age, 20, 80, 0, width);
                let y = map(volume, 0, 1500, height, 0);
                ellipse(x, y, 10, 10);
                if (dist(mouseX, mouseY, x, y) < 5) {
                    cursor(HAND);
                }
            }
        }

        function drawFittedCurve() {
            noFill();
            stroke('#0000CD');
            beginShape();
            for (let x = u; x <= 80; x += 0.1) {
                let y = p * pow(x - u, 4 / 5);
                vertex(map(x, 20, 80, 0, width), map(y, 0, 1500, height, 0));
            }
            endShape();
        }

        function mousePressed() {
            for (let i = 0; i < dataPoints.length; i++) {
                let { age, volume } = dataPoints[i];
                let x = map(age, 20, 80, 0, width);
                let y = map(volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, x, y) < 5) {
                    selectedPointIndex = i;
                    return;
                }
            }
            if (mouseY >= 0 && mouseY <= height && mouseX >= 0 && mouseX <= width) {
                let age = map(mouseX, 0, width, 20, 80);
                let volume = map(mouseY, height, 0, 0, 1500);
                dataPoints.push({ age, volume });
                recalculate();
            }
        }

        function mouseDragged() {
            if (selectedPointIndex !== -1) {
                let age = map(mouseX, 0, width, 20, 80);
                let volume = map(mouseY, height, 0, 0, 1500);
                dataPoints[selectedPointIndex] = { age, volume };
                recalculate();
            }
        }

        function mouseReleased() {
            selectedPointIndex = -1;
        }

        function doubleClicked() {
            for (let i = 0; i < dataPoints.length; i++) {
                let { age, volume } = dataPoints[i];
                let x = map(age, 20, 80, 0, width);
                let y = map(volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, x, y) < 5) {
                    dataPoints.splice(i, 1);
                    recalculate();
                    break;
                }
            }
        }

        function recalculate() {
            let learningRate = 0.01;
            let iterations = 1000;
            let epsilon = 0.0001;
            let minAge = min(dataPoints.map(dp => dp.age));

            for (let iter = 0; iter < iterations; iter++) {
                let gradP = 0;
                let gradU = 0;
                let error = 0;

                for (let { age, volume } of dataPoints) {
                    let predicted = p * pow(age - u, 4 / 5);
                    let diff = predicted - volume;
                    error += diff * diff;
                    gradP += 2 * diff * pow(age - u, 4 / 5);
                    gradU += 2 * diff * p * (4 / 5) * pow(age - u, -1 / 5);
                }

                p -= learningRate * gradP;
                u -= learningRate * gradU;

                if (u >= minAge) {
                    u = minAge - epsilon;
                }

                if (error < 0.0001) {
                    break;
                }
            }

            document.getElementById('results-u').textContent = u.toFixed(4);
            document.getElementById('results-p').textContent = p.toFixed(4);

            let tableBody = document.getElementById('data-table-body');
            tableBody.innerHTML = '';
            for (let { age, volume } of dataPoints) {
                let row = document.createElement('tr');
                let ageCell = document.createElement('td');
                let volumeCell = document.createElement('td');
                ageCell.textContent = age.toFixed(2);
                volumeCell.textContent = volume.toFixed(2);
                row.appendChild(ageCell);
                row.appendChild(volumeCell);
                tableBody.appendChild(row);
            }
        }
    </script>
