<body>
    <div id="demo-container">
        <div id="controls">
            <label for="angle-slider">vertices sorted by angle:</label>
            <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
            <span id="angle-value">3</span>
            <div id="reset-button">+</div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        const outerPolygon = [[50, 50], [550, 50], [550, 550], [50, 550]];
        const holes = [
            [[100, 100], [250, 100], [250, 150], [200, 150], [200, 200], [100, 200]],
            [[300, 100], [450, 100], [450, 250], [400, 250], [400, 200], [300, 200]],
            [[100, 350], [250, 350], [250, 500], [200, 500], [200, 450], [100, 450]],
            [[300, 400], [450, 400], [450, 500], [300, 500]],
            [[300, 300], [350, 300], [350, 350], [300, 350]]
        ];

        let sourcePoint = { x: 275, y: 275 };
        let angleSliderValue = 3;
        let dragging = false;

        function setup() {
            createCanvas(600, 600);
            document.getElementById('angle-slider').addEventListener('input', updateSliderValue);
            document.getElementById('reset-button').addEventListener('click', resetDemo);
        }

        function draw() {
            background(255);
            drawPolygons();
            drawVertices();
            drawSourcePoint();
            drawVisibilityPolygon();
            if (angleSliderValue < 38) {
                drawHelperGraphics();
            }
        }

        function drawPolygons() {
            fill(255);
            stroke(170);
            beginShape();
            outerPolygon.forEach(([x, y]) => vertex(x, y));
            holes.forEach(hole => {
                beginContour();
                hole.forEach(([x, y]) => vertex(x, y));
                endContour();
            });
            endShape(CLOSE);
        }

        function drawVertices() {
            noStroke();
            fill(255, 0, 0);
            [...outerPolygon, ...holes.flat()].forEach(([x, y]) => ellipse(x, y, 4, 4));
        }

        function drawSourcePoint() {
            noStroke();
            fill(0, 0, 255);
            ellipse(sourcePoint.x, sourcePoint.y, 8, 8);
            stroke(0, 0, 255);
            noFill();
            ellipse(sourcePoint.x, sourcePoint.y, 10, 10);
        }

        function drawVisibilityPolygon() {
            const vertices = getSortedVertices();
            const visibilityPolygon = getVisibilityPolygon(vertices.slice(0, angleSliderValue));

            if (angleSliderValue < 38) {
                fill(255, 255, 153, 128);
                stroke(170, 170, 170, 128);
                beginShape();
                visibilityPolygon.slice(0, angleSliderValue - 1).forEach(([x, y]) => vertex(x, y));
                endShape(CLOSE);

                fill(255, 204, 102, 128);
                stroke(170, 170, 170, 128);
                beginShape();
                visibilityPolygon.slice(angleSliderValue - 2, angleSliderValue + 1).forEach(([x, y]) => vertex(x, y));
                endShape(CLOSE);
            } else {
                fill(255, 204, 102, 128);
                stroke(170, 170, 170, 128);
                beginShape();
                visibilityPolygon.forEach(([x, y]) => vertex(x, y));
                endShape(CLOSE);
            }
        }

        function drawHelperGraphics() {
            const vertices = getSortedVertices();
            const activeVertex = vertices[angleSliderValue - 1];
            const prevVertex = vertices[angleSliderValue - 2];

            stroke(0, 0, 255);
            line(sourcePoint.x, sourcePoint.y, activeVertex.x, activeVertex.y);

            stroke(0, 204, 0);
            strokeWeight(3);
            const intersectionEdge = getIntersectionEdge(activeVertex);
            if (intersectionEdge) {
                line(intersectionEdge[0][0], intersectionEdge[0][1], intersectionEdge[1][0], intersectionEdge[1][1]);
            }
            strokeWeight(1);

            stroke(0, 0, 255);
            const startAngle = atan2(prevVertex.y - sourcePoint.y, prevVertex.x - sourcePoint.x);
            const endAngle = atan2(activeVertex.y - sourcePoint.y, activeVertex.x - sourcePoint.x);
            arc(sourcePoint.x, sourcePoint.y, 20, 20, startAngle, endAngle);
        }

        function getSortedVertices() {
            const allVertices = [...outerPolygon, ...holes.flat()];
            return allVertices.sort((a, b) => {
                const angleA = atan2(a[1] - sourcePoint.y, a[0] - sourcePoint.x);
                const angleB = atan2(b[1] - sourcePoint.y, b[0] - sourcePoint.x);
                return angleA - angleB;
            });
        }

        function getVisibilityPolygon(vertices) {
            const visibilityPolygon = [];
            let lastIntersection = sourcePoint;

            for (let i = 0; i < vertices.length; i++) {
                const vertex = vertices[i];
                const intersection = getIntersection(vertex);
                visibilityPolygon.push(intersection);
                lastIntersection = intersection;
            }

            return visibilityPolygon;
        }

        function getIntersection(vertex) {
            const intersections = getAllIntersections(vertex);
            return intersections.reduce((closest, intersection) => {
                const dist1 = dist(sourcePoint.x, sourcePoint.y, closest.x, closest.y);
                const dist2 = dist(sourcePoint.x, sourcePoint.y, intersection.x, intersection.y);
                return dist1 < dist2 ? closest : intersection;
            }, { x: Infinity, y: Infinity });
        }

        function getAllIntersections(vertex) {
            const intersections = [];
            const edges = getEdges();

            edges.forEach(edge => {
                const intersection = getLineIntersection(sourcePoint, vertex, edge[0], edge[1]);
                if (intersection) {
                    intersections.push(intersection);
                }
            });

            return intersections;
        }

        function getEdges() {
            const edges = [];

            for (let i = 0; i < outerPolygon.length; i++) {
                edges.push([outerPolygon[i], outerPolygon[(i + 1) % outerPolygon.length]]);
            }

            holes.forEach(hole => {
                for (let i = 0; i < hole.length; i++) {
                    edges.push([hole[i], hole[(i + 1) % hole.length]]);
                }
            });

            return edges;
        }

        function getIntersectionEdge(vertex) {
            const edges = getEdges();
            for (let edge of edges) {
                const intersection = getLineIntersection(sourcePoint, vertex, edge[0], edge[1]);
                if (intersection) {
                    return edge;
                }
            }
            return null;
        }

        function getLineIntersection(p1, p2, q1, q2) {
            const dx = p2.x - p1.x;
            const dy = p2.y - p1.y;
            const dx2 = q2.x - q1.x;
            const dy2 = q2.y - q1.y;

            const determinant = dx * dy2 - dx2 * dy;
            if (determinant === 0) return null;

            const t = ((q1.x - p1.x) * dy2 - (q1.y - p1.y) * dx2) / determinant;
            const u = -((q1.x - p1.x) * dy - (q1.y - p1.y) * dx) / determinant;

            if (t >= 0 && t <= 1 && u >= 0) {
                return { x: p1.x + t * dx, y: p1.y + t * dy };
            }

            return null;
        }

        function updateSliderValue(event) {
            angleSliderValue = parseInt(event.target.value);
            document.getElementById('angle-value').textContent = angleSliderValue;
            redraw();
        }

        function resetDemo() {
            sourcePoint = { x: 275, y: 275 };
            angleSliderValue = 3;
            document.getElementById('angle-slider').value = angleSliderValue;
            document.getElementById('angle-value').textContent = angleSliderValue;
            redraw();
        }

        function mousePressed() {
            if (dist(mouseX, mouseY, sourcePoint.x, sourcePoint.y) < 10) {
                dragging = true;
            }
        }

        function mouseDragged() {
            if (dragging) {
                sourcePoint.x = constrain(mouseX, 50, 550);
                sourcePoint.y = constrain(mouseY, 50, 550);
                redraw();
            }
        }

        function mouseReleased() {
            dragging = false;
        }
    </script>
</body>
</html>
