    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div class="slider-container">
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div class="slider-container">
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        // Initial values
        let order = 5;
        let a = 2.0;
        let b = 0.0;

        // Function to format numbers to one decimal place
        const formatNumber = (num) => num.toFixed(1);

        // Function to calculate factorial
        const factorial = (n) => n ? n * factorial(n - 1) : 1;

        // Function to calculate power series coefficients
        const calculateCoefficients = (order, a, b) => {
            const f_derivs = [a, b];
            f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];
            f_derivs[3] = -[f_derivs[2] * f_derivs[1] + f_derivs[2] + 1.5 * f_derivs[1]] / f_derivs[0];
            f_derivs[4] = -[2 * f_derivs[3] * f_derivs[1] + Math.pow(f_derivs[2], 2) + f_derivs[3] + 1.5 * f_derivs[2]] / f_derivs[0];
            f_derivs[5] = -[3 * f_derivs[4] * f_derivs[1] + 4 * f_derivs[3] * f_derivs[2] + f_derivs[4] + 1.5 * f_derivs[3]] / f_derivs[0];
            return f_derivs.map((deriv, k) => deriv / factorial(k));
        };

        // Function to generate power series data
        const generatePowerSeriesData = (order, coefficients) => {
            const xValues = Array.from({ length: 200 }, (_, i) => i * 1.5 / 199);
            const yValues = xValues.map(x => coefficients.reduce((sum, coeff, k) => sum + coeff * Math.pow(x, k), 0));
            return { x: xValues, y: yValues };
        };

        // Function to solve ODE using Runge-Kutta method
        const rungeKuttaSolver = (order, a, b) => {
            const h = 0.0075; // Step size
            const steps = 200; // Number of steps
            let y1 = a, y2 = b;
            const xValues = [];
            const yValues = [];

            for (let i = 0; i <= steps; i++) {
                const k1_y1 = y2;
                const k1_y2 = -(y2 + 1.5 * y1) / y1;

                const k2_y1 = y2 + 0.5 * h * k1_y2;
                const k2_y2 = -((y2 + 0.5 * h * k1_y2) + 1.5 * (y1 + 0.5 * h * k1_y1)) / (y1 + 0.5 * h * k1_y1);

                const k3_y1 = y2 + 0.5 * h * k2_y2;
                const k3_y2 = -((y2 + 0.5 * h * k2_y2) + 1.5 * (y1 + 0.5 * h * k2_y1)) / (y1 + 0.5 * h * k2_y1);

                const k4_y1 = y2 + h * k3_y2;
                const k4_y2 = -((y2 + h * k3_y2) + 1.5 * (y1 + h * k3_y1)) / (y1 + h * k3_y1);

                y1 += (h / 6) * (k1_y1 + 2 * k2_y1 + 2 * k3_y1 + k4_y1);
                y2 += (h / 6) * (k1_y2 + 2 * k2_y2 + 2 * k3_y2 + k4_y2);

                xValues.push(i * h);
                yValues.push(y1);
            }

            return { x: xValues, y: yValues };
        };

        // Function to format polynomial string
        const formatPolynomial = (coefficients) => {
            return coefficients.reduce((str, coeff, k) => {
                if (Math.abs(coeff) < 1e-5) return str;
                const sign = coeff > 0 && str ? ' + ' : ' - ';
                const absCoeff = Math.abs(coeff).toFixed(5).replace(/\.?0+$/, '');
                const term = k === 0 ? absCoeff : k === 1 ? `${absCoeff} x` : `${absCoeff} x^${k}`;
                return str + sign + term;
            }, '').replace(/^ \+/, '');
        };

        // Function to update plot
        const updatePlot = () => {
            const coefficients = calculateCoefficients(order, a, b);
            const powerSeriesData = generatePowerSeriesData(order, coefficients);
            const numericalSolutionData = rungeKuttaSolver(order, a, b);

            let xLimit = 1.5;
            for (let i = 0; i < powerSeriesData.x.length; i++) {
                if (Math.abs(powerSeriesData.y[i] - numericalSolutionData.y[i]) >= 0.01) {
                    xLimit = powerSeriesData.x[i];
                    break;
                }
            }

            const polynomialString = formatPolynomial(coefficients);
            const traceRed = { x: powerSeriesData.x, y: powerSeriesData.y, mode: 'lines', line: { color: 'red', width: 2 } };
            const traceGreen = { x: numericalSolutionData.x, y: numericalSolutionData.y, mode: 'lines', line: { color: 'green', width: 3 } };
            const layout = {
                title: `Power Series Polynomial: ${polynomialString}`,
                xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
                yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                margin: { l: 50, r: 20, t: 50, b: 40 },
                shapes: [{
                    type: 'rect',
                    xref: 'x',
                    yref: 'paper',
                    x0: 0,
                    y0: 0,
                    x1: xLimit,
                    y1: 1,
                    fillcolor: 'lightblue',
                    opacity: 0.4,
                    layer: 'below',
                    line: { width: 0 }
                }]
            };

            Plotly.react('plot-container', [traceRed, traceGreen], layout);
        };

        // Event listeners for sliders
        document.getElementById('slider-order').addEventListener('input', (e) => {
            order = parseInt(e.target.value);
            document.getElementById('value-order').textContent = order;
            updatePlot();
        });

        document.getElementById('slider-a').addEventListener('input', (e) => {
            a = parseFloat(e.target.value);
            document.getElementById('value-a').textContent = formatNumber(a);
            updatePlot();
        });

        document.getElementById('slider-b').addEventListener('input', (e) => {
            b = parseFloat(e.target.value);
            document.getElementById('value-b').textContent = formatNumber(b);
            updatePlot();
        });

        // Initial plot setup
        updatePlot();
    </script>
</body>
</html>
