</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">text</label>
                <select id="select-text">
                    <option>To Be Or Not To Be</option>
                    <option>The quick brown fox jumps over the lazy dog</option>
                    <option>It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>plot</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>keyboard</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script>
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('visualization-canvas') });
        renderer.setSize(window.innerWidth, 600);
        scene.background = new THREE.Color(0xffffff);

        const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let currentText = "To Be Or Not To Be";
        let currentPlot = "2d";
        let currentKeyboard = "qwerty";

        const qwertyLayout = [
            ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
            ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
            ['Z', 'X', 'C', 'V', 'B', 'N', 'M']
        ];

        const dvorakLayout = [
            ['P', 'Y', 'F', 'G', 'C', 'R', 'L', '', '', ''],
            ['A', 'O', 'E', 'U', 'I', 'D', 'H', 'T', 'N', 'S'],
            [';', 'Q', 'J', 'K', 'X', 'B', 'M', 'W', 'V', 'Z']
        ];

        function calculateLetterFrequencies(text) {
            const freq = {};
            for (let char of text.toUpperCase()) {
                if (char >= 'A' && char <= 'Z') {
                    freq[char] = (freq[char] || 0) + 1;
                }
            }
            return freq;
        }

        function draw2DPlot(freq) {
            scene.clear();
            const letters = Object.keys(freq).sort();
            const maxFreq = Math.ceil(Math.max(...Object.values(freq)) / 50) * 50;

            const barWidth = 2;
            const barDepth = 1;
            const barSpacing = 3;

            const yScale = 20 / maxFreq;

            const geometry = new THREE.BoxGeometry(barWidth, 1, barDepth);
            const material = new THREE.MeshStandardMaterial();

            letters.forEach((letter, index) => {
                const height = freq[letter] * yScale;
                const x = index * (barWidth + barSpacing) - (letters.length * (barWidth + barSpacing) - barSpacing) / 2;

                const bar = new THREE.Mesh(geometry.clone(), material.clone());
                bar.position.set(x, height / 2, 0);
                bar.scale.y = height;

                const color = getColorForFrequency(freq[letter], maxFreq);
                bar.material.color.set(color);

                scene.add(bar);

                const textCanvas = document.createElement('canvas');
                const ctx = textCanvas.getContext('2d');
                ctx.font = '20px Arial';
                ctx.fillStyle = '#000';
                ctx.textAlign = 'center';
                ctx.fillText(letter, textCanvas.width / 2, textCanvas.height / 2);

                const texture = new THREE.CanvasTexture(textCanvas);
                texture.needsUpdate = true;

                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.scale.set(2, 2, 1);
                sprite.position.set(x, height + 1, 0);

                scene.add(sprite);
            });

            drawAxes(maxFreq);
            camera.position.set(0, 20, 50);
            camera.lookAt(scene.position);
            camera.updateProjectionMatrix();
        }

        function draw3DPlot(freq, layout) {
            scene.clear();

            const maxFreq = Math.ceil(Math.max(...Object.values(freq)) / 50) * 50;

            const yScale = 20 / maxFreq;

            const geometry = new THREE.CylinderGeometry(1, 1, 1, 32);
            const material = new THREE.MeshStandardMaterial();

            layout.forEach((row, rowIndex) => {
                row.forEach((letter, colIndex) => {
                    if (letter) {
                        const height = freq[letter] ? freq[letter] * yScale : 0.5;
                        const x = (colIndex - row.length / 2) * 4;
                        const z = (rowIndex - layout.length / 2) * 4;

                        const key = new THREE.Mesh(geometry.clone(), material.clone());
                        key.position.set(x, height / 2, z);
                        key.scale.y = height;

                        const color = getColorForRow(rowIndex);
                        key.material.color.set(color);

                        scene.add(key);

                        const textCanvas = document.createElement('canvas');
                        const ctx = textCanvas.getContext('2d');
                        ctx.font = '20px Arial';
                        ctx.fillStyle = '#000';
                        ctx.textAlign = 'center';
                        ctx.fillText(letter, textCanvas.width / 2, textCanvas.height / 2);

                        const texture = new THREE.CanvasTexture(textCanvas);
                        texture.needsUpdate = true;

                        const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                        const sprite = new THREE.Sprite(spriteMaterial);
                        sprite.scale.set(2, 2, 1);
                        sprite.position.set(x, height + 1, z);

                        scene.add(sprite);
                    }
                });
            });

            drawAxes(maxFreq);
            camera.position.set(0, 30, 50);
            camera.lookAt(scene.position);
            camera.updateProjectionMatrix();
        }

        function drawAxes(maxFreq) {
            const axisLength = 26 * 4; // assuming 4 units per bar/letter

            const points = [];
            points.push(new THREE.Vector3(-axisLength / 2, 0, 0));
            points.push(new THREE.Vector3(axisLength / 2, 0, 0));

            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: 0x000000 });
            const xAxis = new THREE.Line(geometry, material);
            scene.add(xAxis);

            for (let i = 0; i <= maxFreq; i += 50) {
                const lineGeometry = new THREE.BufferGeometry().setFromPoints([
                    new THREE.Vector3(-axisLength / 2, i, 0),
                    new THREE.Vector3(axisLength / 2, i, 0)
                ]);
                const line = new THREE.Line(lineGeometry, material);
                scene.add(line);

                const textCanvas = document.createElement('canvas');
                const ctx = textCanvas.getContext('2d');
                ctx.font = '20px Arial';
                ctx.fillStyle = '#000';
                ctx.textAlign = 'center';
                ctx.fillText(i.toString(), -axisLength / 2 - 20, i);

                const texture = new THREE.CanvasTexture(textCanvas);
                texture.needsUpdate = true;

                const spriteMaterial = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(spriteMaterial);
                sprite.scale.set(40, 20, 1);
                sprite.position.set(-axisLength / 2 - 10, i, 0);

                scene.add(sprite);
            }

            const gridHelper = new THREE.GridHelper(axisLength, 26, 0x888888, 0x444444);
            scene.add(gridHelper);
        }

        function getColorForFrequency(freq, maxFreq) {
            const normalized = freq / maxFreq;
            let r, g, b;
            if (normalized <= 0.25) {
                r = 0;
                g = 0;
                b = 255 * normalized * 4;
            } else if (normalized <= 0.5) {
                r = 0;
                g = 255 * (normalized - 0.25) * 4;
                b = 255;
            } else if (normalized <= 0.75) {
                r = 255 * (normalized - 0.5) * 4;
                g = 255;
                b = 255 - 255 * (normalized - 0.5) * 4;
            } else {
                r = 255;
                g = 255 - 255 * (normalized - 0.75) * 4;
                b = 0;
            }
            return `rgb(${r},${g},${b})`;
        }

        function getColorForRow(rowIndex) {
            if (rowIndex === 0) return '#80ff00'; // olive green/yellow-green
            if (rowIndex === 1) return '#00ffff'; // blue/cyan
            if (rowIndex === 2) return '#ff00ff'; // purple
            return '#ffffff'; // default white
        }

        function updateVisualization() {
            const freq = calculateLetterFrequencies(currentText);
            if (currentPlot === "2d") {
                draw2DPlot(freq);
            } else {
                const layout = currentKeyboard === "qwerty" ? qwertyLayout : dvorakLayout;
                draw3DPlot(freq, layout);
            }
            renderer.render(scene, camera);
        }

        document.getElementById('select-text').addEventListener('change', (event) => {
            currentText = event.target.value;
            updateVisualization();
        });

        document.getElementById('btn-plot-2d').addEventListener('click', () => {
            if (currentPlot !== "2d") {
                currentPlot = "2d";
                document.getElementById('btn-plot-2d').classList.add('active');
                document.getElementById('btn-plot-3d').classList.remove('active');
                updateVisualization();
            }
        });

        document.getElementById('btn-plot-3d').addEventListener('click', () => {
            if (currentPlot !== "3d") {
                currentPlot = "3d";
                document.getElementById('btn-plot-3d').classList.add('active');
                document.getElementById('btn-plot-2d').classList.remove('active');
                updateVisualization();
            }
        });

        document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
            if (currentKeyboard !== "qwerty") {
                currentKeyboard = "qwerty";
                document.getElementById('btn-keyboard-qwerty').classList.add('active');
                document.getElementById('btn-keyboard-dvorak').classList.remove('active');
                if (currentPlot === "3d") {
                    updateVisualization();
                }
            }
        });

        document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
            if (currentKeyboard !== "dvorak") {
                currentKeyboard = "dvorak";
                document.getElementById('btn-keyboard-dvorak').classList.add('active');
                document.getElementById('btn-keyboard-qwerty').classList.remove('active');
                if (currentPlot === "3d") {
                    updateVisualization();
                }
            }
        });

        updateVisualization();

        window.addEventListener('resize', () => {
            const width = window.innerWidth;
            const height = 600;
            renderer.setSize(width, height);
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
        });
    </script>
</body>
</html>
