                    <label for="slider-p-wl">wavelength</label>
                    <input type="range" id="slider-p-wl" min="0.5" max="5" step="0.01" value="3">
                    <span id="label-p-wl">3</span>
                </div>
            </div>
            <div class="control-section">
                <h3>shear wave</h3>
                <div>
                    <svg width="40" height="20"><path d="M0 10 C 5 0, 15 20, 20 10 S 25 0, 30 10 S 35 20, 40 10" stroke="black" fill="none"/></svg>
                    <svg width="40" height="20"><path d="M0 10 H 10 C 15 10, 15 0, 20 0 S 25 10, 30 10 H 40" stroke="black" fill="none"/></svg>
                </div>
                <div class="control-row">
                    <label for="slider-s-amp">amplitude</label>
                    <input type="range" id="slider-s-amp" min="0" max="0.4" step="0.001" value="0.1">
                    <span id="label-s-amp">0.1</span>
                </div>
                <div class="control-row">
                    <label for="slider-s-wl">wavelength</label>
                    <input type="range" id="slider-s-wl" min="0.5" max="5" step="0.01" value="1">
                    <span id="label-s-wl">1</span>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        let particles = [];
        let time = 0;
        let isPlaying = false;

        function setup() {
            const canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');
            initializeParticles();
            setupEventListeners();
        }

        function initializeParticles() {
            const numPoints = parseInt(document.getElementById('slider-points').value);
            particles = [];
            for (let i = 0; i < numPoints; i++) {
                particles.push({
                    x0: map(i, 0, numPoints - 1, 0, 5),
                    history: []
                });
            }
        }

        function setupEventListeners() {
            document.getElementById('slider-time').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('slider-trail').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('slider-points').addEventListener('input', () => {
                initializeParticles();
                updateLabelsAndRedraw();
            });
            document.getElementById('slider-p-amp').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('slider-p-wl').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('slider-s-amp').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('slider-s-wl').addEventListener('input', updateLabelsAndRedraw);
            document.getElementById('btn-play-pause').addEventListener('click', togglePlayPause);
            document.getElementById('btn-reset').addEventListener('click', resetSimulation);
        }

        function updateLabelsAndRedraw() {
            document.getElementById('label-time').textContent = parseFloat(document.getElementById('slider-time').value).toFixed(2);
            document.getElementById('label-trail').textContent = document.getElementById('slider-trail').value;
            document.getElementById('label-points').textContent = document.getElementById('slider-points').value;
            document.getElementById('label-p-amp').textContent = parseFloat(document.getElementById('slider-p-amp').value).toFixed(3);
            document.getElementById('label-p-wl').textContent = parseFloat(document.getElementById('slider-p-wl').value).toFixed(2);
            document.getElementById('label-s-amp').textContent = parseFloat(document.getElementById('slider-s-amp').value).toFixed(3);
            document.getElementById('label-s-wl').textContent = parseFloat(document.getElementById('slider-s-wl').value).toFixed(2);
            redraw();
        }

        function togglePlayPause() {
            isPlaying = !isPlaying;
            document.getElementById('btn-play-pause').textContent = isPlaying ? 'Pause' : 'Play';
        }

        function resetSimulation() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = 'Play';
            document.getElementById('slider-time').value = 0;
            document.getElementById('slider-trail').value = 5;
            document.getElementById('slider-points').value = 50;
            document.getElementById('slider-p-amp').value = 0.2;
            document.getElementById('slider-p-wl').value = 3;
            document.getElementById('slider-s-amp').value = 0.1;
            document.getElementById('slider-s-wl').value = 1;
            updateLabelsAndRedraw();
        }

        function draw() {
            background(255);
            if (isPlaying) {
                time += 0.01;
                if (time > 5) time = 0;
                document.getElementById('slider-time').value = time.toFixed(2);
                document.getElementById('label-time').textContent = time.toFixed(2);
            }

            const trailLength = parseInt(document.getElementById('slider-trail').value);
            const pAmp = parseFloat(document.getElementById('slider-p-amp').value);
            const pWl = parseFloat(document.getElementById('slider-p-wl').value);
            const sAmp = parseFloat(document.getElementById('slider-s-amp').value);
            const sWl = parseFloat(document.getElementById('slider-s-wl').value);

            drawAxes();

            particles.forEach(particle => {
                const dx = pAmp * sin(TWO_PI * (particle.x0 / pWl - time));
                const dy = sAmp * sin(TWO_PI * (particle.x0 / sWl - time));
                const xFinal = map(particle.x0 + dx, 0, 5, 0, width);
                const yFinal = map(dy, -0.5, 0.5, height, 0);

                // Draw trail
                if (trailLength > 0) {
                    particle.history.push({ x: xFinal, y: yFinal });
                    if (particle.history.length > trailLength) {
                        particle.history.shift();
                    }
                    particle.history.forEach((pos, index) => {
                        const alpha = map(index, 0, trailLength, 0, 255);
                        fill(106, 90, 205, alpha);
                        noStroke();
                        ellipse(pos.x, pos.y, 3);
                    });
                }

                // Draw vertical line
                stroke(211, 211, 211);
                line(xFinal, 0, xFinal, height);

                // Draw particle
                fill(106, 90, 205);
                noStroke();
                ellipse(xFinal, yFinal, 6);
            });
        }

        function drawAxes() {
            stroke(0);
            line(0, height / 2, width, height / 2); // X-axis
            line(0, 0, 0, height); // Y-axis

            // X-axis ticks
            for (let i = 0; i <= 5; i++) {
                const x = map(i, 0, 5, 0, width);
                line(x, height / 2 - 5, x, height / 2 + 5);
                textSize(12);
                textAlign(CENTER, TOP);
                text(i, x, height / 2 + 5);
            }

            // Y-axis ticks
            for (let i = -0.4; i <= 0.4; i += 0.2) {
                const y = map(i, -0.5, 0.5, height, 0);
                line(width / 2 - 5, y, width / 2 + 5, y);
                textSize(12);
                textAlign(RIGHT, CENTER);
                text(i.toFixed(1), width / 2 - 5, y);
            }
        }
    </script>
</body>
</html>
