<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-dip">fault dip</label>
                <input type="range" id="slider-dip" min="0" max="90" step="1" value="0">
                <span id="value-dip">0</span>
            </div>
            <div class="control-group">
                <label for="slider-depth">fault depth</label>
                <input type="range" id="slider-depth" min="100" max="1000" step="10" value="500">
                <span id="value-depth">500</span>
            </div>
            <div class="control-group">
                <label for="slider-limit">x and y plot limit</label>
                <input type="range" id="slider-limit" min="500" max="2000" step="50" value="1000">
                <span id="value-limit">1000</span>
            </div>
            <div class="control-group">
                <label>displacement component</label>
                <div id="btn-group-component">
                    <button id="btn-component-x">X</button>
                    <button id="btn-component-y">Y</button>
                    <button id="btn-component-z" class="active">Z</button>
                </div>
            </div>
            <div class="control-group">
                <label>fault type</label>
                <div id="btn-group-fault-type">
                    <button id="btn-fault-tensile" class="active">tensile</button>
                    <button id="btn-fault-strike-slip">strike-slip</button>
                    <button id="btn-fault-normal">normal</button>
                </div>
            </div>
        </div>
        <div id="plot-container">
            <div id="plot"></div>
        </div>
    </div>

    <script>
        const nu = 0.25; // Poisson's ratio
        const b = 1; // Dislocation magnitude

        function calculateDisplacements(x, y, dip, depth, faultType, nu) {
            const mu = 1; // Shear modulus (assumed to be 1 for simplicity)
            const lambda = 2 * mu * nu / (1 - 2 * nu); // Lame's first parameter
            const G = mu; // Shear modulus

            const dipRad = dip * Math.PI / 180;
            const cosDip = Math.cos(dipRad);
            const sinDip = Math.sin(dipRad);

            const r = Math.sqrt(x * x + y * y + depth * depth);
            const r3 = r * r * r;

            let ux = 0, uy = 0, uz = 0;

            if (faultType === 'tensile') {
                const term1 = b * (1 - 2 * nu) / (8 * Math.PI * G * r3);
                ux += term1 * (x * (3 * depth * cosDip - y * sinDip));
                uy += term1 * (y * (3 * depth * cosDip + x * sinDip));
                uz += term1 * (depth * (3 * depth * cosDip - r * sinDip));
            } else if (faultType === 'strike-slip') {
                const term2 = b / (8 * Math.PI * G * r3);
                ux += term2 * (x * (3 * y * sinDip) + y * (r * cosDip - 3 * depth * sinDip));
                uy += term2 * (y * (3 * x * sinDip) - x * (r * cosDip - 3 * depth * sinDip));
                uz += term2 * (depth * (3 * x * y * sinDip / r));
            } else if (faultType === 'normal') {
                const term3 = b * (1 - 2 * nu) / (8 * Math.PI * G * r3);
                ux += term3 * (x * (3 * depth * sinDip - y * cosDip));
                uy += term3 * (y * (3 * depth * sinDip + x * cosDip));
                uz += term3 * (depth * (3 * depth * sinDip + r * cosDip));
            }

            return { ux, uy, uz };
        }

        function updatePlot() {
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const depth = parseFloat(document.getElementById('slider-depth').value);
            const limit = parseFloat(document.getElementById('slider-limit').value);
            const component = document.querySelector('#btn-group-component .active').id.replace('btn-component-', '');
            const faultType = document.querySelector('#btn-group-fault-type .active').id.replace('btn-fault-', '');

            const x_coords = [];
            const y_coords = [];
            const z_values = [];

            for (let i = 0; i <= 50; i++) {
                x_coords.push(-limit + (i * (2 * limit / 50)));
            }

            for (let j = 0; j <= 50; j++) {
                y_coords.push(-limit + (j * (2 * limit / 50)));
            }

            for (let i = 0; i <= 50; i++) {
                z_values[i] = [];
                for (let j = 0; j <= 50; j++) {
                    const { ux, uy, uz } = calculateDisplacements(x_coords[i], y_coords[j], dip, depth, faultType, nu);
                    let displacement = 0;
                    if (component === 'x') {
                        displacement = ux;
                    } else if (component === 'y') {
                        displacement = uy;
                    } else if (component === 'z') {
                        displacement = uz;
                    }
                    z_values[i].push(displacement * 1000); // Convert to mm
                }
            }

            const trace = {
                type: 'surface',
                x: x_coords,
                y: y_coords,
                z: z_values,
                colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            };

            const layout = {
                title: '',
                scene: {
                    xaxis: { title: 'x (m)', range: [-limit, limit] },
                    yaxis: { title: 'y (m)', range: [-limit, limit] },
                    zaxis: { title: 'z (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            Plotly.react('plot', [trace], layout);
        }

        document.addEventListener('DOMContentLoaded', () => {
            document.getElementById('slider-dip').addEventListener('input', (e) => {
                document.getElementById('value-dip').textContent = e.target.value;
                updatePlot();
            });

            document.getElementById('slider-depth').addEventListener('input', (e) => {
                document.getElementById('value-depth').textContent = e.target.value;
                updatePlot();
            });

            document.getElementById('slider-limit').addEventListener('input', (e) => {
                document.getElementById('value-limit').textContent = e.target.value;
                updatePlot();
            });

            document.querySelectorAll('#btn-group-component button').forEach(btn => {
                btn.addEventListener('click', () => {
                    document.querySelector('#btn-group-component .active').classList.remove('active');
                    btn.classList.add('active');
                    updatePlot();
                });
            });

            document.querySelectorAll('#btn-group-fault-type button').forEach(btn => {
                btn.addEventListener('click', () => {
                    document.querySelector('#btn-group-fault-type .active').classList.remove('active');
                    btn.classList.add('active');
                    updatePlot();
                });
            });

            updatePlot(); // Initial plot
        });
    </script>
