<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Vibrating String Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            background-color: #e0e0e0;
        }
        #app-container {
            display: flex;
            width: 80%;
            max-width: 1200px;
            background-color: #fff;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        #control-panel {
            flex: 1;
            padding: 20px;
            border-right: 1px solid #ddd;
        }
        #control-panel div {
            margin-bottom: 20px;
        }
        #control-panel label {
            display: block;
            margin-bottom: 5px;
        }
        #visualization-area {
            flex: 2;
            padding: 20px;
        }
        #formula-display {
            margin-bottom: 20px;
        }
        #canvas-container {
            width: 100%;
            height: 400px;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div>
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div>
                <label for="checkbox-axes">axes</label>
                <input type="checkbox" id="checkbox-axes" checked>
            </div>
        </div>
        <div id="visualization-area">
            <div id="formula-display">\(\omega_2 = 2.76 \sqrt{g/l}\)</div>
            <div id="canvas-container"></div>
        </div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];
        let t = 0.1;
        let k = 2;
        let showAxes = true;

        function setup() {
            const canvas = createCanvas(600, 400);
            canvas.parent('canvas-container');
            document.getElementById('slider-t').addEventListener('input', updateT);
            document.getElementById('slider-k').addEventListener('input', updateK);
            document.getElementById('checkbox-axes').addEventListener('change', updateAxes);
        }

        function draw() {
            background('#f0f0f0');
            const z_k = zeros[k - 1];
            const omega_k = z_k / 2;
            const timeFactor = cos(omega_k * t);

            if (showAxes) {
                stroke(150);
                strokeWeight(1);
                dashedLine(0, height / 2, width, height / 2);
                dashedLine(width / 2, 0, width / 2, height);

                textSize(12);
                textAlign(CENTER, CENTER);
                for (let i = -2; i <= 2; i++) {
                    const x = map(i, -2, 2, 0, width);
                    line(x, height / 2 - 5, x, height / 2 + 5);
                    text(i, x, height / 2 + 20);
                }
                for (let j = -0.2; j >= -1.0; j -= 0.2) {
                    const y = map(j, -1.2, 0, 0, height);
                    line(width / 2 - 5, y, width / 2 + 5, y);
                    text(j.toFixed(1), width / 2 + 10, y);
                }
            }

            stroke(0, 102, 255);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                const bessel_arg = z_k * sqrt(y_norm);
                const x_pos = 0.8 * timeFactor * besselJ0(bessel_arg);
                const y_pos = -y_norm;
                const x = map(x_pos, -2, 2, 0, width);
                const y = map(y_pos, -1.2, 0, 0, height);
                vertex(x, y);
            }
            endShape();
        }

        function updateT(event) {
            t = parseFloat(event.target.value);
            document.getElementById('t-value-display').textContent = t.toFixed(2);
        }

        function updateK(event) {
            k = parseInt(event.target.value);
            document.getElementById('k-value-display').textContent = k;
            const z_k = zeros[k - 1];
            const omega_k = z_k / 2;
            const formula = `\\(\\omega_{${k}} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)`;
            document.getElementById('formula-display').innerHTML = formula;
            MathJax.typesetPromise([document.getElementById('formula-display')]);
        }

        function updateAxes(event) {
            showAxes = event.target.checked;
        }

        function dashedLine(x1, y1, x2, y2) {
            const dashLength = 5;
            const dx = x2 - x1;
            const dy = y2 - y1;
            const numDashes = floor(sqrt(dx * dx + dy * dy) / dashLength);
            for (let i = 0; i < numDashes; i++) {
                const startX = x1 + (dx * i) / numDashes;
                const startY = y1 + (dy * i) / numDashes;
                const endX = startX + dx / numDashes;
                const endY = startY + dy / numDashes;
                line(startX, startY, endX, endY);
                i++;
            }
        }

        function besselJ0(x) {
            const terms = [
                1.0,
                -0.25 * x * x,
                0.020833333333333332 * x * x * x * x,
                -0.0013888888888888888 * x * x * x * x * x * x,
                0.000041666666666666664 * x * x * x * x * x * x * x * x,
                -0.0000007936507936507936 * x * x * x * x * x * x * x * x * x * x
            ];
            return terms.reduce((sum, term) => sum + term, 0);
        }
    </script>
