        function draw() {
            background('#f0f0f0');
            const currentOrder = layeringPermutations[layeringOrderIndex];
            const circles = [
                { id: 1, center: [width * 0.35, height * 0.4], diameter: width * 0.4 },
                { id: 2, center: [width * 0.65, height * 0.4], diameter: width * 0.4 },
