                { id: 4, center: [width * 0.5, height * 0.45], diameter: width * 0.3 }
            ];

            currentOrder.forEach(circleId => {
                const circle = circles.find(c => c.id === circleId);
                const colorInput = document.getElementById(`color-picker${circleId}`);
                const opacitySlider = document.getElementById(`slider-opacity${circleId}`);
                const hexColor = colorInput.value;
                const rgbColor = hexToRgb(hexColor);
                const alpha = opacitySlider.value * 255;

                fill(rgbColor.r, rgbColor.g, rgbColor.b, alpha);
                noStroke();
                ellipse(circle.center[0], circle.center[1], circle.diameter, circle.diameter);
            });

            circles.forEach(circle => {
                fill(0);
                textSize(32);
                textAlign(CENTER, CENTER);
                text(circle.id, circle.center[0], circle.center[1]);
            });
        }

        function hexToRgb(hex) {
            const bigint = parseInt(hex.slice(1), 16);
            return {
                r: (bigint >> 16) & 255,
                g: (bigint >> 8) & 255,
                b: bigint & 255
            };
        }

        function updateLayeringDisplay() {
            const displayElement = document.getElementById('layering-display');
            displayElement.textContent = layeringPermutations[layeringOrderIndex].join(', ');
        }

        function resetControls() {
            document.getElementById('slider-opacity1').value = 0.7;
            document.getElementById('slider-opacity2').value = 0.7;
            document.getElementById('slider-opacity3').value = 0.7;
            document.getElementById('slider-opacity4').value = 0.7;
            document.getElementById('color-picker1').value = '#00ff00';
            document.getElementById('color-picker2').value = '#0000ff';
            document.getElementById('color-picker3').value = '#ff0000';
            document.getElementById('color-picker4').value = '#000000';
            layeringOrderIndex = 0;
            updateLayeringDisplay();
            redraw();
        }

        document.addEventListener('DOMContentLoaded', () => {
            const opacitySliders = document.querySelectorAll('input[type="range"]');
            opacitySliders.forEach(slider => {
                slider.addEventListener('input', redraw);
            });

            const colorPickers = document.querySelectorAll('input[type="color"]');
            colorPickers.forEach(picker => {
                picker.addEventListener('input', redraw);
            });

            document.getElementById('btn-layer-prev').addEventListener('click', () => {
                layeringOrderIndex = (layeringOrderIndex - 1 + layeringPermutations.length) % layeringPermutations.length;
                updateLayeringDisplay();
                redraw();
            });

            document.getElementById('btn-layer-next').addEventListener('click', () => {
                layeringOrderIndex = (layeringOrderIndex + 1) % layeringPermutations.length;
                updateLayeringDisplay();
                redraw();
            });

            document.getElementById('btn-reset').addEventListener('click', resetControls);

            setup();
        });
    </script>
</body>
</html>
