</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="button-group">
                <button id="btn-srk" class="selected">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div>
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div class="slider-container">
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const Tc = 365.57; // Critical Temperature in K
        const Pc = 46.65; // Critical Pressure in bar
        const ω = 0.144; // Acentric Factor
        const R = 0.0831446; // Gas Constant in L·bar/(mol·K)
        const T_range = Array.from({ length: 201 }, (_, i) => 300 + i); // Temperatures from 300 to 500 K

        const aspenData = {
            x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
            y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946]
        };

        let eosType = 'srk';
        let pressure = 10;
        let showAspen = true;

        document.getElementById('btn-srk').addEventListener('click', () => {
            eosType = 'srk';
            updateButtons();
            updatePlot();
        });

        document.getElementById('btn-pr').addEventListener('click', () => {
            eosType = 'pr';
            updateButtons();
            updatePlot();
        });

        document.getElementById('checkbox-aspen').addEventListener('change', () => {
            showAspen = document.getElementById('checkbox-aspen').checked;
            updatePlot();
        });

        document.getElementById('slider-pressure').addEventListener('input', (event) => {
            pressure = parseInt(event.target.value);
            document.getElementById('display-pressure').textContent = pressure;
            updatePlot();
        });

        function updateButtons() {
            document.getElementById('btn-srk').classList.toggle('selected', eosType === 'srk');
            document.getElementById('btn-pr').classList.toggle('selected', eosType === 'pr');
        }

        function calculateFugacityCoefficients(eosType, pressure) {
            const fugacityCoefficients = [];
            const a = eosType === 'srk' ? 0.42748 * R * R * Tc * Tc / Pc : 0.45724 * R * R * Tc * Tc / Pc;
            const b = eosType === 'srk' ? 0.08664 * R * Tc / Pc : 0.07780 * R * Tc / Pc;
            const m = eosType === 'srk' ? 0.480 + 1.574 * ω - 0.176 * ω * ω : 0.37464 + 1.54226 * ω - 0.26992 * ω * ω;

            T_range.forEach(T => {
                const Tr = T / Tc;
                const α = (1 + m * (1 - Math.sqrt(Tr))) ** 2;
                const a_eff = a * α;
                const A = a_eff * pressure / (R * T) ** 2;
                const B = b * pressure / (R * T);

                const coefficients = eosType === 'srk' 
                    ? [1, -1, A - B - B ** 2, -A * B]
                    : [1, -(1 - B), A - 3 * B ** 2 - 2 * B, -(A / (2 * Math.sqrt(2) * B)) * Math.log((1 + Math.sqrt(2)) / (1 - Math.sqrt(2))) - A * B + B ** 2 + B ** 3];

                const roots = solveCubic(coefficients);
                const Z = Math.max(...roots.filter(root => root > B));
                const lnPhi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
                const phi = Math.exp(lnPhi);
                fugacityCoefficients.push(phi);
            });

            return fugacityCoefficients;
        }

        function solveCubic(coeffs) {
            const [a, b, c, d] = coeffs;
            const delta0 = b ** 2 - 3 * a * c;
            const delta1 = 2 * b ** 3 - 9 * a * b * c + 27 * a ** 2 * d;
            const C = Math.cbrt((delta1 + Math.sqrt(delta1 ** 2 - 4 * delta0 ** 3)) / 2);
            const u = [1, (-1 + Math.sqrt(3) * 1j) / 2, (-1 - Math.sqrt(3) * 1j) / 2];
            const v = [1, (-1 - Math.sqrt(3) * 1j) / 2, (-1 + Math.sqrt(3) * 1j) / 2];
            const roots = [];

            for (let k = 0; k < 3; k++) {
                const root = -1 / (3 * a) * (b + C * u[k] + delta0 / (C * v[k]));
                roots.push(root.real);
            }

            return roots;
        }

        function updatePlot() {
            const fugacityCoefficients = calculateFugacityCoefficients(eosType, pressure);
            const traceEos = {
                x: T_range,
                y: fugacityCoefficients,
                mode: 'lines',
                type: 'scatter',
                line: { color: eosType === 'srk' ? 'red' : 'blue', width: 2 }
            };

            const traceAspen = {
                x: aspenData.x,
                y: aspenData.y,
                mode: 'markers',
                type: 'scatter',
                marker: { color: 'green', symbol: 'square', size: 8 },
                visible: showAspen && pressure === 10
            };

            const layout = {
                title: 'Gas-Phase Fugacity Coefficient vs Temperature',
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505],
                    showgrid: true,
                    zeroline: true,
                    gridcolor: 'lightgrey',
                    zerolinecolor: 'white'
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02],
                    showgrid: true,
                    zeroline: true,
                    gridcolor: 'lightgrey',
                    zerolinecolor: 'white'
                },
                plot_bgcolor: 'lightgrey',
                paper_bgcolor: 'lightgrey'
            };

            Plotly.newPlot('plot-container', [traceEos, traceAspen], layout);
        }

        updatePlot();
    </script>
</body>
</html>
