        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let currentStep = 1;
        let pX, pY;
        let triangleVertices;
        let mainAltitude;

        function setup() {
            createCanvas(600, 520);
            triangleVertices = [
                { x: 100, y: 400 }, // A
                { x: 500, y: 400 }, // B
                { x: 300, y: 100 }  // C
            ];
            mainAltitude = (triangleVertices[1].x - triangleVertices[0].x) * Math.sqrt(3) / 2;
            pX = width * 0.4;
            pY = height * 0.6;
            document.querySelectorAll('input[name="step-selector"]').forEach(radio => {
                radio.addEventListener('change', () => {
                    currentStep = parseInt(radio.value);
                    redraw();
                });
            });
        }

        function draw() {
            background(255);
            drawMainTriangle();
            drawMainAltitude();
            if (currentStep === 1) {
                drawPerpendiculars();
            } else if (currentStep === 2) {
                drawSmallTriangles();
            } else if (currentStep === 3) {
                drawStackedTriangles();
            }
            drawPointP();
