        function drawMainTriangle() {
            noFill();
            stroke(0);
            beginShape();
            triangleVertices.forEach(v => vertex(v.x, v.y));
            vertex(triangleVertices[0].x, triangleVertices[0].y);
            endShape();
            fill(0);
            textSize(16);
            text('A', triangleVertices[0].x - 10, triangleVertices[0].y + 20);
            text('B', triangleVertices[1].x + 10, triangleVertices[1].y + 20);
            text('C', triangleVertices[2].x - 10, triangleVertices[2].y - 10);
        }

        function drawMainAltitude() {
            stroke(173, 216, 230);
            strokeWeight(1);
            line(
                (triangleVertices[0].x + triangleVertices[1].x) / 2,
                (triangleVertices[0].y + triangleVertices[1].y) / 2,
                triangleVertices[2].x,
                triangleVertices[2].y
            );
        }

        function drawPerpendiculars() {
            let feet = getFeetOfPerpendiculars(pX, pY);
            stroke(0);
            strokeWeight(2);
            feet.forEach(f => line(pX, pY, f.x, f.y));
            fill(0, 0, 255);
            feet.forEach(f => ellipse(f.x, f.y, 5, 5));
        }

        function drawSmallTriangles() {
            let feet = getFeetOfPerpendiculars(pX, pY);
            let heights = feet.map((f, i) => dist(pX, pY, f.x, f.y));
            let sides = heights.map(h => h * 2 / Math.sqrt(3));

            fill(255, 0, 0, 128);
            drawEquilateralTriangle(pX, pY, sides[0], 0);

            fill(0, 255, 0, 128);
            let angleAC = Math.atan2(triangleVertices[2].y - triangleVertices[0].y, triangleVertices[2].x - triangleVertices[0].x);
            drawEquilateralTriangle(pX, pY, sides[2], angleAC);
            text('x', pX + sides[2] * Math.cos(angleAC + PI / 6), pY + sides[2] * Math.sin(angleAC + PI / 6));

            fill(0, 0, 255, 128);
            let angleBC = Math.atan2(triangleVertices[2].y - triangleVertices[1].y, triangleVertices[2].x - triangleVertices[1].x);
            drawEquilateralTriangle(pX, pY, sides[1], angleBC);
            text('y', pX + sides[1] * Math.cos(angleBC - PI / 6), pY + sides[1] * Math.sin(angleBC - PI / 6));
        }

        function drawStackedTriangles() {
            let feet = getFeetOfPerpendiculars(pX, pY);
            let heights = feet.map((f, i) => dist(pX, pY, f.x, f.y));
            let sides = heights.map(h => h * 2 / Math.sqrt(3));

            fill(255, 0, 0, 128);
            drawEquilateralTriangle((triangleVertices[0].x + triangleVertices[1].x) / 2 - sides[0] / 2, (triangleVertices[0].y + triangleVertices[1].y) / 2, sides[0], 0);

            fill(0, 0, 255, 128);
            drawEquilateralTriangle((triangleVertices[0].x + triangleVertices[1].x) / 2 - sides[1] / 2, (triangleVertices[0].y + triangleVertices[1].y) / 2 - sides[0] * Math.sqrt(3) / 2, sides[1], 0);
            text('y', (triangleVertices[0].x + triangleVertices[1].x) / 2 - sides[1] / 2 + sides[1] * Math.cos(-PI / 6), (triangleVertices[0].y + triangleVertices[1].y) / 2 - sides[0] * Math.sqrt(3) / 2 + sides[1] * Math.sin(-PI / 6));

            fill(0, 255, 0, 128);
            drawEquilateralTriangle((triangleVertices[0].x + triangleVertices[1].x) / 2 - sides[2] / 2, (triangleVertices[0].y + triangleVertices[1].y) / 2 - sides[0] * Math.sqrt(3) / 2 - sides[1] * Math.sqrt(3) / 2, sides[2], 0);

            fill(192, 192, 192, 128);
            drawPointSymbol(pX, pY);
        }

        function drawPointP() {
            fill(192, 192, 192, 128);
            stroke(0);
            strokeWeight(1);
            ellipse(pX, pY, 10, 10);
            line(pX - 5, pY, pX + 5, pY);
            line(pX, pY - 5, pX, pY + 5);
            fill(255, 0, 0);
            textSize(16);
            text('P', pX + 10, pY + 5);
        }

        function drawPointSymbol(x, y) {
            ellipse(x, y, 10, 10);
            line(x - 5, y, x + 5, y);
            line(x, y - 5, x, y + 5);
        }

        function drawEquilateralTriangle(x, y, side, angle) {
            beginShape();
            for (let i = 0; i < 3; i++) {
                vertex(
                    x + side * Math.cos(angle + i * 2 * PI / 3),
                    y + side * Math.sin(angle + i * 2 * PI / 3)
                );
            }
            endShape(CLOSE);
        }

        function getFeetOfPerpendiculars(px, py) {
            let feet = [];
            for (let i = 0; i < 3; i++) {
                let v1 = triangleVertices[i];
                let v2 = triangleVertices[(i + 1) % 3];
                let dx = v2.x - v1.x;
                let dy = v2.y - v1.y;
                let d = dx * dx + dy * dy;
                let t = ((px - v1.x) * dx + (py - v1.y) * dy) / d;
                t = constrain(t, 0, 1);
                let foot = {
                    x: v1.x + t * dx,
                    y: v1.y + t * dy
                };
                feet.push(foot);
            }
            return feet;
        }

        function mousePressed() {
            let d = dist(mouseX, mouseY, pX, pY);
            if (d < 5) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                let [x, y] = constrainPointToTriangle(mouseX, mouseY);
                pX = x;
                pY = y;
                redraw();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function constrainPointToTriangle(px, py) {
            let closestDist = Infinity;
            let closestPoint = { x: px, y: py };

            for (let i = 0; i < 3; i++) {
                let v1 = triangleVertices[i];
                let v2 = triangleVertices[(i + 1) % 3];
                let [x, y] = projectPointOnSegment(px, py, v1, v2);
                let d = dist(px, py, x, y);
                if (d < closestDist) {
                    closestDist = d;
                    closestPoint = { x, y };
                }
            }

            return [closestPoint.x, closestPoint.y];
        }

        function projectPointOnSegment(px, py, v1, v2) {
            let dx = v2.x - v1.x;
            let dy = v2.y - v1.y;
            let t = ((px - v1.x) * dx + (py - v1.y) * dy) / (dx * dx + dy * dy);
            t = constrain(t, 0, 1);
            return [v1.x + t * dx, v1.y + t * dy];
        }
    </script>
</body>
