    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        const orbitalElements = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M: 174.796 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M: 50.416 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M: 358.617 },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M: 19.412 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M: 77.372 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M: 14.882 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M: 204.03 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M: 95.895 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M: 206.51 }
        };

        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf4f4f4);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 15, 30);

        const renderer = new THREE.WebGLRenderer({ canvas: document.getElementById('3d-canvas') });
        renderer.setSize(window.innerWidth, window.innerHeight);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const pointLight = new THREE.PointLight(0xffffff, 1, 0);
        pointLight.position.set(0, 0, 0);
        scene.add(pointLight);

        let selectedDwarfPlanet = 'Pluto';
        let isAnimating = false;
        let animationFrameId;

        const bodies = {};
        const orbits = {};

        function createBody(name, radius, color, position) {
            const geometry = new THREE.SphereGeometry(radius, 32, 32);
            const material = new THREE.MeshLambertMaterial({ color: color });
            const mesh = new THREE.Mesh(geometry, material);
            mesh.position.copy(position);
            scene.add(mesh);
            bodies[name] = mesh;
        }

        function createOrbit(name, color, curve) {
            const points = curve.getPoints(200);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color });
            const line = new THREE.Line(geometry, material);
            scene.add(line);
            orbits[name] = line;
        }

        function calculatePosition(date, elements) {
            const j2000 = new Date('2000-01-01T12:00:00Z');
            const d = (date - j2000) / (24 * 60 * 60 * 1000);
            const P = 365.25 * Math.pow(elements.a, 3 / 2);
            let M = (elements.M + 360 / P * d) % 360;
            M = M * Math.PI / 180;

            let E = M;
            for (let i = 0; i < 10; i++) {
                E = M + elements.e * Math.sin(E);
            }

            const ν = 2 * Math.atan2(Math.sqrt(1 + elements.e) * Math.sin(E / 2), Math.sqrt(1 - elements.e) * Math.cos(E / 2));
            const r = elements.a * (1 - elements.e * Math.cos(E));

            const x_orb = r * Math.cos(ν);
            const y_orb = r * Math.sin(ν);

            const Ω = elements.Ω * Math.PI / 180;
            const ω = elements.ω * Math.PI / 180;
            const i = elements.i * Math.PI / 180;

            const x = x_orb * (Math.cos(Ω) * Math.cos(ω) - Math.sin(Ω) * Math.sin(ω) * Math.cos(i)) - y_orb * (Math.sin(Ω) * Math.cos(ω) + Math.cos(Ω) * Math.sin(ω) * Math.cos(i));
            const y = x_orb * (Math.cos(Ω) * Math.sin(ω) + Math.sin(Ω) * Math.cos(ω) * Math.cos(i)) + y_orb * (Math.cos(Ω) * Math.cos(ω) - Math.sin(Ω) * Math.sin(ω) * Math.cos(i));
            const z = x_orb * (Math.sin(Ω) * Math.sin(i)) + y_orb * (Math.cos(Ω) * Math.sin(i));

            return new THREE.Vector3(x, y, z);
        }

        function updateScene() {
            const sliderValue = parseInt(document.getElementById('time-slider').value);
            const date = new Date('2000-01-01T00:00:00Z');
            date.setDate(date.getDate() + sliderValue);
            document.getElementById('date-display').textContent = date.toUTCString().replace(/(\d{3}) (\d{3})/, '$1 $2').replace(/(\d{4})(.*)/, '$1 00:00:00');

            Object.keys(orbitalElements).forEach(body => {
                const position = calculatePosition(date, orbitalElements[body]);
                if (bodies[body]) {
                    bodies[body].position.copy(position);
                }
                if (orbits[body]) {
                    orbits[body].geometry.dispose();
                    const curve = new THREE.EllipseCurve(
                        0, 0,
                        orbitalElements[body].a, orbitalElements[body].a * Math.sqrt(1 - Math.pow(orbitalElements[body].e, 2)),
                        0, 2 * Math.PI,
                        false,
                        0
                    );
                    curve.aX = orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) - orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
