                    curve.bX = -orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) - orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
                    curve.bY = -orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) + orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
                    const points = curve.getPoints(200);
                    const geometry = new THREE.BufferGeometry().setFromPoints(points);
                    orbits[body].geometry = geometry;
                }
            });

            renderer.render(scene, camera);
        }

        function animate() {
            if (isAnimating) {
                const slider = document.getElementById('time-slider');
                let value = parseInt(slider.value) + 2;
                if (value > parseInt(slider.max)) value = parseInt(slider.min);
                slider.value = value;
                updateScene();
                animationFrameId = requestAnimationFrame(animate);
            }
        }

        document.getElementById('time-slider').addEventListener('input', updateScene);
        document.getElementById('play-pause-button').addEventListener('click', () => {
            isAnimating = !isAnimating;
