        createBody('Mars', 0.1, 0xff0000, calculatePosition(new Date('2008-01-01'), orbitalElements.Mars));
        createBody('Ceres', 0.1, 0x800080, calculatePosition(new Date('2008-01-01'), orbitalElements.Ceres));
        createBody('Pluto', 0.1, 0x800080, calculatePosition(new Date('2008-01-01'), orbitalElements.Pluto));
