        createBody('Makemake', 0.1, 0x800080, calculatePosition(new Date('2008-01-01'), orbitalElements.Makemake));
        createBody('Eris', 0.1, 0x800080, calculatePosition(new Date('2008-01-01'), orbitalElements.Eris));

        Object.keys(orbitalElements).forEach(body => {
            const curve = new THREE.EllipseCurve(
                0, 0,
                orbitalElements[body].a, orbitalElements[body].a * Math.sqrt(1 - Math.pow(orbitalElements[body].e, 2)),
                0, 2 * Math.PI,
                false,
                0
            );
            curve.aX = orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) - orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
            curve.aY = orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) + orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
            curve.bX = -orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) - orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
            curve.bY = -orbitalElements[body].a * Math.sin(orbitalElements[body].ω * Math.PI / 180) * Math.sin(orbitalElements[body].Ω * Math.PI / 180) + orbitalElements[body].a * Math.cos(orbitalElements[body].ω * Math.PI / 180) * Math.cos(orbitalElements[body].Ω * Math.PI / 180) * Math.cos(orbitalElements[body].i * Math.PI / 180);
            createOrbit(body, body === selectedDwarfPlanet ? 0xff0000 : 0x0000ff, curve);
        });

        updateScene();

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });
    </script>
</body>
