        'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
    };

    const selectCfc = document.getElementById('select-cfc');
    const checkboxShowAll = document.getElementById('checkbox-show-all');
    const sliderYear = document.getElementById('slider-year');
    const labelYear = document.getElementById('label-year');
    const sliderTime = document.getElementById('slider-time');
    const labelTime = document.getElementById('label-time');

    let currentCfc = 'CFC-12';
    let showAll = false;
    let currentYear = parseInt(sliderYear.value);
    let currentTime = parseInt(sliderTime.value);

    const plotOzoneContainer = document.getElementById('plot-ozone');
    const plotCfcContainer = document.getElementById('plot-cfc');
    const sceneContainer = document.getElementById('scene-container');

    let plotOzoneData = [];
    let plotCfcData = [];

    const camera = new THREE.PerspectiveCamera(75, sceneContainer.clientWidth / sceneContainer.clientHeight, 0.1, 1000);
    const scene = new THREE.Scene();
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
    sceneContainer.appendChild(renderer.domElement);

    const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
    const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
    const earth = new THREE.Mesh(earthGeometry, earthMaterial);
    scene.add(earth);

    const ozoneLayerGeometry = new THREE.SphereGeometry(1.02, 32, 32);
    const ozoneLayerMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true, opacity: 0.5 });
    const ozoneLayer = new THREE.Mesh(ozoneLayerGeometry, ozoneLayerMaterial);
    scene.add(ozoneLayer);

    const atmosphereGeometry = new THREE.SphereGeometry(1.05, 32, 32);
    const atmosphereMaterial = new THREE.MeshBasicMaterial({ color: 0xffaa88, transparent: true, opacity: 0.1 });
    const atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
    scene.add(atmosphere);

    const sunGeometry = new THREE.SphereGeometry(0.1, 32, 32);
    const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
    const sun = new THREE.Mesh(sunGeometry, sunMaterial);
    sun.position.x = -5;
    scene.add(sun);

    const arrowLength = 2;
    const arrowColor = 0xffa500;
    const arrowOrigin = new THREE.Vector3(-5, 0, 0);
    const arrowDirection = new THREE.Vector3(1, 0, 0);
    const arrowHelper1 = new THREE.ArrowHelper(arrowDirection.clone().normalize(), arrowOrigin, arrowLength, arrowColor);
    const arrowHelper2 = new THREE.ArrowHelper(arrowDirection.clone().normalize().applyAxisAngle(new THREE.Vector3(0, 1, 0), Math.PI / 4), arrowOrigin, arrowLength, arrowColor);
    const arrowHelper3 = new THREE.ArrowHelper(arrowDirection.clone().normalize().applyAxisAngle(new THREE.Vector3(0, 1, 0), -Math.PI / 4), arrowOrigin, arrowLength, arrowColor);
    scene.add(arrowHelper1, arrowHelper2, arrowHelper3);

    camera.position.z = 5;

    function update() {
        currentCfc = selectCfc.value;
        showAll = checkboxShowAll.checked;
        currentYear = parseInt(sliderYear.value);
        currentTime = parseInt(sliderTime.value);

        labelYear.textContent = currentYear;
        labelTime.textContent = currentTime;

        const cfcConcentrations = Object.values(cfcData).map(item => item.data);
        const selectedCfcData = cfcData[currentCfc].data;

        const selectedCfcConcentration = selectedCfcData.find(([year]) => year === currentYear)[1];
        const ozoneSlope = 0.01 * selectedCfcConcentration; // Pseudo-first-order rate constant

        plotCfcData = cfcConcentrations.map((data, index) => ({
            x: data.map(point => point[0]),
            y: data.map(point => point[1]),
            mode: 'lines',
            name: Object.keys(cfcData)[index],
            line: { color: Object.values(cfcData)[index].color }
        }));

        if (!showAll) {
            plotCfcData = plotCfcData.filter(trace => trace.name === currentCfc);
        }

        const markerIndex = selectedCfcData.findIndex(([year]) => year === currentYear);
        plotCfcData.forEach(trace => {
            trace.marker = { color: 'red' };
            trace.selectedpoints = markerIndex;
        });

        plotOzoneData = [{
            x: Array.from({ length: currentTime + 1 }, (_, i) => i),
            y: Array.from({ length: currentTime + 1 }, (_, i) => 1 / (1 + ozoneSlope * selectedCfcConcentration * i)),
            mode: 'lines',
            line: { color: 'blue' }
        }];

        Plotly.newPlot(plotOzoneContainer, plotOzoneData, { title: 'concentration of ozone', xaxis: { title: 'time (s)', range: [0, 30] }, yaxis: { title: '1/<math><mi>O</mi><sub>3</sub></math>', range: [0, 110] } });
        Plotly.newPlot(plotCfcContainer, plotCfcData, { title: 'CFC concentration', xaxis: { title: 'year', range: [1920, 2020] }, yaxis: { range: [0, 600] } });

        ozoneLayerMaterial.opacity = 1 - (selectedCfcConcentration / 600);
    }

    selectCfc.addEventListener('change', update);
    checkboxShowAll.addEventListener('change', update);
    sliderYear.addEventListener('input', update);
    sliderTime.addEventListener('input', update);

    window.addEventListener('resize', () => {
        camera.aspect = sceneContainer.clientWidth / sceneContainer.clientHeight;
        camera.updateProjectionMatrix();
        renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
    });

    function animate() {
        requestAnimationFrame(animate);
        renderer.render(scene, camera);
    }

    update();
    animate();
</script>

</body>
</html>
