<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Eratosthenes's Measure of the Earth's Circumference</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #visualization-container {
            width: 600px;
            height: 450px;
            margin: 0 auto;
        }
        canvas {
            display: block;
            background-color: #F0F0F0;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p>This demonstration shows how Eratosthenes measured the Earth's circumference using the angle of shadows cast by posts in Syene and Alexandria.</p>
    <div id="control-panel">
        <label for="slider-post-angle">post angle</label>
        <input type="range" id="slider-post-angle" min="1" max="60" step="0.1" value="7">
        <span id="display-post-angle">7</span>
        <label for="checkbox-zoom">zoom</label>
        <input type="checkbox" id="checkbox-zoom">
    </div>
    <div id="visualization-container">
        <canvas id="demo-canvas"></canvas>
    </div>

    <script>
        let angle = 7;
        let isZoomed = false;

        function setup() {
            createCanvas(600, 450);
            angleMode(DEGREES);
            document.getElementById('slider-post-angle').addEventListener('input', updateAngle);
            document.getElementById('checkbox-zoom').addEventListener('change', toggleZoom);
        }

        function draw() {
            background(240);
            drawSunRays();
            if (isZoomed) {
                drawZoomedView();
            } else {
                drawFullView();
            }
        }

        function drawSunRays() {
            stroke('#DAA520');
            strokeWeight(2);
            for (let y = 50; y < height - 50; y += 20) {
                line(width - 50, y, width - 200, y);
                drawArrowhead(width - 200, y, 10, 5, LEFT);
            }
        }

        function drawArrowhead(x, y, size, headSize, direction) {
            push();
            translate(x, y);
            rotate(direction === LEFT ? PI : 0);
            beginShape();
            vertex(0, 0);
            vertex(-size, headSize);
            vertex(-size, -headSize);
            endShape(CLOSE);
            pop();
        }

        function drawFullView() {
            const earthRadius = 150;
            const centerX = 150;
            const centerY = 225;

            // Draw Earth
            fill('#468296');
            noStroke();
            ellipse(centerX, centerY, earthRadius * 2, earthRadius * 2);
            fill(255);
            textAlign(CENTER, CENTER);
            textSize(20);
            text('Earth', centerX, centerY);

            // Draw Syene
            stroke(0);
            strokeWeight(1);
            line(centerX, centerY, centerX + earthRadius, centerY);
            fill(0);
            textAlign(LEFT, CENTER);
            textSize(16);
            text('Syene', centerX + earthRadius + 5, centerY);

            // Draw Alexandria
            const alexX = centerX + earthRadius * cos(angle);
            const alexY = centerY - earthRadius * sin(angle);
            line(centerX, centerY, alexX, alexY);
            line(alexX, alexY, alexX, centerY);
            text('Alexandria', alexX + 5, centerY);

            // Draw Angle Arc and Label
            noFill();
            stroke(0);
            arc(centerX, centerY, 100, 100, 0, angle);
            fill(0);
            textAlign(CENTER, CENTER);
            textSize(16);
            text(`${angle.toFixed(1)}°`, centerX + 50 * cos(angle / 2), centerY - 50 * sin(angle / 2));

            // Draw Arrowhead
            drawArrowhead(alexX, alexY, 5, 3, RIGHT);
        }

        function drawZoomedView() {
            const sectorRadius = 200;
            const centerX = 100;
            const centerY = 350;

            // Draw Earth Sector
            fill('#468296');
            noStroke();
            arc(centerX, centerY, sectorRadius * 2, sectorRadius * 2, 0, angle);
            stroke(0);
            strokeWeight(1);
            line(centerX, centerY, centerX + sectorRadius, centerY);
            line(centerX, centerY, centerX + sectorRadius * cos(angle), centerY - sectorRadius * sin(angle));

            // Draw Labels
            fill(0);
            textAlign(RIGHT, CENTER);
            textSize(16);
            text('Syene', centerX + sectorRadius + 5, centerY);
            textAlign(LEFT, CENTER);
            text('Alexandria', centerX + sectorRadius * cos(angle) + 5, centerY - sectorRadius * sin(angle));

            // Draw Angle Arc and Label
            noFill();
            stroke(0);
            arc(centerX, centerY, 150, 150, 0, angle);
            fill(0);
            textAlign(CENTER, CENTER);
            textSize(16);
            text(`${angle.toFixed(1)}°`, centerX + 75 * cos(angle / 2), centerY - 75 * sin(angle / 2));

            // Draw Post and Shadow
            const postHeight = 20;
            const shadowLength = postHeight / tan(radians(angle));
            fill('#D2B48C');
            quad(
                centerX + sectorRadius * cos(angle), centerY - sectorRadius * sin(angle),
                centerX + sectorRadius * cos(angle) + postHeight * sin(radians(angle)), centerY - sectorRadius * sin(angle) + postHeight * cos(radians(angle)),
                centerX + sectorRadius * cos(angle) + postHeight * sin(radians(angle)) + shadowLength, centerY - sectorRadius * sin(angle) + postHeight * cos(radians(angle)),
                centerX + sectorRadius * cos(angle) + shadowLength, centerY - sectorRadius * sin(angle)
            );
        }

        function updateAngle(event) {
            angle = parseFloat(event.target.value);
            document.getElementById('display-post-angle').textContent = angle.toFixed(1);
        }

        function toggleZoom(event) {
            isZoomed = event.target.checked;
        }
    </script>
</body>
</html>
