    <div id="app-container">
        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div class="control-group">
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
            </div>
            <div class="control-group">
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
            </div>
            <div class="control-group">
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
            </div>
            <div class="control-group">
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
            </div>
        </div>
        <div id="viz-container"></div>
    </div>

    <script>
        let moonPosSlider, moonPullSlider, sunPosSlider, sunPullSlider;
        const baseWaterRadius = 200;
        const scalingFactor = 5;

        function setup() {
            createCanvas(500, 500).parent('viz-container');
            noLoop();

            moonPosSlider = select('#slider-moon-pos');
            moonPullSlider = select('#slider-moon-pull');
            sunPosSlider = select('#slider-sun-pos');
            sunPullSlider = select('#slider-sun-pull');

            moonPosSlider.input(redraw);
            moonPullSlider.input(redraw);
            sunPosSlider.input(redraw);
            sunPullSlider.input(redraw);

            select('#btn-reset').mousePressed(resetSliders);
        }

        function draw() {
            background(255);
            let moonAngle = moonPosSlider.value();
            let moonPull = moonPullSlider.value();
            let sunAngle = sunPosSlider.value();
            let sunPull = sunPullSlider.value();

            beginShape();
            fill('#aed6f1');
            stroke('#000000');
            strokeWeight(1);

            for (let theta = 0; theta <= 360; theta++) {
                let thetaRad = radians(theta);
                let moonEffect = moonPull * pow(cos(thetaRad - radians(moonAngle)), 2);
                let sunEffect = sunPull * pow(cos(thetaRad - radians(sunAngle)), 2);
                let totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);

                let x = width / 2 + totalRadius * cos(thetaRad);
                let y = height / 2 + totalRadius * sin(thetaRad);
                vertex(x, y);
            }

            endShape(CLOSE);

            noStroke();
            fill('#966F33');
            ellipse(width / 2, height / 2, 360, 360);
        }

        function resetSliders() {
            moonPosSlider.value(144);
            moonPullSlider.value(40);
            sunPosSlider.value(144);
            sunPullSlider.value(20);
            redraw();
        }
    </script>
</body>
</html>
