        }
        #telescopic-view-wrapper p, #orbit-view-wrapper p {
            text-align: center;
            margin-top: 5px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-earth-position">Earth position</label>
                <input type="range" id="slider-earth-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-comet-position">Comet position</label>
                <input type="range" id="slider-comet-position" min="0" max="360" step="1" value="180">
            </div>
            <div class="control-group">
                <label for="slider-coma">Coma</label>
                <input type="range" id="slider-coma" min="0" max="100" step="1" value="20">
            </div>
            <div class="control-group">
                <label for="slider-tail">Tail</label>
                <input type="range" id="slider-tail" min="0" max="100" step="1" value="50">
            </div>
            <div class="control-group">
                <label for="slider-tail-spread">Tail spread</label>
                <input type="range" id="slider-tail-spread" min="0" max="100" step="1" value="20">
            </div>
        </div>
        <div id="visualization-panel">
            <div id="telescopic-view-wrapper">
                <canvas id="telescopic-canvas"></canvas>
                <p>telescopic view</p>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagram</p>
            </div>
        </div>
    </div>

    <!-- Three.js CDN -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>

    <script>
        // Initialize Three.js scenes, cameras, and renderers
        const sceneOrbit = new THREE.Scene();
        const sceneTelescope = new THREE.Scene();

        const cameraOrbit = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        const cameraTelescope = new THREE.PerspectiveCamera(15, window.innerWidth / window.innerHeight, 0.1, 1000);

        const rendererOrbit = new THREE.WebGLRenderer({ canvas: document.getElementById('orbit-canvas'), antialias: true });
        const rendererTelescope = new THREE.WebGLRenderer({ canvas: document.getElementById('telescopic-canvas'), antialias: true });

        rendererOrbit.setSize(window.innerWidth / 2, window.innerHeight);
        rendererTelescope.setSize(window.innerWidth / 2, window.innerHeight);

        // Orbit Scene Setup
        sceneOrbit.background = new THREE.Color(0xffffff);

        cameraOrbit.position.set(0, 20, 20);
        cameraOrbit.lookAt(0, 0, 0);

        const ambientLightOrbit = new THREE.AmbientLight(0x404040);
        const pointLightOrbit = new THREE.PointLight(0xffffcc, 1, 100);
        pointLightOrbit.position.set(0, 0, 0);

        sceneOrbit.add(ambientLightOrbit);
        sceneOrbit.add(pointLightOrbit);

        // Telescopic Scene Setup
        sceneTelescope.background = new THREE.Color(0x000000);

        cameraTelescope.position.set(0, 0, 0);
        cameraTelescope.lookAt(0, 0, 0);

        const ambientLightTelescope = new THREE.AmbientLight(0x404040);

        sceneTelescope.add(ambientLightTelescope);

        // Create Sun
        const sunGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        sceneOrbit.add(sun);

        // Create Earth
        const earthGeometry = new THREE.SphereGeometry(0.2, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        sceneOrbit.add(earth);

        // Create Comet
        const cometNucleusGeometry = new THREE.SphereGeometry(0.1, 32, 32);
        const cometNucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const cometNucleus = new THREE.Mesh(cometNucleusGeometry, cometNucleusMaterial);

        const cometComaGeometry = new THREE.SphereGeometry(0.5, 32, 32);
        const cometComaMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, transparent: true, opacity: 0.5 });
        const cometComa = new THREE.Mesh(cometComaGeometry, cometComaMaterial);

        const cometTailGeometry = new THREE.ConeGeometry(0.1, 1, 32);
        const cometTailMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, transparent: true, opacity: 0.4 });
        const cometTail = new THREE.Mesh(cometTailGeometry, cometTailMaterial);

        const comet = new THREE.Group();
        comet.add(cometNucleus);
        comet.add(cometComa);
        comet.add(cometTail);

        sceneOrbit.add(comet);
        sceneTelescope.add(comet);

        // Create Orbits
        function createOrbit(a, e, color) {
            const curve = new THREE.EllipseCurve(
                0, 0,             // ax, aY
                a, a,             // xRadius, yRadius
                0, 2 * Math.PI,   // aStartAngle, aEndAngle
                false,            // aClockwise
                0                 // aRotation
            );

            const points = curve.getPoints(50);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color });
            const line = new THREE.Line(geometry, material);
            return line;
        }

        const earthOrbit = createOrbit(4, 0.0167, 0x0000ff);
        const marsOrbit = createOrbit(6.1, 0.0934, 0xff0000);
        const jupiterOrbit = createOrbit(20.8, 0.0489, 0xffa500);
        const cometOrbit = createOrbit(14.4, 0.432, 0xffff00);

        sceneOrbit.add(earthOrbit);
        sceneOrbit.add(marsOrbit);
        sceneOrbit.add(jupiterOrbit);
        sceneOrbit.add(cometOrbit);

        // Update Function
        function update() {
            const earthPosition = parseFloat(document.getElementById('slider-earth-position').value);
            const cometPosition = parseFloat(document.getElementById('slider-comet-position').value);
            const comaSize = parseFloat(document.getElementById('slider-coma').value) / 100;
            const tailLength = parseFloat(document.getElementById('slider-tail').value) / 100;
            const tailSpread = parseFloat(document.getElementById('slider-tail-spread').value) / 100;

            const earthAngle = earthPosition * (Math.PI / 180);
            const cometAngle = cometPosition * (Math.PI / 180);

            earth.position.x = 4 * Math.cos(earthAngle);
            earth.position.z = 4 * Math.sin(earthAngle);

            comet.position.x = 14.4 * Math.cos(cometAngle);
            comet.position.z = 14.4 * Math.sin(cometAngle);

            cometComa.scale.set(comaSize, comaSize, comaSize);

            cometTail.scale.y = tailLength;
            cometTail.scale.x = tailSpread;
            cometTail.scale.z = tailSpread;

            const tailDirection = new THREE.Vector3().subVectors(new THREE.Vector3(0, 0, 0), comet.position).normalize();
            cometTail.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), tailDirection);

            cameraTelescope.position.copy(earth.position);
            cameraTelescope.lookAt(comet.position);

            rendererOrbit.render(sceneOrbit, cameraOrbit);
            rendererTelescope.render(sceneTelescope, cameraTelescope);
        }

        // Event Listeners for Sliders
        document.getElementById('slider-earth-position').addEventListener('input', update);
        document.getElementById('slider-comet-position').addEventListener('input', update);
        document.getElementById('slider-coma').addEventListener('input', update);
        document.getElementById('slider-tail').addEventListener('input', update);
        document.getElementById('slider-tail-spread').addEventListener('input', update);

        // Initial Render
        update();

        // Animation Loop
        function animate() {
            requestAnimationFrame(animate);
            update();
        }

        animate();

        // Resize Handler
        window.addEventListener('resize', () => {
            const width = window.innerWidth / 2;
            const height = window.innerHeight;

            cameraOrbit.aspect = width / height;
