    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let m, x, y;
        const L = 10; // Distance between posts
        const g = 9.81; // Acceleration due to gravity
        const postWidth = 10;
        const postHeight = 300;
        const postX1 = 100;
        const postX2 = 700;
        const postY = 150;
        const scale = 50; // Pixels per unit

        function setup() {
            createCanvas(800, 500);
            m = parseFloat(document.getElementById('slider-m').value);
            x = parseFloat(document.getElementById('slider-x').value);
            y = parseFloat(document.getElementById('slider-y').value);
        }

        function draw() {
            background('#F0F0F0');
            updateValues();
            calculateAndDraw();
        }

        function updateValues() {
            m = parseFloat(document.getElementById('slider-m').value);
            x = parseFloat(document.getElementById('slider-x').value);
            y = parseFloat(document.getElementById('slider-y').value);
            document.getElementById('label-m').textContent = m.toFixed(3);
            document.getElementById('label-x').textContent = x.toFixed(1);
            document.getElementById('label-y').textContent = y.toFixed(3);
        }

        function calculateAndDraw() {
            const theta1 = atan(y / x);
            const theta2 = atan(y / (L - x));
            const Fg = m * g;
            const T1 = (Fg * cos(theta2)) / sin(theta1 + theta2);
            const T2 = (Fg * cos(theta1)) / sin(theta1 + theta2);

            fill(0);
            textSize(20);
            text(`T\u2081 = ${T1.toFixed(4)} N | T\u2082 = ${T2.toFixed(4)} N`, width / 2 - 150, 30);

            stroke(0);
            strokeWeight(postWidth);
            line(postX1, postY, postX1, postY + postHeight);
            line(postX2, postY, postX2, postY + postHeight);

            noFill();
            strokeWeight(2);
            line(postX1, postY, map(x, 0, L, postX1, postX2), postY + y * scale);
            line(postX2, postY, map(x, 0, L, postX1, postX2), postY + y * scale);

            fill(0);
            ellipse(map(x, 0, L, postX1, postX2), postY + y * scale, 40, 40);

            strokeWeight(1);
            dashedLine(postX1, postY, postX2, postY);

            noFill();
            strokeWeight(2);
            arc(postX1, postY, 50, 50, -PI / 2, -theta1);
            arc(postX2, postY, 50, 50, -PI / 2, theta2);

            fill(0);
            textSize(16);
            text(`θ\u2081`, postX1 + 30, postY - 10);
            text(`θ\u2082`, postX2 - 30, postY - 10);
            text(`T\u2081`, map(x, 0, L, postX1, postX2) - 20, postY + y * scale - 20);
            text(`T\u2082`, map(x, 0, L, postX1, postX2) + 20, postY + y * scale - 20);
        }

        function dashedLine(x1, y1, x2, y2) {
            const dashLength = 10;
            const dx = x2 - x1;
            const dy = y2 - y1;
            const length = sqrt(dx * dx + dy * dy);
            const step = dashLength / length;
            for (let t = 0; t < 1; t += step * 2) {
                const xA = x1 + dx * t;
                const yA = y1 + dy * t;
                const xB = x1 + dx * min(1, t + step);
                const yB = y1 + dy * min(1, t + step);
                line(xA, yA, xB, yB);
            }
        }

        document.getElementById('slider-m').addEventListener('input', draw);
        document.getElementById('slider-x').addEventListener('input', draw);
        document.getElementById('slider-y').addEventListener('input', draw);
    </script>
</head>
