                <span id="value-velocity">1</span>
            </div>
            <h2>system parameters</h2>
            <div class="control-row">
                <label for="slider-stiffness">spring stiffness</label>
                <input type="range" id="slider-stiffness" min="0.1" max="5" step="0.01" value="1">
                <span id="value-stiffness">1</span>
            </div>
            <div class="control-row">
                <label for="slider-mass">mass</label>
                <input type="range" id="slider-mass" min="0.1" max="5" step="0.01" value="1">
                <span id="value-mass">1</span>
            </div>
            <div class="control-row">
                <label for="slider-damping">dampening</label>
                <input type="range" id="slider-damping" min="0" max="5" step="0.1" value="0.1">
                <span id="value-damping">0.1</span>
            </div>
            <h2>controller gains</h2>
            <div class="control-row">
                <label for="slider-proportional-gain">proportional displacement gain</label>
                <input type="range" id="slider-proportional-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-proportional-gain">0.1</span>
            </div>
            <div class="control-row">
                <label for="slider-derivative-gain">derivative velocity gain</label>
                <input type="range" id="slider-derivative-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-derivative-gain">0.1</span>
            </div>
            <h2>release system</h2>
            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>
        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <!-- Load three.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <!-- Load plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // State variables
        let time = 0;
        let animationFrameId = null;
        let isRunning = false;

        // DOM elements
        const sliderDisplacement = document.getElementById('slider-displacement');
        const sliderVelocity = document.getElementById('slider-velocity');
        const sliderStiffness = document.getElementById('slider-stiffness');
        const sliderMass = document.getElementById('slider-mass');
        const sliderDamping = document.getElementById('slider-damping');
        const sliderProportionalGain = document.getElementById('slider-proportional-gain');
        const sliderDerivativeGain = document.getElementById('slider-derivative-gain');
        const btnPlay = document.getElementById('btn-play');
        const btnPause = document.getElementById('btn-pause');
        const btnReset = document.getElementById('btn-reset');

        const valueDisplacement = document.getElementById('value-displacement');
        const valueVelocity = document.getElementById('value-velocity');
        const valueStiffness = document.getElementById('value-stiffness');
        const valueMass = document.getElementById('value-mass');
        const valueDamping = document.getElementById('value-damping');
        const valueProportionalGain = document.getElementById('value-proportional-gain');
        const valueDerivativeGain = document.getElementById('value-derivative-gain');

        // Update slider values
        function updateSliderValues() {
            valueDisplacement.textContent = parseFloat(sliderDisplacement.value).toFixed(1);
            valueVelocity.textContent = parseFloat(sliderVelocity.value).toFixed(1);
            valueStiffness.textContent = parseFloat(sliderStiffness.value).toFixed(2);
            valueMass.textContent = parseFloat(sliderMass.value).toFixed(2);
            valueDamping.textContent = parseFloat(sliderDamping.value).toFixed(1);
            valueProportionalGain.textContent = parseFloat(sliderProportionalGain.value).toFixed(1);
            valueDerivativeGain.textContent = parseFloat(sliderDerivativeGain.value).toFixed(1);
        }

        // Solve the system differential equations
        function solveSystem(x0, v0, m, c, k, Kp, Kd) {
            const k_eff = k + Kp;
            const c_eff = c + Kd;
            const omega_n = Math.sqrt(k_eff / m);
            const zeta = c_eff / (2 * m * omega_n);

            const wn_damped = omega_n * Math.sqrt(1 - zeta ** 2);
            const phi = Math.atan((zeta * omega_n) / wn_damped);

            const x_t = (t) => x0 * Math.exp(-zeta * omega_n * t) * Math.cos(wn_damped * t - phi) + (v0 + zeta * omega_n * x0) / wn_damped * Math.exp(-zeta * omega_n * t) * Math.sin(wn_damped * t - phi);
            const v_t = (t) => -(x0 * zeta * omega_n * Math.exp(-zeta * omega_n * t) * Math.cos(wn_damped * t - phi)) - (x0 * wn_damped * Math.exp(-zeta * omega_n * t) * Math.sin(wn_damped * t - phi)) - ((v0 + zeta * omega_n * x0) * zeta * omega_n * Math.exp(-zeta * omega_n * t) * Math.sin(wn_damped * t - phi)) + ((v0 + zeta * omega_n * x0) * wn_damped * Math.exp(-zeta * omega_n * t) * Math.cos(wn_damped * t - phi));

            return { x_t, v_t };
        }

        // Update plots
        function updatePlots(x_t, v_t) {
            const t_values = Array.from({ length: 1001 }, (_, i) => i * 0.01);
            const x_values = t_values.map(x_t);
            const v_values = t_values.map(v_t);

            Plotly.newPlot('plot-displacement', [
                { x: t_values, y: x_values, mode: 'lines', name: 'Displacement' },
                { x: [time], y: [x_t(time)], mode: 'markers', marker: { color: 'blue' } }
            ], {
                title: 'Displacement vs Time',
                xaxis: { title: 'Time (s)', range: [0, 10] },
                yaxis: { title: 'Displacement (m)', range: [-3, 3] },
                margin: { l: 50, r: 50, b: 50, t: 50, pad: 4 },
                showlegend: false
            });

            Plotly.newPlot('plot-velocity', [
                { x: t_values, y: v_values, mode: 'lines', name: 'Velocity' },
                { x: [time], y: [v_t(time)], mode: 'markers', marker: { color: 'green' } }
            ], {
                title: 'Velocity vs Time',
                xaxis: { title: 'Time (s)', range: [0, 10] },
                yaxis: { title: 'Velocity (m/s)', range: [-3, 3] },
                margin: { l: 50, r: 50, b: 50, t: 50, pad: 4 },
                showlegend: false
            });
        }

        // Update 3D scene
        function updateScene(x) {
            mass.position.x = x;
            spring.scale.x = 1 + x / 3; // Scale spring based on displacement
            damperPiston.position.x = x;
        }

        // Animation loop
        function animate() {
            if (!isRunning) return;

            time += 0.01;
            if (time > 10) {
                btnPause.click();
                return;
            }

            const x = x_t(time);
            const v = v_t(time);

            updateScene(x);
            Plotly.restyle('plot-displacement', 'x[1]', [time]);
            Plotly.restyle('plot-displacement', 'y[1]', [x]);
            Plotly.restyle('plot-velocity', 'x[1]', [time]);
            Plotly.restyle('plot-velocity', 'y[1]', [v]);

            animationFrameId = requestAnimationFrame(animate);
        }

        // Event listeners for sliders
        const sliders = [sliderDisplacement, sliderVelocity, sliderStiffness, sliderMass, sliderDamping, sliderProportionalGain, sliderDerivativeGain];
        sliders.forEach(slider => slider.addEventListener('input', () => {
            updateSliderValues();
            btnPause.click();
            btnReset.click();

            const x0 = parseFloat(sliderDisplacement.value);
            const v0 = parseFloat(sliderVelocity.value);
            const m = parseFloat(sliderMass.value);
            const c = parseFloat(sliderDamping.value);
            const k = parseFloat(sliderStiffness.value);
            const Kp = parseFloat(sliderProportionalGain.value);
            const Kd = parseFloat(sliderDerivativeGain.value);

            const solution = solveSystem(x0, v0, m, c, k, Kp, Kd);
            x_t = solution.x_t;
            v_t = solution.v_t;

            updatePlots(x_t, v_t);
            updateScene(x0);
        }));

        // Event listeners for buttons
        btnPlay.addEventListener('click', () => {
            if (!isRunning) {
                isRunning = true;
                animate();
            }
        });

        btnPause.addEventListener('click', () => {
            if (isRunning) {
                cancelAnimationFrame(animationFrameId);
                isRunning = false;
            }
        });

        btnReset.addEventListener('click', () => {
            btnPause.click();
            time = 0;
            updateScene(x_t(0));
            Plotly.restyle('plot-displacement', 'x[1]', [0]);
            Plotly.restyle('plot-displacement', 'y[1]', [x_t(0)]);
            Plotly.restyle('plot-velocity', 'x[1]', [0]);
            Plotly.restyle('plot-velocity', 'y[1]', [v_t(0)]);
        });

        // Initialize 3D scene
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('scene-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040); // soft white light
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const baseGeometry = new THREE.BoxGeometry(10, 0.1, 10);
        const baseMaterial = new THREE.MeshStandardMaterial({ color: 0x00ff00 });
        const base = new THREE.Mesh(baseGeometry, baseMaterial);
        base.position.y = -0.05;
        scene.add(base);

        const massGeometry = new THREE.BoxGeometry(0.5, 0.5, 0.5);
        const massMaterial = new THREE.MeshStandardMaterial({ color: 0xffff00 });
        const mass = new THREE.Mesh(massGeometry, massMaterial);
        scene.add(mass);

        const springGeometry = new THREE.TubeGeometry(new THREE.CatmullRomCurve3([
            new THREE.Vector3(0, 0, 0),
            new THREE.Vector3(0, 0.5, 0),
            new THREE.Vector3(0, 1, 0),
            new THREE.Vector3(0, 1.5, 0),
            new THREE.Vector3(0, 2, 0)
        ]), 20, 0.1, 8, false);
        const springMaterial = new THREE.MeshStandardMaterial({ color: 0xff0000 });
        const spring = new THREE.Mesh(springGeometry, springMaterial);
        scene.add(spring);

        const damperHousingGeometry = new THREE.CylinderGeometry(0.1, 0.1, 1, 32);
        const damperHousingMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff });
        const damperHousing = new THREE.Mesh(damperHousingGeometry, damperHousingMaterial);
        damperHousing.position.y = 1.5;
        scene.add(damperHousing);

        const damperPistonGeometry = new THREE.CylinderGeometry(0.1, 0.1, 0.5, 32);
        const damperPistonMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff });
        const damperPiston = new THREE.Mesh(damperPistonGeometry, damperPistonMaterial);
        damperPiston.position.y = 1.75;
        scene.add(damperPiston);

        const enclosureGeometry = new THREE.BoxGeometry(2, 3, 2);
        const enclosureMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff, wireframe: true });
        const enclosure = new THREE.Mesh(enclosureGeometry, enclosureMaterial);
        enclosure.position.y = 1;
        scene.add(enclosure);

        camera.position.z = 5;

        // Initial setup
        updateSliderValues();
        const initialSolution = solveSystem(parseFloat(sliderDisplacement.value), parseFloat(sliderVelocity.value), parseFloat(sliderMass.value), parseFloat(sliderDamping.value), parseFloat(sliderStiffness.value), parseFloat(sliderProportionalGain.value), parseFloat(sliderDerivativeGain.value));
        x_t = initialSolution.x_t;
        v_t = initialSolution.v_t;
        updatePlots(x_t, v_t);
        updateScene(x_t(0));

        function render() {
            requestAnimationFrame(render);
            renderer.render(scene, camera);
        }

        render();
    </script>
</body>
</html>
